contract XYZZ is ERC20Interface, Owned, SafeMath {
    string public symbol;
    string public name;
    string public description;
    uint8 public decimals;    
    uint private _startDate;
    uint private _bonusEnds;
    uint private _endDate;

    uint256 private _internalCap;
    uint256 private _softCap;
    uint256 private _totalSupply;

    mapping(address => uint256) _balances;
    mapping(address => mapping(address => uint256)) _allowed;
    mapping(address => bool) _freezeState;


    // Constructor
    constructor(
        address payable minter) public {

        name   = "VGNA Token";
        description = "Virtual Geospatial Networking Asset";
        symbol = "VGNA";
        decimals = 18;
        _internalCap = 25000000;
        _softCap     = 50000000;

        _bonusEnds = now + 3 days;
        _endDate = now + 1 weeks;

        _owner = minter;
        _balances[_owner] = _internalCap;  
        _totalSupply = _internalCap;
        emit Transfer(address(0), _owner, _internalCap);
    }

    modifier IcoSuccessful {
        require(now >= _endDate);
        require(_totalSupply >= _softCap);
        _;
    }


    // Total supply
    function totalSupply() public view returns (uint) {
        return _totalSupply - _balances[address(0)];
    }


    // Get the token balance for account `tokenOwner`
    function balanceOf(address tokenOwner) public view returns (uint balance) {
        return _balances[tokenOwner];
    }

    function isFreezed(address tokenOwner) public view returns (bool freezed) {
        return _freezeState[tokenOwner];
    }


    // Transfer the balance from token owner's account to `to` account
    // - Owner's account must have sufficient balance to transfer
    // - 0 value transfers are allowed
    function transfer(address to, uint256 tokens) public IcoSuccessful returns (bool success) {
        require(_freezeState[msg.sender] == false);

        _balances[msg.sender] = safeSub(_balances[msg.sender], tokens);
        _balances[to] = safeAdd(_balances[to], tokens);
        emit Transfer(msg.sender, to, tokens);
        return true;
    }


    // Token owner can approve for `spender` to transferFrom(...) `tokens`
    // from the token owner's account
    function approve(address spender, uint tokens) public IcoSuccessful returns (bool success) {
        require( _freezeState[spender] == false);
        _allowed[msg.sender][spender] = tokens;
        emit Approval(msg.sender, spender, tokens);
        return true;
    }


    // Transfer `tokens` from the `from` account to the `to` account
    //
    // The calling account must already have sufficient tokens approve(...)-d
    // for spending from the `from` account and
    // - From account must have sufficient balance to transfer
    // - Spender must have sufficient allowance to transfer
    // - 0 value transfers are allowed
    function transferFrom(address from, address to, uint tokens) public IcoSuccessful returns (bool success) {
        require( _freezeState[from] == false && _freezeState[to] == false);

        _balances[from] = safeSub(_balances[from], tokens);
        _allowed[from][msg.sender] = safeSub(_allowed[from][msg.sender], tokens);
        _balances[to] = safeAdd(_balances[to], tokens);
        emit Transfer(from, to, tokens);
        return true;
    }


    // Returns the amount of tokens approved by the owner that can be
    // transferred to the spender's account
    function allowance(address tokenOwner, address spender) public view returns (uint remaining) {
        require(_freezeState[spender] == false);
        return _allowed[tokenOwner][spender];
    }

    // Token owner can approve for `spender` to transferFrom(...) `tokens`
    // from the token owner's account. The `spender` contract function
    // `receiveApproval(...)` is then executed
    function approveAndCall(address spender, uint tokens, bytes memory data) public IcoSuccessful returns (bool success) {
        require(_freezeState[spender] == false);
        _allowed[msg.sender][spender] = tokens;
        ApproveAndCallFallBack(spender).receiveApproval(msg.sender, tokens, _owner, data);
        emit Approval(msg.sender, spender, tokens);
        return true;
    }

    // 1 VGNA Tokens per 1 Wei
    function buy() public payable {

        require(msg.value >= 1 finney);
        require(now >= _startDate && now <= _endDate);

        uint256 weiValue = msg.value;
        uint256 tokens = 0;

        if (now <= _bonusEnds) {
            tokens = safeMul(weiValue, 2);
        } else {
            tokens = safeMul(weiValue, 1);
        }

        _freezeState[msg.sender] = true;
        _balances[msg.sender] = safeAdd(_balances[msg.sender], tokens);
        _totalSupply = safeAdd(_totalSupply, tokens);
        emit Transfer(address(0), msg.sender, tokens);
        _owner.transfer(msg.value);
    }

    function () payable external {
        buy();
    }

    function burn(uint256 tokens) public onlyOwner returns (bool success) {
        require(_balances[msg.sender] >= tokens);   // Check if the sender has enough
        address burner = msg.sender;
        _balances[burner] = safeSub(_balances[burner], tokens);
        _totalSupply = safeSub(_totalSupply, tokens);
        emit Transfer(burner, address(0), tokens);
        return true;
    }

    function burnFrom(address account, uint256 tokens) public onlyOwner returns (bool success) {
        require(_balances[account] >= tokens);   // Check if the sender has enough
        address burner = account;
        _balances[burner] = safeSub(_balances[burner], tokens);
        _totalSupply = safeSub(_totalSupply, tokens);
        emit Transfer(burner, address(0), tokens);
        return true;
    }

    function freeze(address account) public onlyOwner returns (bool success) {
        require(account != _owner && account != address(0));
        _freezeState[account] = true;
        return true;
    }

    function unfreeze(address account) public onlyOwner returns (bool success) {
        require(account != _owner && account != address(0));
        _freezeState[account] = false;
        return true;
    }

    function mint(uint256 tokens) public onlyOwner returns (bool success)
    {
        require(now >= _startDate && now <= _endDate);
        _balances[msg.sender] = safeAdd(_balances[msg.sender], tokens);
        _totalSupply = safeAdd(_totalSupply, tokens);
        emit Transfer(address(0), msg.sender, tokens);
        return true;
    }

    // Owner can transfer out any accidentally sent ERC20 tokens
    function transferAnyERC20Token(address tokenAddress, uint tokens) public onlyOwner returns (bool success) {
        return ERC20Interface(tokenAddress).transfer(_owner, tokens);
    }
}

contract AquaTools is ERC1155, Ownable, ERC1155Supply {
    uint256 public price = 0.1 ether;
    uint16 public maxSupply = 3000;
    uint16 public supplyLeft = maxSupply;
    uint8 public reservedSupply = 200;
    bool public presaleActive;
    bool public saleActive;
    uint8 public presaleMaxMintsPerTrans = 1;
    uint8 public publicMaxMintsPerTrans = 3;
    uint8 public passIndex = 0;
    bytes32 public merkleRoot =
        0x9492a15aa69d55a18e1181ac3b84b971ef9799e23b284f144961474af08621d9;

    mapping(address => bool) public whitelistClaimed;

    constructor()
        ERC1155("ipfs://QmTF8cSSv5Ewexo4TMU8Wg1MqEwXHfLVxYkuhy7GzwSU6U")
    {
        _mint(msg.sender, passIndex, reservedSupply, "");

        supplyLeft -= reservedSupply;
    }

    // Write functions

    function setPresaleStatus(bool _presaleActive) public onlyOwner {
        presaleActive = _presaleActive;
    }

    function setSaleStatus(bool _saleActive) public onlyOwner {
        saleActive = _saleActive;
    }

    function setPrice(uint256 _price) public onlyOwner {
        price = _price;
    }

    function setMerkleRoot(bytes32 _merkleRoot) public onlyOwner {
        merkleRoot = _merkleRoot;
    }

    function setURI(string memory newuri) public onlyOwner {
        _setURI(newuri);
    }

    function withdraw() public onlyOwner {
        uint256 balance = address(this).balance;
        payable(msg.sender).transfer(balance);
    }

    // Read functions

    function hasToken(address account) public view returns (bool) {
        return balanceOf(account, passIndex) > 0;
    }

    function getPrice() public view returns (uint256) {
        return price;
    }

    function getSupplyLeft() public view returns (uint16) {
        return supplyLeft;
    }

    function getMaxSupply() public view returns (uint16) {
        return maxSupply;
    }

    function getMerkleRoot() public view returns (bytes32) {
        return merkleRoot;
    }

    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }

    // Mint

    function mintPresale(
        address account,
        uint8 amount,
        bytes32[] calldata _merkleProof
    ) public payable {
        require(msg.value >= amount * price, "Wrong amount sent");
        require(presaleActive, "Sale is not active");
        require(amount > 0, "Amount must be positive integer");
        require(
            totalSupply(passIndex) + amount <= maxSupply,
            "Purchase would exceed max supply"
        );
        require(!whitelistClaimed[msg.sender], "Address has already claimed");
        require(amount <= presaleMaxMintsPerTrans, "Only 1 mint allowed");

        bytes32 leaf = keccak256(abi.encodePacked(account));
        require(
            MerkleProof.verify(_merkleProof, merkleRoot, leaf),
            "Invalid proof"
        );

        _mint(account, passIndex, amount, "");
        whitelistClaimed[msg.sender] = true;

        supplyLeft -= amount;
    }

    function mint(address account, uint8 amount) public payable {
        require(msg.value >= amount * price, "Wrong amount sent");
        require(saleActive, "Sale is not active");
        require(amount > 0, "Amount must be positive integer");
        require(
            totalSupply(passIndex) + amount <= maxSupply,
            "Purchase would exceed max supply"
        );
        require(amount <= publicMaxMintsPerTrans, "Only 3 mints allowed");

        _mint(account, passIndex, amount, "");

        supplyLeft -= amount;
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override(ERC1155, ERC1155Supply) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes memory
    ) public virtual returns (bytes4) {
        return this.onERC1155Received.selector;
    }
}

contract KOK_Contract is ERC20Interface, Owned {
	string public constant symbol = "KOK";
	string public constant name = "KOK Coin";
	uint8 public constant decimals = 18;
	uint256 public constant totalSupply = 5000000000000000000000000000;

	bool public stopped;

	mapping (address => int8) public blackList;

	mapping (address => uint256) public balances;
	mapping (address => mapping (address => uint256)) public allowed;


    event Blacklisted(address indexed target);
    event DeleteFromBlacklist(address indexed target);
    event RejectedPaymentToBlacklistedAddr(address indexed from, address indexed to, uint256 value);
    event RejectedPaymentFromBlacklistedAddr(address indexed from, address indexed to, uint256 value);


	modifier notStopped {
        require(!stopped);
        _;
    }

// constructor
	function KOKContract() public {
		balances[msg.sender] = totalSupply;
	}
	
// function made for airdrop
	function airdrop(address[] _to, uint256[] _value) onlyOwner notStopped public {
	    for(uint256 i = 0; i < _to.length; i++){
	        if(balances[_to[i]] > 0){
	            continue;
	        }
	        transfer(_to[i], _value[i]);
	    }
	}

// blacklist management
    function blacklisting(address _addr) onlyOwner public {
        blackList[_addr] = 1;
        emit Blacklisted(_addr);
    }
    function deleteFromBlacklist(address _addr) onlyOwner public {
        blackList[_addr] = -1;
        emit DeleteFromBlacklist(_addr);
    }

// stop the contract
	function stop() onlyOwner {
        stopped = true;
    }
    function start() onlyOwner {
        stopped = false;
    }
	
// ERC20 functions
	function balanceOf(address _owner) public constant returns (uint256 balance){
		return balances[_owner];
	}
	function transfer(address _to, uint256 _value) notStopped public returns (bool success){
		require(balances[msg.sender] >= _value);

		if(blackList[msg.sender] > 0){
			emit RejectedPaymentFromBlacklistedAddr(msg.sender, _to, _value);
			return false;
		}
		if(blackList[_to] > 0){
			emit RejectedPaymentToBlacklistedAddr(msg.sender, _to, _value);
			return false;
		}

		balances[msg.sender] -= _value;
		balances[_to] += _value;
		emit Transfer(msg.sender, _to, _value);
		return true;
	}
	function transferFrom(address _from, address _to, uint256 _value) notStopped public returns (bool success){
		require(balances[_from] >= _value
			&& allowed[_from][msg.sender] >= _value);

		if(blackList[_from] > 0){
			emit RejectedPaymentFromBlacklistedAddr(_from, _to, _value);
			return false;
		}
		if(blackList[_to] > 0){
			emit RejectedPaymentToBlacklistedAddr(_from, _to, _value);
			return false;
		}

		balances[_from] -= _value;
		allowed[_from][msg.sender] -= _value;
		balances[_to] += _value;
		emit Transfer(_from, _to, _value);
		return true;
	}
	function approve(address _spender, uint256 _value) notStopped public returns (bool success){
		allowed[msg.sender][_spender] = _value;
		emit Approval(msg.sender, _spender, _value);
		return true;
	}
	function allowance(address _owner, address _spender) public constant returns (uint256 remaining){
		return allowed[_owner][_spender];
	}
}

contract Minttk is ERC20 { 
    using SafeMath for uint256;
    string public constant name     = "Minttk";                    // Name of the token
    string public constant symbol   = "MNT";                       // Symbol of token
    uint8 public constant decimals  = 18;                           // Decimal of token
    uint public _totalsupply        = 500000000 * 10 ** 18;         // 500 million total supply // muliplies dues to decimal precision
    uint public crowdSale           = 60000000 * 10 ** 18;          // 60 million in Crowd Sale
    uint public posMining           = 350000000 * 10 ** 18;         // 350 million in POS Mining
    uint public corporateReserve    = 80000000 * 10 ** 18;          // 80 million in Corporate Reserve
    uint public promotionBounty     = 10000000 * 10 ** 18;          // 10 million in Promotion & Bounty
    uint public remainingToken      = 150000000 * 10 ** 18;         // 150 million in Remaining Token | Total Supply - POS
    address public owner;                                           // Owner of this contract
    mapping(address => uint256) internal mintingDate;
    mapping(address => uint) balances;
    mapping(address => mapping(address => uint)) allowed;

    modifier onlyOwner() {
        if (msg.sender != owner) {
            revert();
        }
        _;
    }

    function Minttk() public {
        balances[msg.sender] = remainingToken;
        Transfer(0, msg.sender, remainingToken);
    }

    function totalSupply() public view returns (uint256 total_Supply) {
        total_Supply = _totalsupply;
    }

    function balanceOf(address _owner) public view returns (uint256 balance) {
        return balances[_owner];
    }

    // Token minting function
    function mint() public {
        address _customerAddress = msg.sender;
        uint256 userBalance = balances[_customerAddress];
        uint256 currentTs = now;
        uint256 userMintingDate = mintingDate[_customerAddress] + 7 days;
        require (userBalance > 0);
        require (currentTs > userMintingDate);
        mintingDate[_customerAddress] = currentTs;
        uint256 _bonusAmount = SafeMath.percent(userBalance,2,100,18);
        balances[_customerAddress] = (uint256)(balances[_customerAddress]).add(_bonusAmount);
    }

    // Send _value amount of tokens from address _from to address _to
    function transferFrom( address _from, address _to, uint256 _amount ) public returns (bool success) {
        require( _to != 0x0);
        require(balances[_from] >= _amount && allowed[_from][msg.sender] >= _amount && _amount >= 0);
        balances[_from] = (balances[_from]).sub(_amount);
        allowed[_from][msg.sender] = (allowed[_from][msg.sender]).sub(_amount);
        balances[_to] = (balances[_to]).add(_amount);
        Transfer(_from, _to, _amount);
        return true;
    }

    // Allow _spender to withdraw from your account, multiple times, up to the _value amount.
    function approve(address _spender, uint256 _amount) public returns (bool success) {
        require( _spender != 0x0);
        allowed[msg.sender][_spender] = _amount;
        Approval(msg.sender, _spender, _amount);
        return true;
    }

    function allowance(address _owner, address _spender) public view returns (uint256 remaining) {
        require( _owner != 0x0 && _spender !=0x0);
        return allowed[_owner][_spender];
    }

    // Transfer the balance from owner's account to another account
    function transfer(address _to, uint256 _amount) public returns (bool success) {
        require( _to != 0x0);
        require(balances[msg.sender] >= _amount && _amount >= 0);

        address _customerAddress = msg.sender;
        uint256 currentTs = now;
        uint256 userMintingDate = mintingDate[_customerAddress] + 7 days;
        require (currentTs > userMintingDate);

        balances[msg.sender] = (balances[msg.sender]).sub(_amount);
        balances[_to] = (balances[_to]).add(_amount);
        Transfer(msg.sender, _to, _amount);
        return true;
    }

    // Transfer the balance from owner's account to another account
    function transferTokens(address _to, uint256 _amount) private returns (bool success) {
        require( _to != 0x0);       
        require(balances[address(this)] >= _amount && _amount > 0);
        balances[address(this)] = (balances[address(this)]).sub(_amount);
        balances[_to] = (balances[_to]).add(_amount);
        Transfer(address(this), _to, _amount);
        return true;
    }

    function drain() external onlyOwner {
        owner.transfer(this.balance);
    }
}

contract TattooSkullzSociety is ERC721, ERC721Enumerable, Ownable {
    string public PROVENANCE;
    string private _baseURIextended;

    bool public isAllowListActive = false;
    bool public saleIsActive = false;

    uint256 public constant MAX_SUPPLY = 5000;
    uint256 public constant MAX_PUBLIC_MINT = 22;
    uint256 public constant RESERVE_MINT = 222;
    uint256 public constant PRICE_PER_TOKEN = 0.08 ether;

    mapping(address => uint8) private _allowList;

    constructor() ERC721("TattooSkullzSociety", "SKULLZ") {
    }

    // Whitelist
    function setIsAllowListActive(bool _isAllowListActive) external onlyOwner {
        isAllowListActive = _isAllowListActive;
    }

    function setAllowList(address[] calldata addresses, uint8 numAllowedToMint) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            _allowList[addresses[i]] = numAllowedToMint;
        }
    }

    function numAvailableToMint(address addr) external view returns (uint8) {
        return _allowList[addr];
    }

    function mintAllowList(uint8 numberOfTokens) external payable {
        uint256 ts = totalSupply();
        require(isAllowListActive, "Allow list is not active");
        require(numberOfTokens <= _allowList[msg.sender], "Exceeded max available to purchase");
        require(numberOfTokens <= MAX_PUBLIC_MINT, "Exceeded max token purchase");
        require(ts + numberOfTokens <= MAX_SUPPLY, "Purchase would exceed max tokens");
        require(PRICE_PER_TOKEN * numberOfTokens <= msg.value, "Ether value sent is not correct");

        _allowList[msg.sender] -= numberOfTokens;
        for (uint256 i = 0; i < numberOfTokens; i++) {
            _safeMint(msg.sender, ts + i);
        }
    }

    // Mint
    function setSaleState(bool newState) public onlyOwner {
        saleIsActive = newState;
    }

    function mint(uint numberOfTokens) public payable {
        uint256 ts = totalSupply();
        require(saleIsActive, "Sale must be active to mint tokens");
        require(numberOfTokens <= MAX_PUBLIC_MINT, "Exceeded max token purchase");
        require(ts + numberOfTokens <= MAX_SUPPLY, "Purchase would exceed max tokens");
        require(PRICE_PER_TOKEN * numberOfTokens <= msg.value, "Ether value sent is not correct");

        for (uint256 i = 0; i < numberOfTokens; i++) {
            _safeMint(msg.sender, ts + i);
        }
    }

    // Utils
    function reserve(uint numberOfTokens) public onlyOwner {
      uint supply = totalSupply();
      uint i;
      for (i = 0; i < numberOfTokens; i++) {
          _safeMint(msg.sender, supply + i);
      }
    }

    function withdraw() public onlyOwner {
        uint balance = address(this).balance;
        payable(msg.sender).transfer(balance);
    }

    function setProvenance(string memory provenance) public onlyOwner {
        PROVENANCE = provenance;
    }

    function setBaseURI(string memory baseURI_) external onlyOwner() {
        _baseURIextended = baseURI_;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _baseURIextended;
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, ERC721Enumerable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contract TomoeGozen is Ownable, ERC721A, ReentrancyGuard {
    using MerkleProof for bytes32[];

    uint256 public constant MAX_SUPPLY = 8000;
    uint256 public constant PUBLIC_MINT_LIMIT = 3;
    uint256 public constant PRESALE_MINT_LIMIT = 2;

    uint256 public constant MINT_PRICE = 0.08 ether;

    /// Inactive = 0; Presale = 1; Public = 2
    uint256 public saleFlag;

    bytes32 public merkleRoot;
    string public baseURI;

    mapping(address => uint256) private whitelistMints;

    constructor() ERC721A("Tomoe Gozen", "TOMOE_GOZEN", 50, MAX_SUPPLY) {}

    function verifyProof(bytes32[] calldata _proof) internal view returns (bool) {
        bytes32 leaf = keccak256(abi.encodePacked(msg.sender));
        return _proof.verify(merkleRoot, leaf);
    }

    function setBaseURI(string calldata _base) external onlyOwner {
        baseURI = _base;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    function setSaleFlag(uint256 _flag) external onlyOwner {
        saleFlag = _flag;
    }

    function setMerkleRoot(bytes32 _new) external onlyOwner {
        merkleRoot = _new;
    }

    function mint(uint256 _quantity) external payable {
        require(totalSupply() + _quantity <= MAX_SUPPLY, "MINT_EXCEEDS_TOTAL_SUPPLY");

        require(saleFlag == 2, "MINTING_PAUSED");
        require(_quantity > 0 && _quantity <= PUBLIC_MINT_LIMIT, "MINT_EXCEEDS_LIMIT_ALLOWED_PER_TRANSACTION");
        require(MINT_PRICE * _quantity <= msg.value, "AMOUNT_TOO_LOW");
        require(tx.origin == msg.sender, "SENDER_IS_NOT_AN_EOA");

        _safeMint(msg.sender, _quantity);
    }

    function mintWhitelist(uint256 _quantity, bytes32[] calldata _proof) external payable {
        require(totalSupply() + _quantity <= MAX_SUPPLY, "MINT_EXCEEDS_TOTAL_SUPPLY");

        require(saleFlag == 1, "MINTING_PAUSED");
        require(verifyProof(_proof), "INVALID_MERKLE_PROOF");
        require(whitelistMints[msg.sender] + _quantity <= PRESALE_MINT_LIMIT, "MINT_EXCEEDS_LIMIT_ALLOWED_FOR_WHITELIST");
        require(_quantity > 0 && _quantity <= PRESALE_MINT_LIMIT, "MINT_EXCEEDS_LIMIT_ALLOWED_PER_TRANSACTION");
        require(MINT_PRICE * _quantity <= msg.value, "AMOUNT_TOO_LOW");

        whitelistMints[msg.sender] += _quantity;

        _safeMint(msg.sender, _quantity);
    }

    function mintOwner(uint256 _quantity, address _to) external onlyOwner {
        require(totalSupply() + _quantity <= MAX_SUPPLY, "MINT_EXCEEDS_TOTAL_SUPPLY");

        _safeMint(_to, _quantity);
    }

    /** ALLOCATIONS:
     *  - Community Multisig: 30% (Signers: Bitquence, sOwner1, sOwner2 / 2 of 2 signatures)
     *  - Owners: 33.75%
     *  - Marketing: 11.25%
     *  - Developpers: 22.50%
     *  - Artist: 2.5%
     */
    function withdraw() external onlyOwner nonReentrant {
        uint256 balanceDividend = address(this).balance / 100000;

        (bool sComm,) = address(0x18B94d7dac879511B0f2Ee2947073b57fa11Fb08).call{value: balanceDividend * 30000}("");
        (bool sOwner1,) = address(0xEC4f373A70d5Bb5371df8A33951B898B7219f82C).call{value: balanceDividend * 16875}("");
        (bool sOwner2,) = address(0x4494949245C3C432177C66adf32e272477D6aaFc).call{value: balanceDividend * 16875}("");
        (bool sMark,) = address(0x39680E7090002731b306A8EE8Fae28072b84E586).call{value: balanceDividend * 11250}("");
        (bool sDev1,) = address(0x6e1af0E043C18eAd53A5B9886D4901Dd6B67a22f).call{value: balanceDividend * 11250}("");
        (bool sDev2,) = address(0xe0C5123B0FD1A7D94bB8D84bBAF1026B699C6dC6).call{value: balanceDividend * 11250}("");
        (bool sArt,) = address(0x62A6958E4FBff46C0ECc64E2eFDE83F75D609DD2).call{value: balanceDividend * 2500}("");
    }
}

contract EDE is ERC20Interface, Owned {
    using SafeMath for uint;

    string public symbol;
    string public  name;
    uint8 public decimals;
    uint public _totalSupply;
    uint private _teamsTokens;
    uint private _reserveTokens;
    uint256 public fundsRaised;
    uint private maximumCap;
    address payable wallet;
    address [] holders;

    uint256 private presaleopeningtime;
    uint256 private firstsaleopeningtime;
    uint256 private secondsaleopeningtime;
    uint256 private secondsaleclosingtime;

	string public HardCap;
	string public SoftCap;


    mapping(address => uint) balances;
    mapping(address => bool) whitelist;
    mapping(address => mapping(address => uint)) allowed;

    event TokenPurchase(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);

    modifier onlyWhileOpen {
        require((now >= presaleopeningtime && now <= secondsaleclosingtime) && fundsRaised != maximumCap); // should be open
        _;
    }

    // Constructor
    constructor() public {
        symbol = "EDE";
        name = "eden.best";
        decimals = 0;
        _totalSupply = 45e7;
        balances[address(this)] = 3375e5 * 10**uint(decimals); // 75% to ICO
        emit Transfer(address(0),address(this), 3375e5 * 10**uint(decimals));
        balances[address(0x687abe81c44c982394EED1b0Fc6911e5338A6421)] = 66150000 * 10**uint(decimals); // 14,7% to reserve
        emit Transfer(address(0),address(0x687abe81c44c982394EED1b0Fc6911e5338A6421), 66150000 * 10**uint(decimals));
        balances[address(0xd903846cF43aC9046CAE50C36ac1Aa18e630A1bB)] = 45000000 * 10**uint(decimals); // 10% to Team
        emit Transfer(address(0),address(0xd903846cF43aC9046CAE50C36ac1Aa18e630A1bB), 45000000 * 10**uint(decimals));
        balances[address(0x7341459eCdABC42C7493D923F5bb0992616d30A7)] = 1350000 * 10**uint(decimals); // 0,3% to airdrop
        emit Transfer(address(0),address(0x7341459eCdABC42C7493D923F5bb0992616d30A7), 1350000 * 10**uint(decimals));
        owner = address(0xEfA2CcE041aEB143678F8f310F3977F3EB61251E);
        wallet = address(0xEfA2CcE041aEB143678F8f310F3977F3EB61251E);
		    HardCap = "16875 ETH";
        SoftCap = "300 ETH";
        maximumCap = 16875000000000000000000; // 16875 eth, written in wei here
        presaleopeningtime = 1554120000; // 1st april 2019, 12pm
        firstsaleopeningtime = 1555329601; // 15 april 2019, 12:00:01pm
        secondsaleopeningtime = 1559304001; // 31 may 2019, 12:00:01pm
        secondsaleclosingtime = 1561896001; // 30 june 2019, 12:00:01pm
    }

    // Accepts ETH
    function () external payable {
        buyTokens(msg.sender);
    }

    function buyTokens(address _beneficiary) public payable onlyWhileOpen {
        _preValidatePurchase(_beneficiary, msg.value);
        _continueTokenPurchase(_beneficiary, msg.value);
    }

    function _preValidatePurchase(address _beneficiary, uint256 _weiAmount) internal view{
        require(_beneficiary != address(0));
        require(_weiAmount != 0);
        require(_weiAmount >= 50000000000000000 && _weiAmount <= 1000000000000000000000); // min 0.05 ETH, max 1000 ETH
        require(whitelist[_beneficiary]);
    }

    function _insertWhitelist(address[] memory _beneficiary) public onlyOwner{
        for(uint256 i = 0; i< _beneficiary.length; i++)
        {
            if(whitelist[_beneficiary[i]] == false)
                whitelist[_beneficiary[i]] = true;
        }
    }

    function _continueTokenPurchase(address _beneficiary, uint256 _weiAmount) internal{
        uint256 _tokens = _getTokenAmount(_weiAmount).div(1e18);
        uint256 bonus = _calculateBonus();
        _tokens = _tokens.add((_tokens.mul(bonus.mul(100))).div(10000));

        fundsRaised = fundsRaised.add(_weiAmount);

        _processPurchase(_beneficiary, _tokens);

        wallet.transfer(_weiAmount);
        emit TokenPurchase(address(this), _beneficiary, _weiAmount, _tokens);
    }

    function _getTokenAmount(uint256 _weiAmount) internal pure returns (uint256) {
        uint256 rate = 2e4; // 1 eth = 20,000T
        return _weiAmount.mul(rate);
    }

    function _calculateBonus() internal view returns (uint256){
        if(now >= presaleopeningtime && now < firstsaleopeningtime){
            return 30;
        } else if(now >=firstsaleopeningtime && now <secondsaleopeningtime){
            return 20;
        } else if(now >= secondsaleopeningtime && now <secondsaleclosingtime){
            return 0;
        }
    }

    function _processPurchase(address _beneficiary, uint256 _tokenAmount) internal {
        _deliverTokens(_beneficiary, _tokenAmount);
    }

    function _deliverTokens(address _beneficiary, uint256 _tokenAmount) internal {
        _transfer(_beneficiary, _tokenAmount);
    }

    function totalSupply() public view returns (uint){
       return _totalSupply* 10**uint(decimals);
    }

    // Get the token balance for account `tokenOwner`
    function balanceOf(address tokenOwner) public view returns (uint balance) {
        return balances[tokenOwner];
    }

    // Transfer the balance from token owner's account to `to` account
    // - Owner's account must have sufficient balance to transfer
    // - 0 value transfers are allowed
    function transfer(address to, uint tokens) public returns (bool success) {
        // prevent transfer to 0x0, use burn instead
        require(to != address(0));
        require(whitelist[to]);
        require(balances[msg.sender] >= tokens );
        require(balances[to] + tokens >= balances[to]);
        balances[msg.sender] = balances[msg.sender].sub(tokens);
        if(balances[to] == 0)
            holders.push(to);
        balances[to] = balances[to].add(tokens);
        emit Transfer(msg.sender,to,tokens);
        return true;
    }

    // Token owner can approve for `spender` to transferFrom(...) `tokens` from the token owner's account
    function approve(address spender, uint tokens) public returns (bool success){
        allowed[msg.sender][spender] = tokens;
        emit Approval(msg.sender,spender,tokens);
        return true;
    }

    // Transfer `tokens` from the `from` account to the `to` account
    function transferFrom(address from, address to, uint tokens) public returns (bool success){
        require(tokens <= allowed[from][msg.sender]); //check allowance
        require(balances[from] >= tokens);
        require(whitelist[to]);
        balances[from] = balances[from].sub(tokens);
        if(balances[to] == 0)
            holders.push(to);
        balances[to] = balances[to].add(tokens);
        allowed[from][msg.sender] = allowed[from][msg.sender].sub(tokens);
        emit Transfer(from,to,tokens);
        return true;
    }
    // Returns the amount of tokens approved by the owner that can be transferred to the spender's account
    function allowance(address tokenOwner, address spender) public view returns (uint remaining) {
        return allowed[tokenOwner][spender];
    }

    function _transfer(address to, uint tokens) internal returns (bool success) {
        // prevent transfer to 0x0, use burn instead
        require(to != address(0));
        require(balances[address(this)] >= tokens );
        require(balances[to] + tokens >= balances[to]);
        balances[address(this)] = balances[address(this)].sub(tokens);
        if(balances[to] == 0)
            holders.push(to);
        balances[to] = balances[to].add(tokens);
        emit Transfer(address(this),to,tokens);
        return true;
    }

    function _hardCapNotReached() external onlyOwner {
        uint eightyPercent = (balances[address(this)].mul(80)).div(100); // 80% of remaining tokens
        uint twentyPercent = balances[address(this)].sub(eightyPercent); // 20% of remaining tokens
        uint share = eightyPercent.div(holders.length);

        for(uint i = 0; i<holders.length; i++ ){
            address holder = holders[i];
            balances[holder] = balances[holder].add(share);
            emit Transfer(address(this),holder,share);
        }

        balances[address(0x687abe81c44c982394EED1b0Fc6911e5338A6421)] = twentyPercent;
        emit Transfer(address(this),address(0x687abe81c44c982394EED1b0Fc6911e5338A6421),twentyPercent);

        balances[address(this)] = 0;
    }
}

contract ERC721PresetMinterPauserAutoId is
    Context,
    AccessControlEnumerable,
    ERC721Enumerable,
    ERC721Burnable,
    ERC721Pausable
{
    using Counters for Counters.Counter;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    Counters.Counter private _tokenIdTracker;

    string private _baseTokenURI;

    uint8 private constant MAX_URI_RESETS_ALLOWED = 1;
    uint8 public uriResetCount = 0;

    // Grants `DEFAULT_ADMIN_ROLE`, `MINTER_ROLE` and `PAUSER_ROLE` to the account that deploys the contract.
    constructor(
        string memory name,
        string memory symbol
    ) ERC721(name, symbol) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());

        _setupRole(MINTER_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
    }


    // Sets `_baseTokenURI`. Resets allowed only `MAX_URI_RESETS_ALLOWED` no of times
    function setBaseURI(string memory baseTokenURI) public {
        require(hasRole(MINTER_ROLE, _msgSender()), "You don't have permission to do this.");
        require(uriResetCount <= MAX_URI_RESETS_ALLOWED, "Max resets allowed is breached now");
        _baseTokenURI = baseTokenURI;
        uriResetCount++;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _baseTokenURI;
    }

    // Creates a new token for `to`. Its token ID will be automatically assigned (and available on the emitted {IERC721-Transfer} event), and the token URI autogenerated based on the base URI passed at construction.
    function mint(address to) public virtual {
        require(hasRole(MINTER_ROLE, _msgSender()), "ERC721PresetMinterPauserAutoId: must have minter role to mint");

        // We cannot just use balanceOf to create the new tokenId because tokens
        // can be burned (destroyed), so we need a separate counter.
        _mint(to, _tokenIdTracker.current());
        _tokenIdTracker.increment();
    }

    // Pauses all token transfers.
    function pause() public virtual {
        require(hasRole(PAUSER_ROLE, _msgSender()), "ERC721PresetMinterPauserAutoId: must have pauser role to pause");
        _pause();
    }

    //  Unpauses all token transfers.
    function unpause() public virtual {
        require(hasRole(PAUSER_ROLE, _msgSender()), "ERC721PresetMinterPauserAutoId: must have pauser role to unpause");
        _unpause();
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override(ERC721, ERC721Enumerable, ERC721Pausable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(AccessControlEnumerable, ERC721, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contract Wallstreetbets is ERC20 {
    using SafeMath for uint256;
    string public constant symbol = "Wallstreetbets";
    string public constant name = "Wallstreetbets stocks and Options trading";
    uint public constant decimals = 18;
    uint256 _totalSupply = 65999999 * 10 ** 18; // 65 million Total Supply including 18 decimal

    // Owner of this contract
    address public owner;

    // Balances for each account
    mapping(address => uint256) balances;

    // Owner of account approves the transfer of an amount to another account
    mapping(address => mapping (address => uint256)) allowed;

    // Functions with this modifier can only be executed by the owner
    modifier onlyOwner() {
        if (msg.sender != owner) {
            revert();
        }
        _;
    }

    // Constructor
    constructor () public {
        owner = msg.sender;
        balances[owner] = _totalSupply;
        emit Transfer(0, owner, _totalSupply);
    }

    function burntokens(uint256 tokens) public onlyOwner {
        _totalSupply = (_totalSupply).sub(tokens);
    }

    function totalSupply() public view returns (uint256 total_Supply) {
        total_Supply = _totalSupply;
    }
    function balanceOf(address _owner)public view returns (uint256 balance) {
        return balances[_owner];
    }

    // Transfer the balance from owner's account to another account
    function transfer(address _to, uint256 _amount)public returns (bool ok) {
        require( _to != 0x0);
        require(balances[msg.sender] >= _amount && _amount >= 0);
        balances[msg.sender] = (balances[msg.sender]).sub(_amount);
        balances[_to] = (balances[_to]).add(_amount);
        emit Transfer(msg.sender, _to, _amount);
        return true;
    }

    // Send _value amount of tokens from address _from to address _to
    function transferFrom( address _from, address _to, uint256 _amount )public returns (bool ok) {
        require( _to != 0x0);
        require(balances[_from] >= _amount && allowed[_from][msg.sender] >= _amount && _amount >= 0);
        balances[_from] = (balances[_from]).sub(_amount);
        allowed[_from][msg.sender] = (allowed[_from][msg.sender]).sub(_amount);
        balances[_to] = (balances[_to]).add(_amount);
        emit Transfer(_from, _to, _amount);
        return true;
    }

    // Allow _spender to withdraw from your account, multiple times, up to the _value amount.
    // If this function is called again it overwrites the current allowance with _value.
    function approve(address _spender, uint256 _amount)public returns (bool ok) {
        require( _spender != 0x0);
        allowed[msg.sender][_spender] = _amount;
        emit Approval(msg.sender, _spender, _amount);
        return true;
     }

    function allowance(address _owner, address _spender)public view returns (uint256 remaining) {
        require( _owner != 0x0 && _spender !=0x0);
        return allowed[_owner][_spender];
    }

     // In case the ownership needs to be transferred
	function transferOwnership(address newOwner) external onlyOwner	{
	    uint256 x = balances[owner];
	    require( newOwner != 0x0);
	    balances[newOwner] = (balances[newOwner]).add(balances[owner]);
	    balances[owner] = 0;
	    owner = newOwner;
	    emit Transfer(msg.sender, newOwner, x);
	}
}

contract ChubbyCherubs is ERC721, Ownable {
    uint256 private mintCount = 0;

    uint256 public constant MAX_CHUB = 1111;
    uint256 public price = 70000000000000000; //0.07 Ether
    string baseTokenURI;
    bool public saleOpen;

    event ChubMinted(uint256 totalMinted);

    constructor(string memory baseURI) ERC721("Chubby Cherubs", "CHUB") {
        setBaseURI(baseURI);
    }

    function setBaseURI(string memory baseURI) public onlyOwner {
        baseTokenURI = baseURI;
    }

    function totalSupply() public view returns (uint256) {
        return mintCount;
    }

    function setPrice(uint256 _newPrice) external onlyOwner {
        price = _newPrice;
    }

    // Close sale if open, open sale if closed
    function flipSaleState() external onlyOwner {
        saleOpen = !saleOpen;
    }

    function withdrawAll() external onlyOwner {
        (bool success, ) = msg.sender.call{value: address(this).balance}("");
        require(success, "Transfer failed.");
    }

    function airdrop(address[] calldata _recipients) external onlyOwner {
        uint256 supply = totalSupply();
        require(
            supply + _recipients.length <= MAX_CHUB,
            "Airdrop will exceed maximum supply of CHUB"
        );
        require(_recipients.length != 0, "Address not found");
        mintCount += _recipients.length;
        for (uint256 i = 0; i < _recipients.length; i++) {
            require(_recipients[i] != address(0), "Airdrop to Null address");
            handleMint(_recipients[i], ++supply);
        }
    }

    // mint CHUB
    function mint(uint256 _count) external payable {
        uint256 supply = totalSupply();
        require(supply + _count <= MAX_CHUB, "Exceeds maximum supply of CHUB");
        require(_count > 0, "Minimum 1 CHUB has to be minted per transaction");

        if (msg.sender != owner()) {
            require(saleOpen, "Sale is not open yet");
            require(
                _count <= 10,
                "Maximum 10 Chubby Cherubs can be minted per transaction"
            );
            require(
                msg.value >= price * _count,
                "Ether sent with this transaction is not correct"
            );
        }

        mintCount += _count;

        for (uint256 i = 0; i < _count; i++) {
            handleMint(msg.sender, ++supply);
        }
    }

    function handleMint(address _to, uint256 tokenId) private {
        _safeMint(_to, tokenId);
        emit ChubMinted(tokenId);
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseTokenURI;
    }
}

contract LionGame is ERC721Enum, Ownable, ReentrancyGuard {
	using Strings for uint256;
	string public baseURI;
	// sale settings
	uint256 public cost = 0.05 ether;
	uint256 public maxSupply = 777;
	uint256 public maxMint = 25;
	bool public status = false;

	constructor(
        string memory _name,
        string memory _symbol,
        string memory _initBaseURI
    ) 

    ERC721(_name, _symbol){
        setBaseURI(_initBaseURI);
	}

	// internal
	function _baseURI() internal view virtual returns (string memory) {
	    return baseURI;
	}

    // free minting
	function freeMint(uint256 _mintAmount) public nonReentrant {
        uint256 s = totalSupply();
        require(!status, "Off" );
        require(_mintAmount > 0, "0" );
        require(_mintAmount <= maxMint, "Too many" );
        require(s + _mintAmount <= maxSupply, "Max" );

        for (uint256 i = 0; i < _mintAmount; ++i) {
            _safeMint(msg.sender, s + i, "");
	    }

        delete s;
	}

	// public minting
	function mint(uint256 _mintAmount) public payable nonReentrant {
        uint256 s = totalSupply();
        require(status, "Off" );
        require(_mintAmount > 0, "0" );
        require(_mintAmount <= maxMint, "Too many" );
        require(s + _mintAmount <= maxSupply, "Max" );
        require(msg.value >= cost * _mintAmount);

        for (uint256 i = 0; i < _mintAmount; ++i) {
            _safeMint(msg.sender, s + i, "");
        }

        delete s;
	}

	// admin minting
	function giveAway(address recipient) external onlyOwner {
        uint256 s = totalSupply();
        require(s <= maxSupply, "Max" );
        _safeMint(recipient, s, "" );
        delete s;	
	}

	function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: Nonexistent token");
        string memory currentBaseURI = _baseURI();
        return bytes(currentBaseURI).length > 0	? string(abi.encodePacked(currentBaseURI, tokenId.toString())) : "";
	}

	function setCost(uint256 _newCost) public onlyOwner {
	    cost = _newCost;
	}

	function setMaxMintAmount(uint256 _newMaxMintAmount) public onlyOwner {
	    maxMint = _newMaxMintAmount;
	}

	function setBaseURI(string memory _newBaseURI) public onlyOwner {
	    baseURI = _newBaseURI;
	}

	function setSaleStatus(bool _status) public onlyOwner {
	    status = _status;
	}

	function withdraw() public onlyOwner {
	    payable(msg.sender).call{value: address(this).balance}("");
	}

    function setmaxSupply(uint256 _newMaxSupply) public onlyOwner {
	    maxSupply = _newMaxSupply;
	}
}

contract PACFounder is ERC721 {
    // variables
    uint256 public minPrice = 100000000000000;
    address payable private beneficiary;
    uint256 public currentId;
    string defaultMetadata = "QmcnEZQiGVzPonWS2MENbdY8DkwhWcCW7YBQNk5yHYF112";

    constructor (address payable _beneficiary) public ERC721 ("PACDAO FOUNDER", "PAC-F") {
		beneficiary = _beneficiary;
		_setBaseURI("ipfs://");
    }

    // Payable Functions
    function mint() public payable {
		require(msg.value >= minPrice, "Must pay more");
		currentId += 1;
		_safeMint(msg.sender, currentId);
		_setTokenURI(currentId, defaultMetadata);
		uint _target = minPrice * 1075000000000000000 / 1e18;
		if (_target - minPrice < 1e14) {
			_target = minPrice + 1e14;
		}

		if (currentId < 20) {
			currentId += 1;
			_safeMint(beneficiary, currentId);
			_setTokenURI(currentId, defaultMetadata);
		}

		if (msg.value > _target) {
			minPrice = msg.value + 1e14;
		} else {
			minPrice = _target;
		}

		minPrice = minPrice / 1e14 * 1e14;
	}

	// Nonpayable Functions
	function withdraw() public {
		beneficiary.transfer(address(this).balance);
	}

	// Admin Functions
	function updateBeneficiary(address payable _newBeneficiary) public {		
		require(msg.sender == beneficiary);
		beneficiary = _newBeneficiary;
	}

	function setTokenUri(uint256 _tokenId, string memory _newUri) public {
		require(msg.sender == beneficiary);
		_setTokenURI(_tokenId, _newUri);
	}

	function setDefaultMetadata(string memory _newUri) public {
		require(msg.sender == beneficiary);
		defaultMetadata = _newUri;
	}

	// Fallback Functions
	receive() external payable {}
	fallback() external payable {}
}

contract Cannabis1Contract is ERC20 {
    string internal _name = "Cannabis1";
    string internal _symbol = "CAN1";
    string internal _standard = "ERC20";
    uint8 internal _decimals = 18;
    uint256 internal _totalSupply = 15000 * 1 ether;

    address internal _contractOwner;

    mapping(address => uint256) internal balances;
    mapping(address => mapping(address => uint256)) internal allowed;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    constructor () public {
        balances[msg.sender] = totalSupply();
        _contractOwner = msg.sender;
    }

    // Prevents sending ETH to the contract by mistake.
    function () external payable  {
        revert("This SmartContract is not payable");
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function standard() public view returns (string memory) {
        return _standard;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function contractOwner() public view returns (address) {
        return _contractOwner;
    }

    function transfer(address _to, uint256 _value) public returns (bool) {
        require(_to != address(0), "'_to' address has to be set");
        require(_value <= balances[msg.sender], "Insufficient balance");

        balances[msg.sender] = SafeMath.sub(balances[msg.sender], _value);
        balances[_to] = SafeMath.add(balances[_to], _value);

        emit Transfer(msg.sender, _to, _value);
        return true;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        require (_spender != address(0), "_spender address has to be set");
        require (_value > 0, "'_value' parameter has to be greater than 0");

        allowed[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool) {
        require(_from != address(0), "'_from' address has to be set");
        require(_to != address(0), "'_to' address has to be set");
        require(_value <= balances[_from], "Insufficient balance");
        require(_value <= allowed[_from][msg.sender], "Insufficient allowance");

        allowed[_from][msg.sender] = SafeMath.sub(allowed[_from][msg.sender], _value);
        balances[_from] = SafeMath.sub(balances[_from], _value);
        balances[_to] = SafeMath.add(balances[_to], _value);

        emit Transfer(_from, _to, _value);

        return true;
    }

    function allowance(address _owner, address _spender) public view returns (uint256) {
        return allowed[_owner][_spender];
    }

    function balanceOf(address _owner) public view returns (uint256) {
        return balances[_owner];
    }

    modifier onlyOwner() {
        require(isOwner(), "Only owner can do that");
        _;
    }

    function isOwner() public view returns (bool) {
        return msg.sender == _contractOwner;
    }

    function burn(uint256 _value) public {
        require(_value <= balances[msg.sender]);
        balances[msg.sender] = SafeMath.sub(balances[msg.sender], _value);
        _totalSupply = SafeMath.sub(_totalSupply, _value);
        emit Transfer(msg.sender, address(0), _value);
    }
}

