        _setupRole(MINTER_ROLE, _msgSender());
    }

    /// @dev Sets the royalty value and recipient
    /// @notice Only admin can call the function
    /// @param recipient The new recipient for the royalties
    /// @param value percentage (using 2 decimals - 10000 = 100, 0 = 0)
    function setRoyalties(address recipient, uint256 value) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "only admin allowed");
        require(
            recipient != 0x0000000000000000000000000000000000000000,
            "S16NFT: Royalty recipient address cannot be Zero Address"
        );
        require(value > 0, "S16NFT: invalid royalty percentage");
        _setRoyalties(recipient, value);

        royaltyRecipient = recipient;
        royaltyValue = value;
    }

    function cap() external view returns (uint256) {
        return _totalCap;
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721Enumerable, AccessControl, ERC2981Base) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _baseTokenURI;
    }

    function setBaseURI(string memory baseURI) public {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
            "Caller is not a admin"
        );

        _baseTokenURI = baseURI;
    }

    function mint(address _mintTo) external returns (bool) {
        require(hasRole(MINTER_ROLE, _msgSender()), "Caller is not a minter");
        require(_mintTo != address(0), "ERC721: mint to the zero address");
        _tokenIds.increment();
        uint256 totalSupply = totalSupply();
        require(_mintedSale <= _availableForSale, "sale minting completed");
        require(
            totalSupply + 1 <= _totalCap,
            "Cap reached, maximum 5555 mints possible"
        );

        _safeMint(_mintTo, _tokenIds.current());
        _mintedSale++;

        return true;
    }

    function mintReserve(address _mintTo) external returns (bool) {
        require(hasRole(MINTER_ROLE, _msgSender()), "Caller is not a minter");
        require(_mintTo != address(0), "ERC721: mint to the zero address");
        _tokenIds.increment();
        uint256 totalSupply = totalSupply();
        require(_mintedReserved <= _reserveTokens, "reserved sale completed");
        require(
            totalSupply + 1 <= _totalCap,
            "Cap reached, maximum 5555 mints possible"
        );
        _safeMint(_mintTo, _tokenIds.current());

        _mintedReserved++;

        return true;
    }
}

contract Poolboys is ERC721, Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIdCounter;

    uint256 private collectionStartingIndexBlock;
    uint256 public collectionStartingIndex;
    string public PBOY_PROVENANCE = "";

    uint256 public pboyPrice = 10000000000000000; // 0.01 ETH
    uint256 public constant maxPurchase = 30;
    uint256 public constant MAX_PBOYS = 4444;

    bool public publicSaleActive = false;

    string private _baseURIextended;

    constructor() ERC721("Poolboys", "PBOY") {}

    function withdraw() external onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    function setPrice(uint256 newPrice) external onlyOwner {
        pboyPrice = newPrice;
    }

    // Set base for the reveal
    function setBaseURI(string memory baseURI_) external onlyOwner() {
        _baseURIextended = baseURI_;
    }
    function _baseURI() internal view virtual override returns (string memory) {
        return _baseURIextended;
    }

    // Returns the total current supply
    function totalSupply() external view returns (uint256) {
        return _tokenIdCounter.current();
    }

    // Pause sale if active, make active if paused
    function flipSaleState() external onlyOwner {
        publicSaleActive = !publicSaleActive;
    }

    function _mintToken(address _to, uint numberOfTokens) private {
        require(numberOfTokens <= maxPurchase, "Can only mint 30 tokens at a time");
        require(_tokenIdCounter.current() + numberOfTokens <= MAX_PBOYS, "Purchase would exceed max supply");

        for(uint256 i; i < numberOfTokens; i++) {
            uint256 tokenId = _tokenIdCounter.current();
            if (tokenId < MAX_PBOYS) {
                _tokenIdCounter.increment();
                _safeMint(_to, tokenId);
            }
        }
    }

    // Set Poolboys aside for giveaways
    function reserveToken(address _to, uint numberOfTokens) external onlyOwner {
        _mintToken(_to,numberOfTokens);
    }

    // Mint Poolboys
    function mintToken(uint numberOfTokens) external payable nonReentrant {
        require(publicSaleActive, "Public sale is not active");
        require(pboyPrice * numberOfTokens <= msg.value, "Ether value sent is not correct");
        _mintToken(msg.sender,numberOfTokens);
        if (collectionStartingIndex == 0) {
            collectionStartingIndexBlock = block.number;
        }

    }

    // Set the starting index for the collection
    function setStartingIndices() external onlyOwner {

        require(collectionStartingIndexBlock != 0, "Starting index block must be set");
        require(collectionStartingIndex == 0, "Starting index is already set");

        collectionStartingIndex = uint256(blockhash(collectionStartingIndexBlock)) % MAX_PBOYS;

        if ((block.number - collectionStartingIndexBlock) > 255) {
            collectionStartingIndex = uint256(blockhash(collectionStartingIndexBlock - 1)) % MAX_PBOYS;
        }

        if (collectionStartingIndex == 0) {
            collectionStartingIndex++;
        }
    }

    // Set provenance once it's calculated
    function setProvenanceHash(string memory provenanceHash) external onlyOwner {
        PBOY_PROVENANCE = provenanceHash;
    }
}

contract ChromiaToken {
    mapping(address => uint256) private balances;
    mapping(address => mapping (address => uint256)) private allowed;
    uint256 private _totalSupply = 100000000;
    address public owner;

    constructor() {
        owner = msg.sender;
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address _owner) public view returns (uint256) {
        return balances[_owner];
    }

    function approve(address _spender, uint256 _amount) public returns (bool) {
        require(_spender != address(0), "ERC20: approve to the zero address");
        allowed[msg.sender][_spender] = _amount;
        emit Approval(msg.sender, _spender, _amount);
        return true;
    }

    function transfer(address _to, uint256 _amount) public returns (bool) {
        require(_to != address(0), "ERC20: transfer to the zero address");
        require(balances[msg.sender] >= _amount, "ERC20: transfer amount exceeds balance");
        require(_amount > 0 && balances[_to] + _amount > balances[_to], "ERC20: Invalid amount");

        balances[msg.sender] -= _amount;
        balances[_to] += _amount;
        emit Transfer(msg.sender, _to, _amount);
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _amount) public returns (bool) {
        require(_from != address(0) && _to != address(0), "ERC20: transfer from or to the zero address");
        require(balances[_from] >= _amount, "ERC20: transfer amount exceeds balance");
        require(allowed[_from][msg.sender] >= _amount, "ERC20: transfer amount exceeds allowance");
        require(_amount > 0 && balances[_to] + _amount > balances[_to], "ERC20: Invalid amount");

        balances[_from] -= _amount;
        balances[_to] += _amount;
        allowed[_from][msg.sender] -= _amount;
        emit Transfer(_from, _to, _amount);
        return true;
    }

    function allowance(address _owner, address _spender) public view returns (uint256) {
        return allowed[_owner][_spender];
    }

    event Approval(address indexed owner, address indexed spender, uint256 value);
    event Transfer(address indexed from, address indexed to, uint256 value);
}

contract POCTOKEN {
    string public name;
    string public symbol;
    uint8 public decimals = 8;
    uint256 public totalSupply;

    // This creates an array with all balances
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;

    // This generates a public event on the blockchain that will notify clients
    event Transfer(address indexed from, address indexed to, uint256 value);

    // This generates a public event on the blockchain that will notify clients
    event Approval(address indexed _owner, address indexed _spender, uint256 _value);

    // This notifies clients about the amount burnt
    event Burn(address indexed from, uint256 value);

    // Initializes contract with initial supply tokens to the creator of the contract
    constructor(uint256 initialSupply, string memory tokenName, string memory tokenSymbol) public {
        totalSupply = initialSupply * 10 ** uint256(decimals);  // Update total supply with the decimal amount
        balanceOf[msg.sender] = totalSupply;                // Give the creator all initial tokens
        name = tokenName;                                   // Set the name for display purposes
        symbol = tokenSymbol;                               // Set the symbol for display purposes
    }

    // Internal transfer, only can be called by this contract
    function _transfer(address _from, address _to, uint _value) internal {
        // Prevent transfer to 0x0 address. Use burn() instead
        require(_to != address(0x0));
        // Check if the sender has enough
        require(balanceOf[_from] >= _value);
        // Check for overflows
        require(balanceOf[_to] + _value >= balanceOf[_to]);
        // Save this for an assertion in the future
        uint previousBalances = balanceOf[_from] + balanceOf[_to];
        // Subtract from the sender
        balanceOf[_from] -= _value;
        // Add the same to the recipient
        balanceOf[_to] += _value;
        emit Transfer(_from, _to, _value);
        // Asserts are used to use static analysis to find bugs in your code. They should never fail
        assert(balanceOf[_from] + balanceOf[_to] == previousBalances);
    }

    // Send `_value` tokens to `_to` from your account
    function transfer(address _to, uint256 _value) public returns (bool success) {
        _transfer(msg.sender, _to, _value);
        return true;
    }

    // Send `_value` tokens to `_to` on behalf of `_from`
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);     // Check allowance
        allowance[_from][msg.sender] -= _value;
        _transfer(_from, _to, _value);
        return true;
    }

    // Set allowance for other address
    function approve(address _spender, uint256 _value) public
        returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    // Set allowance for other address and notify
    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData)
        public
        returns (bool success) {
        tokenRecipient spender = tokenRecipient(_spender);
        if (approve(_spender, _value)) {
            spender.receiveApproval(msg.sender, _value, address(this), _extraData);
            return true;
        }
    }

    // Destroy tokens
    function burn(uint256 _value) public returns (bool success) {
        require(balanceOf[msg.sender] >= _value);   // Check if the sender has enough
        balanceOf[msg.sender] -= _value;            // Subtract from the sender
        totalSupply -= _value;                      // Updates totalSupply
        emit Burn(msg.sender, _value);
        return true;
    }

    // Destroy tokens from other account
    function burnFrom(address _from, uint256 _value) public returns (bool success) {
        require(balanceOf[_from] >= _value);                // Check if the targeted balance is enough
        require(_value <= allowance[_from][msg.sender]);    // Check allowance
        balanceOf[_from] -= _value;                         // Subtract from the targeted balance
        allowance[_from][msg.sender] -= _value;             // Subtract from the sender's allowance
        totalSupply -= _value;                              // Update totalSupply
        emit Burn(_from, _value);
        return true;
    }
}

