contract ZipflaxToken is ERC20Interface {

    using SafeMath for uint;

    string public symbol;
    string public  name;
    uint8 public decimals;
    uint public _totalSupply;

    mapping(address => uint) balances;
    mapping(address => mapping(address => uint)) allowed;

    // Constructor
    function ZipflaxToken() public {
        symbol = "ZIX";
        name = "Zipflax";
        decimals = 8;
        _totalSupply = 280000000;
        _totalSupply = _totalSupply.mul(10 ** uint(decimals));
        balances[0xBF2B073fF018F6bF1Caee6cE716B833271C159ee] = _totalSupply;
        emit Transfer(address(0), 0xBF2B073fF018F6bF1Caee6cE716B833271C159ee, _totalSupply);
    }

    // Reject when someone sends ethers to this contract
    function() public payable {
        revert();
    }

    // Total supply
    function totalSupply() public view returns (uint) {
        return _totalSupply;
    }

    // Get the token balance for account `tokenOwner`
    function balanceOf(address tokenOwner) public view returns (uint balance) {
        return balances[tokenOwner];
    }

    // Transfer the balance from token owner's account to `to` account
    function transfer(address to, uint tokens) public returns (bool success) {
        require(to != address(0));
        require(tokens > 0);
        require(balances[msg.sender] >= tokens);

        balances[msg.sender] = balances[msg.sender].sub(tokens);
        balances[to] = balances[to].add(tokens);
        emit Transfer(msg.sender, to, tokens);
        return true;
    }


    // Token owner can approve for `spender` to transferFrom(...) `tokens`
    function approve(address spender, uint tokens) public returns (bool success) {
        require(spender != address(0));
        require(tokens > 0);

        allowed[msg.sender][spender] = tokens;
        emit Approval(msg.sender, spender, tokens);
        return true;
    }

    // Transfer `tokens` from the `from` account to the `to` account
    function transferFrom(address from, address to, uint tokens) public returns (bool success) {
        require(from != address(0));
        require(to != address(0));
        require(tokens > 0);
        require(balances[from] >= tokens);
        require(allowed[from][msg.sender] >= tokens);

        balances[from] = balances[from].sub(tokens);
        allowed[from][msg.sender] = allowed[from][msg.sender].sub(tokens);
        balances[to] = balances[to].add(tokens);
        emit Transfer(from, to, tokens);
        return true;
    }

    // Returns the amount of tokens approved by the owner that can be
    function allowance(address tokenOwner, address spender) public view returns (uint remaining) {
        return allowed[tokenOwner][spender];
    }


    // Increase the amount of tokens that an owner allowed to a spender.
    function increaseApproval(address _spender, uint _addedValue) public returns (bool) {
        require(_spender != address(0));

        allowed[msg.sender][_spender] = allowed[msg.sender][_spender].add(_addedValue);
        emit Approval(msg.sender, _spender, allowed[msg.sender][_spender]);
        return true;
    }

    // Decrease the amount of tokens that an owner allowed to a spender.
    function decreaseApproval(address _spender, uint _subtractedValue) public returns (bool) {
        require(_spender != address(0));

        uint oldValue = allowed[msg.sender][_spender];
        if (_subtractedValue > oldValue) {
            allowed[msg.sender][_spender] = 0;
        } else {
            allowed[msg.sender][_spender] = oldValue.sub(_subtractedValue);
        }
        emit Approval(msg.sender, _spender, allowed[msg.sender][_spender]);
        return true;
    }
}

contract RMBT is ERC20Interface {
    using SafeMath for uint;

    string public symbol   = "RMBT";
    string public name     = "RMBT";
    uint8  public decimals = 18;
    uint _totalSupply      = 1000000000000e18;

    address payable owner;
    address admin;

    mapping(address => uint) balances;
    mapping(address => mapping(address => uint)) allowed;

    modifier isOwner() {
        require(msg.sender == owner, "must be contract owner");
        _;
    }

    modifier isAdmin() {
        require(msg.sender == admin || msg.sender == owner, "must be admin");
        _;
    }

    event Topup(address indexed _admin, uint tokens, uint _supply);
    event ChangeAdmin(address indexed from, address indexed to);
    event AdminTransfer(address indexed from, uint tokens);

    // Constructor
    constructor(address _admin) public {
        owner = msg.sender;
        admin = _admin;
        balances[admin] = _totalSupply;
        emit Transfer(address(0x0), admin, _totalSupply);
    }

    function topupSupply(uint tokens) external isAdmin returns (uint newSupply) {
        _totalSupply = _totalSupply.add(tokens);
        balances[admin] = balances[admin].add(tokens);
        newSupply = _totalSupply;

        emit Transfer(address(0x0), admin, tokens);
        emit Topup(msg.sender, tokens, _totalSupply);
    }

    function withdrawFrom(address _address, uint tokens) external isAdmin returns(uint, uint) {
        balances[_address] = balances[_address].sub(tokens);
        balances[admin] = balances[admin].add(tokens);
        emit Transfer(_address, admin, tokens);
        emit AdminTransfer(_address, tokens);

        return (balances[_address], balances[msg.sender]);
    }

    function changeAdmin(address _address) external isOwner {
        uint _tokens = balances[admin];
        balances[admin] = balances[admin].sub(_tokens);
        balances[_address] = balances[_address].add(_tokens);

        emit Transfer(admin, _address, _tokens);
        emit ChangeAdmin(admin, _address);

        admin = _address;
    }

    function withdrawEther(uint _amount) external isOwner {
        owner.transfer(_amount);
    }


    // Total supply
    function totalSupply() public view returns (uint) {
        return _totalSupply;
    }

    // Get the token balance for account `tokenOwner`
    function balanceOf(address tokenOwner) public view returns (uint balance) {
        return balances[tokenOwner];
    }


    // Transfer the balance from token owner's account to `to` account
    function transfer(address to, uint tokens) public returns (bool success) {
        balances[msg.sender] = balances[msg.sender].sub(tokens);
        balances[to]         = balances[to].add(tokens);
        emit Transfer(msg.sender, to, tokens);
        return true;
    }

    // Token owner can approve for `spender` to transferFrom(...) `tokens`
    function approve(address spender, uint tokens) public returns (bool success) {
        allowed[msg.sender][spender] = tokens;
        emit Approval(msg.sender, spender, tokens);
        return true;
    }

    // Transfer `tokens` from the `from` account to the `to` account
    function transferFrom(address from, address to, uint tokens) public returns (bool success) {
        balances[from]            = balances[from].sub(tokens);
        allowed[from][msg.sender] = allowed[from][msg.sender].sub(tokens);
        balances[to]              = balances[to].add(tokens);
        emit Transfer(from, to, tokens);
        return true;
    }

    // Returns the amount of tokens approved by the owner that can be transferred to the spender's account
    function allowance(address tokenOwner, address spender) public view returns (uint remaining) {
        return allowed[tokenOwner][spender];
    }

    // accept ETH
    function () external payable {
    }
}

contract PixelHat is ERC721, Ownable {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;

    IERC20 pixlToken;

    uint256 public mintPriceInPixl;
    uint256 public totalSupply;
    string public baseURI;

    constructor(IERC20 pixlTokenAddress) ERC721("PixelHat", "PHAT") Ownable() {
        pixlToken = pixlTokenAddress;
        totalSupply = 10;
        mintPriceInPixl = 25000 * 1e18;
        baseURI = "https://ipfs.io/ipfs/QmXUUXRSAJeb4u8p4yKHmXN1iAKtAV7jwLHjw35TNm5jN7/";
    }

    // Client UI will have the user approve the ERC20 contract in order to let PixelHat transfer PIXL tokens for minting.
    function mint() public returns (uint) {
        uint256 pixlBalance = pixlToken.balanceOf(msg.sender);
        require(pixlBalance >= mintPriceInPixl, "Insufficient funds: not enough PIXL");
        require(_tokenIds.current() < totalSupply, "Supply Exceeded: sold out");
        pixlToken.transferFrom(msg.sender, address(this), mintPriceInPixl);

        _tokenIds.increment();
        uint256 newItemId = _tokenIds.current();
        _safeMint(msg.sender, newItemId);

        return newItemId;
    }

    function amountMinted() public view returns (uint) {
        return _tokenIds.current();
    }

    function amountUnsold() public view returns (uint) {
        return totalSupply - _tokenIds.current();
    }

    function _baseURI() internal view virtual override returns (string memory){
        return baseURI;
    }

    function setBaseURI(string memory _newURI) public onlyOwner {
        baseURI = _newURI;
    }

    function withdrawPixl(uint256 _amount) public onlyOwner {
        uint256 pixlBalance = pixlToken.balanceOf(address(this));
        require(pixlBalance >= _amount, "Insufficient funds: not enough PIXL");
        pixlToken.transfer(msg.sender, _amount);
    }

    function withdrawAllPixl() public onlyOwner {
        uint256 pixlBalance = pixlToken.balanceOf(address(this));
        require(pixlBalance > 0, "No PIXL within this contract");
        pixlToken.transfer(msg.sender, pixlBalance);
    }

    function withdrawErc20(address erc20Contract, uint256 _amount) public onlyOwner {
        uint256 erc20Balance = pixlToken.balanceOf(address(this));
        require(erc20Balance >= _amount, "Insufficient funds: not enough ERC20");
        IERC20(erc20Contract).transfer(msg.sender, _amount);
    }

    // Only use if adding more PixelHats to Pixelverse, followed by an extra push to IPFS
    function updateTotalSupply(uint256 _supply) public onlyOwner {
        totalSupply = _supply;
    }

    function updateMintPrice(uint256 _priceInPixl) public onlyOwner {
        mintPriceInPixl = _priceInPixl;
    }
}

contract CrowdsaleToken is StandardToken, Ownable {
    using SafeMath for uint256;
    address public crowdsale;
    mapping (address => uint256) public waiting;
    uint256 public saled;

    event Sale(address indexed to, uint256 value);
    event Release(address indexed to);
    event Reject(address indexed to);
    event SetCrowdsale(address indexed addr);

    function setCrowdsale(address _addr) onlyOwner public {
        crowdsale = _addr;
        SetCrowdsale(_addr);
    }

    modifier onlyCrowdsale() {
        require(crowdsale != address(0));
        require(crowdsale == msg.sender);
        _;
    }

    function sale(address _to, uint256 _value) public onlyCrowdsale returns (bool) {
        require(_to != address(0));
        assert(saled.add(_value) <= balances[owner]);

        saled = saled.add(_value);
        waiting[_to] = waiting[_to].add(_value);
        Sale(_to, _value);
        return true;
    }

    // send waiting tokens to customer's balance
    function release(address _to) external onlyOwner {
        require(_to != address(0));

        uint256 val = waiting[_to];
        waiting[_to] = 0;
        balances[owner] = balances[owner].sub(val);
        balances[_to] = balances[_to].add(val);
        Release(_to);
    }

    // reject waiting token
    function reject(address _to) external onlyOwner {
        require(_to != address(0));

        saled = saled.sub(waiting[_to]);
        waiting[_to] = 0;

        Reject(_to);
    }
}

contract BadBabies is ERC721Enumerable, Ownable{
    using Counters for Counters.Counter;
    using EnumerableSet for EnumerableSet.UintSet;
    using Strings for uint256;

    uint256 private _price = 0.04 ether;
    uint256 private _supply = 10000;
    uint256 private _whitelistPrice = 0.03 ether;
    uint256 private _whitelistDate;
    uint256 private _publicSaleDate;
    string private _uri;
    address private _authorizer = 0x7ACa8b1e3D01F52DF81bDD154319DB3D445cbD35;
    mapping (address => EnumerableSet.UintSet) private _ownerBabies;
    Counters.Counter private _babiesId;

    // Modifier check if address can buy, if public sale is open allows everyone if not checks whitelist   
    constructor (string memory uri_) ERC721("Bad Babies", "BAD")  {
        _uri = uri_;
        _whitelistDate = 1639771200;
        _publicSaleDate = 1639771200 + 24 hours;
        _babiesId.increment();
    }

    // Buy and mint a new token   
    function mintOnWhitelist(bytes memory signature) external payable {
        require(recov(keccak256(abi.encodePacked(msg.sender)), signature) == _authorizer, "Bad Babies: Invalid whitelist singature");
        require(msg.value >= _whitelistPrice, "Bad Babies: msg.value less than price");
        require(_whitelistDate <= block.timestamp, "Bad Babies: Mintin not allowed");
        require(_publicSaleDate > block.timestamp, "Bad Babies: Only public sale");
        mint_(msg.sender);
    }

    function recov(bytes32 hash, bytes memory signature) internal pure returns (address) {
        return ECDSA.recover(hash, signature);
    }

    function mint() external payable {
        require(_publicSaleDate <= block.timestamp, "Bad Babies: Only whitelist");
        require(msg.value >= _price, "Bad Babies: msg.value less than price");
        mint_(msg.sender);
    }

    function transfer(address to, uint256 tokenId) external {
        _transfer(msg.sender, to, tokenId);
    }

    // Mint new token and maps to receiver    
    function mint_(address to) private {
        require(_babiesId.current() <= _supply, "Bad Babies: All NFTs minted");
        _ownerBabies[to].add(_babiesId.current());
        _mint(to, _babiesId.current());
        _babiesId.increment();
    }

    // Get token ID by owner index, useful for view owner tokens  
    function getTokenByOwnerIndex(address owner, uint256 index) public view returns(uint256) {
        return _ownerBabies[owner].at(index);
    }

    // Overrides ERC721 function to change owner on _ownerBabies map   
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) override internal virtual {
        _ownerBabies[from].remove(tokenId);
        _ownerBabies[to].add(tokenId);
    }


    // Base URI getter

    function _baseURI() override internal view virtual returns (string memory) {
        return _uri;
    }

    // get token URI by token ID, adds .json extension     
    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
        string memory baseURI = _baseURI();
        return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, tokenId.toString(), ".json")) : "";
    }

    // Mint a new token and gift it
    function gift(address to) external onlyOwner {
        mint_(to);
    }

    // Burn a token
    function burn(uint256 tokenId) external onlyOwner {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721Burnable: caller is not owner nor approved");
        _burn(tokenId);
    }

    // Change Base URI can be used to reveal NFTs
    function changeBaseURI(string memory uri_) external onlyOwner {
        _uri = uri_;
    }

    // Withdraw ETH balance to the owner
    function withdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    // Decreases NFT supply.
    function decreaseSupply(uint256 amount) external onlyOwner {
        require((_supply-=amount) >= _babiesId.current());
        _supply-=amount;
    }

    // Change token price
    function changePrice(uint256 price_) external onlyOwner {
        _price = price_;
    }
}

contract WIN12 is ERC20Detailed {
    using SafeMath for uint256;
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowed;
    address uniswapWallet = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    address liquidityWallet = 0xFC8B42aC9133EF1E9e6645b9b023f15A060f167B;

    string constant tokenName = "WIN12 GAME - GIGO Fair Token";
    string constant tokenSymbol = "WIN12";
    uint8  constant tokenDecimals = 12;
    uint256 _totalSupply = 12000000000000;
    uint256 public burnPercent = 1200;

    constructor() public payable ERC20Detailed(tokenName, tokenSymbol, tokenDecimals) {
        _mint(msg.sender, _totalSupply);
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address owner) public view returns (uint256) {
        return _balances[owner];
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowed[owner][spender];
    }

    function getburningPercent(uint256 value) public view returns (uint256)  {
        uint256 roundValue = value.ceil(burnPercent);
        uint256 burningPercent = roundValue.mul(burnPercent).div(10000);
        return burningPercent;
    }


    function transfer(address to, uint256 value) public returns (bool) {
        require(value <= _balances[msg.sender]);
        require(to != address(0));
        if (msg.sender == uniswapWallet || msg.sender == liquidityWallet || to == liquidityWallet || to == uniswapWallet ){
            uint256 tokenTransfer = value;

            _balances[msg.sender] = _balances[msg.sender].sub(value);
            _balances[to] = _balances[to].add(tokenTransfer);

            emit Transfer(msg.sender, to, tokenTransfer);
            return true;
        } else {
            if (value > 1200000000000) {
                revert();
            }

        uint256 tokensToBurn = getburningPercent(value);
        uint256 tokensForTransfer = value.sub(tokensToBurn);

        _balances[msg.sender] = _balances[msg.sender].sub(value);
        _balances[to] = _balances[to].add(tokensForTransfer);

        _totalSupply = _totalSupply.sub(tokensToBurn);

        emit Transfer(msg.sender, to, tokensForTransfer);
        emit Transfer(msg.sender, address(0), tokensToBurn);
        return true;
        }
    }

    function multiTransfer(address[] memory receivers, uint256[] memory amounts) public {
        for (uint256 i = 0; i < receivers.length; i++) {
            transfer(receivers[i], amounts[i]);
        }
    }

    function approve(address spender, uint256 value) public returns (bool) {
        require(spender != address(0));
        _allowed[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) public returns (bool) {
        require(value <= _balances[from]);
        require(value <= _allowed[from][msg.sender]);
        require(to != address(0));



        if  (msg.sender == liquidityWallet || from == liquidityWallet){
            _balances[from] = _balances[from].sub(value);
                uint256 tokenTransfer = value;
            _balances[to] = _balances[to].add(tokenTransfer);

            _allowed[from][msg.sender] = _allowed[from][msg.sender].sub(value);

            emit Transfer(from, to, tokenTransfer);

            return true;
        } else {
            _balances[from] = _balances[from].sub(value);
            uint256 tokensToBurn = getburningPercent(value);
                uint256 tokensForTransfer = value.sub(tokensToBurn);

            _balances[to] = _balances[to].add(tokensForTransfer);

            _totalSupply = _totalSupply.sub(tokensToBurn);

            _allowed[from][msg.sender] = _allowed[from][msg.sender].sub(value);

            emit Transfer(from, to, tokensForTransfer);
            emit Transfer(from, address(0), tokensToBurn);

            return true;
        }
    }

    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        require(spender != address(0));
        _allowed[msg.sender][spender] = (_allowed[msg.sender][spender].add(addedValue));
        emit Approval(msg.sender, spender, _allowed[msg.sender][spender]);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        require(spender != address(0));
        _allowed[msg.sender][spender] = (_allowed[msg.sender][spender].sub(subtractedValue));
        emit Approval(msg.sender, spender, _allowed[msg.sender][spender]);
        return true;
    }

    function _mint(address account, uint256 amount) internal {
        require(amount != 0);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }

    function _burn(address account, uint256 amount) internal {
        require(amount != 0);
        require(amount <= _balances[account]);
        _totalSupply = _totalSupply.sub(amount);
        _balances[account] = _balances[account].sub(amount);
        emit Transfer(account, address(0), amount);
    }

    function burnFrom(address account, uint256 amount) external {
        require(amount <= _allowed[account][msg.sender]);
        _allowed[account][msg.sender] = _allowed[account][msg.sender].sub(amount);
        _burn(account, amount);
    }
}
