contract CrymCoin {
        
        string public constant name = "CrymCoin";
        string public constant symbol = "CRYM";
        uint8 public constant decimals = 8;
        uint public _totalSupply = 88888888800000000;
        uint256 public RATE = 1;
        bool public isMinting = false;
        string public constant generatedBy  = "Togen.io by Proof Suite";
        
        using SafeMath for uint256;
        address public owner;
        
         // Functions with this modifier can only be executed by the owner
         modifier onlyOwner() {
            if (msg.sender != owner) {
                throw;
            }
             _;
         }
     
        // Balances for each account
        mapping(address => uint256) balances;
        // Owner of account approves the transfer of an amount to another account
        mapping(address => mapping(address=>uint256)) allowed;

        // Its a payable function works as a token factory.
        function () payable{
            createTokens();
        }

        // Constructor
        constructor() public {
            owner = 0xf525f66d9207c273748be7dda455d185b950ee12; 
            balances[owner] = _totalSupply;
        }

        //allows owner to burn tokens that are not sold in a crowdsale
        function burnTokens(uint256 _value) onlyOwner {

             require(balances[msg.sender] >= _value && _value > 0 );
             _totalSupply = _totalSupply.sub(_value);
             balances[msg.sender] = balances[msg.sender].sub(_value);
             
        }



        // This function creates Tokens  
         function createTokens() payable {
            if(isMinting == true){
                require(msg.value > 0);
                uint256  tokens = msg.value.div(100000000000000).mul(RATE);
                balances[msg.sender] = balances[msg.sender].add(tokens);
                _totalSupply = _totalSupply.add(tokens);
                owner.transfer(msg.value);
            }
            else{
                throw;
            }
        }


        function endCrowdsale() onlyOwner {
            isMinting = false;
        }

        function changeCrowdsaleRate(uint256 _value) onlyOwner {
            RATE = _value;
        }


        
        function totalSupply() constant returns(uint256){
            return _totalSupply;
        }
        // What is the balance of a particular account?
        function balanceOf(address _owner) constant returns(uint256){
            return balances[_owner];
        }

         // Transfer the balance from owner's account to another account   
        function transfer(address _to, uint256 _value)  returns(bool) {
            require(balances[msg.sender] >= _value && _value > 0 );
            balances[msg.sender] = balances[msg.sender].sub(_value);
            balances[_to] = balances[_to].add(_value);
            Transfer(msg.sender, _to, _value);
            return true;
        }
        
    // Send _value amount of tokens from address _from to address _to
    // The transferFrom method is used for a withdraw workflow, allowing contracts to send
    // tokens on your behalf, for example to "deposit" to a contract address and/or to charge
    // fees in sub-currencies; the command should fail unless the _from account has
    // deliberately authorized the sender of the message via some mechanism; we propose
    // these standardized APIs for approval:
    function transferFrom(address _from, address _to, uint256 _value)  returns(bool) {
        require(allowed[_from][msg.sender] >= _value && balances[_from] >= _value && _value > 0);
        balances[_from] = balances[_from].sub(_value);
        balances[_to] = balances[_to].add(_value);
        allowed[_from][msg.sender] = allowed[_from][msg.sender].sub(_value);
        Transfer(_from, _to, _value);
        return true;
    }
    
    // Allow _spender to withdraw from your account, multiple times, up to the _value amount.
    // If this function is called again it overwrites the current allowance with _value.
    function approve(address _spender, uint256 _value) returns(bool){
        allowed[msg.sender][_spender] = _value; 
        Approval(msg.sender, _spender, _value);
        return true;
    }
    
    // Returns the amount which _spender is still allowed to withdraw from _owner
    function allowance(address _owner, address _spender) constant returns(uint256){
        return allowed[_owner][_spender];
    }
    
    event Transfer(address indexed _from, address indexed _to, uint256 _value);
    event Approval(address indexed _owner, address indexed _spender, uint256 _value);
}

contract Page is ERC721, ERC721Enumerable, Ownable, RoyaltiesV2Impl {
    event CollaboratorsChanged(address[] fromCollaboratorsAddress, address[] toCollaboratorsAddress, uint[] fromCollaboratorsSplit,  uint[] toCollaboratorsSplit);
    event CollectorChanged(address from, address to, uint256 tokenId);
    event GiveawayReleased(address to, uint256 amount);
    event GiveawayReceived(address from, uint256 amount);
    
    using Counters for Counters.Counter;

    bytes4 private constant _INTERFACE_ID_ERC2981 = 0x2a55205a;
    
    uint256 private _mintingPrice = 0.42 ether;

    uint256 private _totalReleased;
    string private _currentBaseURI;
    uint private _maxSupply;
    
    mapping(uint256 => uint256) private _released;
    mapping(uint => Counters.Counter) private _timesMinted;
    mapping(uint => bool) private _chapterOpenForMinting;
    
    address[] private _collaboratorsAddress = [0xDF5529D9AC4F342387a525B8495712967FD9dB83, 0x0AA457e8914618a5Fc26b4bc36B7B34385725883, 0x110920f43E2e4ff3F3dbcb839b70734d66bde8F2, address(this)];
    uint[] private _collaboratorsSplit =  [
        // This is the initial split
        21, // Token Collaborator 1
        21, // Token Collaborator 2
        42, // Artist
        16  // Token Holder Community
    ];

    CollaboratorsPaymentSplitter private _collaboratorsPaymentSplitter;
    
    constructor() ERC721("100 Pages", "PAGE") {
        _currentBaseURI = "https://batistaonthebeat.com/100pages/token/";
        _collaboratorsPaymentSplitter = new CollaboratorsPaymentSplitter(_collaboratorsAddress, _collaboratorsSplit);
    }

    function mintPage(uint8 page) external payable {
        require(address(_collaboratorsPaymentSplitter) != address(0), "PagesToken: CollectorPaymentSplitter not set");
        require(msg.value == _mintingPrice, "PagesToken: incorrect minting prince");
        require(1 <= page && page <= 100, "PagesToken: page must be between 1 and 100");

        uint chapter = getChapter(page);
        require(_chapterOpenForMinting[chapter] == true, "PagesToken: chapter is not opened for minting");
        
        uint nOfHundred = _timesMinted[page].current();
        require(nOfHundred < 100, "PagesToken: each page can only be minted 100 times");
        uint256 tokenId = id(page, nOfHundred+1);
        safeMint(msg.sender, tokenId);
        _timesMinted[page].increment();
        payable(_collaboratorsPaymentSplitter).transfer(_mintingPrice);
        // auto release to collaborators on mint
        _collaboratorsPaymentSplitter.release(payable(address(this)));
    }

    function safeMint(address to, uint256 tokenId) internal {
        _safeMint(to, tokenId);
        _setRoyalties(tokenId, payable(_collaboratorsPaymentSplitter), 1000);
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId);
        if (from != address(0) && _pendingGiveaway(tokenId) > 0){
            changeCollector(from, to, tokenId);
        }
    }

   /**
     * @dev The Ether received will be logged with {PaymentReceived} events. Note that these events are not fully
     * reliable: it's possible for a contract to receive Ether without triggering this function. This only affects the
     * reliability of the events, and not the actual splitting of Ether.
     *
     * To learn more about this see the Solidity documentation for
     * https://solidity.readthedocs.io/en/latest/contracts.html#fallback-function[fallback
     * functions].
     */
    receive() external payable virtual {
        emit GiveawayReceived(_msgSender(), msg.value);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable)
        returns (bool)
    {
        if(interfaceId == LibRoyaltiesV2._INTERFACE_ID_ROYALTIES) {
            return true;
        }
        if(interfaceId == _INTERFACE_ID_ERC2981) {
            return true;
        }
        return super.supportsInterface(interfaceId);
    }
    
    function setBaseURI(string memory baseURI) public onlyOwner {
        _currentBaseURI = baseURI;
    }

    function setMintingPrice(uint256 newPrice) public onlyOwner {
        _mintingPrice = newPrice;
    }

    function getMintingPrice() public view returns (uint) {
        return _mintingPrice;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _currentBaseURI;
    }
    
    function getChapter(uint page) public pure returns (uint) {
        require(1 <= page && page <= 100, "PagesToken: page must be between 1 and 100");
        return (uint(page-1)/20) + 1;
    }

    /**
     * @dev Change Collector for a tokenID.
     */
    function changeCollector(address from, address to, uint256 tokenId) internal {
        // this is to skip on minting
        if (from != address(0)){
            require(ownerOf(tokenId) == from, "CollectorsGiveawaySplitter: from account is not the owner for tokenId");
            // if tokenId has balance, pay it out then emit CollectorChanged
            if (_pendingGiveaway(tokenId) != 0){
                release(tokenId);
            }
        }
        emit CollectorChanged(from, to, tokenId);
    }

    function ReleaseMyGiveaway() public virtual {
        release(payable(_msgSender()));
    }

    function release(address payable account) public virtual {
        uint256 _ownedTokens = balanceOf(account);
        require(_ownedTokens > 0, "CollectorsGiveawaySplitter: account has no tokens");
        uint256 totalGiveaway = _pendingGiveaway(account);
        require(totalGiveaway > 0, "GiveawaySplitter: account is not due giveaway");

        Address.sendValue(account, totalGiveaway);
        
        _totalReleased += totalGiveaway;
        for (uint256 i = 0; i < (_ownedTokens ); i++) {
            uint currentToken = tokenOfOwnerByIndex(account, i);
            uint256 giveawayForToken = _pendingGiveaway(currentToken);
            _released[currentToken] += giveawayForToken;
        }
        emit GiveawayReleased(account, totalGiveaway);
    }

    function release(uint256 tokenId) public virtual {
        address tokenOwner = ownerOf(tokenId);
        require(tokenOwner != address(0), "CollectorsGiveawaySplitter: tokenId owner is the zero address");

        uint256 _tokenPendingGiveaways = _pendingGiveaway(tokenId);
        require(_tokenPendingGiveaways > 0, "CollectorsGiveawaySplitter: tokenId is not due any giveaways");
        Address.sendValue(payable(tokenOwner), _tokenPendingGiveaways);
        _totalReleased += _tokenPendingGiveaways;
        _released[tokenId]+= _tokenPendingGiveaways;
        emit GiveawayReleased(tokenOwner, _tokenPendingGiveaways);
    }

    function _pendingGiveaway(uint256 tokenId) private view returns (uint256) {
        return (address(this).balance + totalReleased()) / maxSupply() - _released[tokenId];
    }

    function PendingGiveaway() public view returns (uint256) {
        uint256 _ownedTokens = balanceOf(_msgSender());
        require(_ownedTokens > 0, "CollectorsGiveawaySplitter: account has no tokens");
        return _pendingGiveaway(_msgSender());
    }

    function _pendingGiveaway(address account) private view returns (uint256) {
        uint256 _ownedTokens = balanceOf(account);

        uint256 totalGiveaway = 0;
        for (uint256 i = 0; i < (_ownedTokens); i++) {
            uint currentToken = tokenOfOwnerByIndex(account, i);
            uint256 giveawayForToken = _pendingGiveaway(currentToken);
            totalGiveaway += giveawayForToken;
        }
        return totalGiveaway;
    }

    function totalReleased() public view returns (uint256) {
        return _totalReleased;
    }

    function setCollaboratorsPaymentSplitter(address[] memory collaboratorsAddress, uint[] memory collaboratorsSplit) public onlyOwner () {
        _collaboratorsPaymentSplitter = new CollaboratorsPaymentSplitter(_collaboratorsAddress, _collaboratorsSplit);
        emit CollaboratorsChanged(_collaboratorsAddress, collaboratorsAddress, _collaboratorsSplit, collaboratorsSplit);
        _collaboratorsAddress = collaboratorsAddress;
        _collaboratorsSplit = collaboratorsSplit;
    }

    function getCollaboratorsPaymentSplitter() public view returns (address) {
        return address(_collaboratorsPaymentSplitter);
    }

    function id(uint page, uint nOfHundred) internal pure returns (uint) {
        require(1 <= page && page <= 100 && nOfHundred > 0 && nOfHundred < 101, "PagesToken: page must be between 1 and 100 and nOfHundred 1-100");
        return (uint((page * 100) - (100 - nOfHundred)));
    }

    function ownerOf(uint page, uint nOfHundred) public view returns (address) {
        require(1 <= page && page <= 100 && nOfHundred > 0 && nOfHundred < 101, "PagesToken: page must be between 1 and 100 and nOfHundred 1-100");
        return ownerOf(id(page, nOfHundred));
    }

    function openChapterForMinting(uint chapter) public onlyOwner {
        require(1 <= chapter && chapter <= 5 , "PagesToken: chapter must be between 1 and 5");
        require(_chapterOpenForMinting[chapter] == false , "PagesToken: chapter is already opened");
        if (chapter > 1 ){
            require(_chapterOpenForMinting[chapter-1] == true , "PagesToken: previous chapter is not opened");
        }
        _chapterOpenForMinting[chapter] = true;
        _maxSupply = chapter * 2000;
    }

    function maxSupply() public view returns (uint) {
        return _maxSupply;
    }
    
    function canMintPage(uint page) public view returns (bool) {
        require(1 <= page && page <= 100, "PagesToken: page must be between 1 and 100");
        return _chapterOpenForMinting[getChapter(page)] && (timesMinted(page) < 100);
    }

    function timesMinted(uint page) public view returns (uint) {
        require(1 <= page && page <= 100, "page must be between 1 and 100");
        return _timesMinted[page].current();
    }

    function _setRoyalties(uint _tokenId, address payable _royaltiesReceipientAddress, uint96 _percentageBasisPoints) internal {
        LibPart.Part[] memory _royalties = new LibPart.Part[](1);
        _royalties[0].value = _percentageBasisPoints;
        _royalties[0].account = _royaltiesReceipientAddress;
        _saveRoyalties(_tokenId, _royalties);
    }

    function setRoyalties(uint _tokenId, address payable _royaltiesReceipientAddress, uint96 _percentageBasisPoints) public onlyOwner {
        _setRoyalties(_tokenId, _royaltiesReceipientAddress, _percentageBasisPoints);
    }

    function royaltyInfo(uint256 _tokenId, uint256 _salePrice) external view returns (address receiver, uint256 royaltyAmount) {
        LibPart.Part[] memory _royalties = royalties[_tokenId];
        if(_royalties.length > 0) {
            return (_royalties[0].account, (_salePrice * _royalties[0].value)/10000);
        }
        return (address(0), 0);
    }
}

contract Yggdrasil is ERC1155, Ownable, ERC1155Burnable {
    constructor()
        ERC1155("ipfs://QmUwpygdcrpdCbuQwLV4dw8xmfQSR21jnXAj9vz8AZHECF")
    {}

    //  Total token supply
    //  Altered by mint & burn functions
    uint256 supply;

    //  Address of the relayer used for token distribution
    address public relayerAddress;

    //  Address used for token minting and storage
    address public anthroWalletAddress;

    //  Getter function for front-ends
    function totalSupply() public view returns(uint256) {
        return (supply);
    }

    //  Set relayer address, can only be used by Owner
    function setRelayerAddress(address relayer) public onlyOwner {
        relayerAddress = relayer;
        setApprovalForAll(relayer, true);
    }

    //  Set anthromancer wallet address, can only be used by Owner
    function setAnthroWalletAddress(address anthroWallet) public onlyOwner {
        anthroWalletAddress = anthroWallet;
    }

    //  Main mint function, can only be used by Owner
    function mintBatch(uint total)
        public
        onlyOwner{

         uint[] memory tokenId = new uint[](1);
        tokenId[0] = 1;  

        uint[] memory amount = new uint[](1);
        amount[0] = total;


        supply = supply + total;

        _mintBatch(anthroWalletAddress, tokenId, amount, abi.encodePacked("1"));
    }

    //  Main distro function, can only be used by Owner & Relayer
    function transferToBacker(address backerAddress) public {
        require (msg.sender == owner() || msg.sender == relayerAddress);
        _safeTransferFrom(anthroWalletAddress, backerAddress, 1, 1, abi.encodePacked("1"));
    }

    //  Burn token function, can only be used by Token Owner or Approved
    function burn(
        address account,
        uint256 id,
        uint256 value
    ) public override virtual {
        require(
            account == _msgSender() || isApprovedForAll(account, _msgSender()),
            "ERC1155: caller is not owner nor approved"
        );

        _burn(account, id, value);
        supply= supply - value;
    }
}

contract EtchedNFT1155 is ERC1155, ERC165Storage, Ownable, IERC2981 {
    using Counters for Counters.Counter;

    Counters.Counter private _id;

    // royalty rate vars
    // scale: how many zeroes should follow the royalty rate
    // in the default values, there would be a 10% tax on a 18 decimal asset
    uint256 private rate = 10_000;
    uint256 private scale = 1e5;

    mapping(uint256 => string) private _tokenURIs;
    mapping(uint256 => address) private _creators;
    mapping(uint256 => uint256) private _totalSupply;
    // id => user => if they're a minter
    mapping(address => bool) private minters;

    event TokenURI(
        uint256 indexed id,
        string tokenUri,
        uint256 amount,
        address creator,
        address owner
    );
    event RoyaltyRateSet(uint256 indexed rate, uint256 indexed scale);
    event MinterUpdated(
        address indexed minter,
        bool indexed canMint,
        string userIdentifier
    );
    event CapSet(uint256 indexed id, uint256 indexed cap);

    constructor() ERC1155("") {
        ERC165Storage._registerInterface(type(IERC2981).interfaceId);
        ERC165Storage._registerInterface(type(IERC1155).interfaceId);
        ERC165Storage._registerInterface(type(IERC1155MetadataURI).interfaceId);

        // ids 1,2 reserved for airdrop
        _id.increment();
        _id.increment();

        // hardcoding for HDA airdrop
        _creators[1] = address(0xeb7b2f62BFBac835CCb3e1ddE38CE79D89a8445C);
        _tokenURIs[1] = "QmT4RVQwvY8tghLa2HzZEFvEGxNRsZf3Fxb8byooMK6Ff4";

        // hardcoding for founder's key
        _creators[2] = address(0xeb7b2f62BFBac835CCb3e1ddE38CE79D89a8445C);
        _tokenURIs[2] = "QmWrhHtZLEkUg46MjjkyhYW8uuijPm89842CcRTeHxUJ6j";
    }

    //  Queries the URI of an NFT
    // parameter: id is the index of the NFT to query
    // returns the URI string of the NFT
    function uri(uint256 id) public view override returns (string memory) {
        return _tokenURIs[id];
    }

    //  Queries the supply of a particular NFT
    // parameter: id is the index of the NFT to query the supply of
    // returns the total number of NFTs in circulation for this NFT
    function totalSupply(uint256 id) external view returns (uint256) {
        require(id <= _id.current(), "id has not been minted");
        return _totalSupply[id];
    }

    //  Given an NFT and the amount of a price, returns pertinent royalty information
    //  This function is specified in EIP-2981
    // parameter: id is the index of the NFT to calculate royalties on
    // parameter: _salePrice is the amount the NFT is being sold for
    // returns the address to send the royalties to, and the amount to send
    function royaltyInfo(uint256 id, uint256 _salePrice)
        external
        view
        override
        returns (address, uint256)
    {
        require(_exists(id), "royaltyInfo: nonexistent token");
        uint256 royaltyAmount = (_salePrice * rate) / scale;
        return (getCreator(id), royaltyAmount);
    }

    //  gets the global royalty rate
    //  divide rate by scale to get the percentage taken as royalties
    // @return a tuple of (rate, scale)
    function getRoyaltyRate() external view returns (uint256, uint256) {
        return (rate, scale);
    }

    //  Queries if an address is allowed to mint
    // parameter: minter is the address to check
    // returns bool if the address is a minter or not
    function isMinter(address minter) external view returns (bool) {
        return minters[minter];
    }

    //  gets the address of a creator for an NFT
    //  The first 450 tokenIds are reserved for the HDA airdrop
    // parameter: id is the index of the NFT to return the creator of
    // returns the address of the creator
    function getCreator(uint256 id) public view returns (address) {
        require(_exists(id), "getCreator: nonexistent token");
        return _creators[id];
    }

    //  Sets the global variables relating to royalties
    // parameter: _rate is the amount, that when adjusted with the scale, represents the royalty rate
    // parameter: _scale is the amount of decimal places to scale the rate when applying
    // example: given an 18-decimal currency, a rate of 5 with a scale of 1e16 would be 5%
    // since this is 0.05 to an 18-decimal currency
    function setRoyaltyRate(uint256 _rate, uint256 _scale) external onlyOwner {
        rate = _rate;
        scale = _scale;
        emit RoyaltyRateSet(_rate, _scale);
    }

    //  Allows contract owner to add or remove minters to a specific id
    // parameter: minter is the address to update
    // parameter: canMint is the status the address should be set to
    function setMinter(
        address minter,
        bool canMint,
        string calldata userIdentifier
    ) external onlyOwner {
        minters[minter] = canMint;
        emit MinterUpdated(minter, canMint, userIdentifier);
    }

    //  Allows a whitelisted address to mint a single NFT
    // parameter: creator is the address to be recorded as the NFT's creator
    // parameter: to is the address to mint the NFT to
    // parameter: amount is the amount of NFTs to mint
    // parameter: tokenUri is the IPFS hash of the NFT metadata
    function mint(
        address creator,
        address to,
        uint256 amount,
        string memory tokenUri
    ) external {
        _id.increment();
        uint256 id = _id.current();
        require(
            minters[msg.sender] == true || msg.sender == owner(),
            "unauthorized minter"
        );
        _mint(to, id, amount, "");
        _totalSupply[id] = amount;
        _tokenURIs[id] = tokenUri;
        _creators[id] = creator;
        emit TokenURI(id, tokenUri, amount, creator, to);
    }

    //  batch mint function
    //  can only be called by owner, will mint all NFTs to one address for distribution
    // parameter: to is the address to mint all NFTs to
    // parameter: creators is an ordered array of the addresses to list as creators for each NFT
    // parameter: amounts is an ordered array of the amount of each NFT to mint
    // parameter: uris is the uri of each NFT being minted
    function mintBatch(
        address to,
        address[] memory creators,
        uint256[] memory amounts,
        string[] memory uris
    ) external onlyOwner {
        uint256 len = amounts.length;
        uint256[] memory ids = new uint256[](len);
        for (uint256 i = 0; i < len; i++) {
            _id.increment();
            uint256 tokenId = _id.current();
            ids[i] = tokenId;
        }
        _mintBatch(to, ids, amounts, "");
        for (uint256 i = 0; i < len; i++) {
            _tokenURIs[ids[i]] = uris[i];
            _creators[ids[i]] = creators[i];
            _totalSupply[ids[i]] = amounts[i];
            emit TokenURI(ids[i], uris[i], amounts[i], _creators[ids[i]], to);
        }
    }

    //  Bespoke function for airdropping the HDA NFT and founder's key
    //  Cycles through an array of addresses and mints
    // parameter: id 1 (HDA) or 2(Founder's Key)
    // parameter: to is an array of addresses to send the NFT to
    // parameter: amount is the amount to send to the corresponding address in `to`
    function airdrop(
        uint256 id,
        address[] memory to,
        uint256[] memory amount
    ) external onlyOwner {
        require(id == 1 || id == 2, "only ids 1,2 can be airdropped");
        // HDA cap: 450, founder's key cap: 75
        uint256 cap = id == 1 ? 450 : 75;
        uint256 len = to.length;
        require(len == amount.length, "arrays must have equal length");
        for (uint256 i = 0; i < len; i++) {
            uint256 amt = amount[i];
            require(_totalSupply[id] + amt <= cap, "airdrop exceeds cap");
            address _to = to[i];
            _totalSupply[id] += amt;
            _mint(_to, id, amt, "");
        }
    }

    //  returns true if this contract implements the interface defined by `interfaceId`
    //  for more on interface ids, see https://eips.ethereum.org/EIPS/eip-165#how-interfaces-are-identified[EIP section]
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC165Storage, IERC165, ERC1155)
        returns (bool)
    {
        return ERC165Storage.supportsInterface(interfaceId);
    }

    function _exists(uint256 id) internal view returns (bool) {
        return
            _totalSupply[id] > 0 ||
            _creators[id] != address(0) ||
            keccak256(bytes(_tokenURIs[id])) != "";
    }
}

