contract Ghost_Core_Finance is ERC20Detailed {

  using SafeMath for uint256;
  mapping (address => uint256) private _balances;
  mapping (address => mapping (address => uint256)) private _allowed;

  string constant tokenName = "Ghost Core Finance";
  string constant tokenSymbol = "GHCO";
  uint8  constant tokenDecimals = 18;
  uint256 _totalSupply = 76000000000000000000000;
  uint256 public basePercent = 100;

  constructor() public payable ERC20Detailed(tokenName, tokenSymbol, tokenDecimals) {
    _mint(msg.sender, _totalSupply);
  }

  function totalSupply() public view returns (uint256) {
    return _totalSupply;
  }

  function balanceOf(address owner) public view returns (uint256) {
    return _balances[owner];
  }

  function allowance(address owner, address spender) public view returns (uint256) {
    return _allowed[owner][spender];
  }

  function findOnePercent(uint256 value) public view returns (uint256)  {
    uint256 roundValue = value.ceil(basePercent);
    uint256 onePercent = roundValue.mul(basePercent).div(10000);
    return onePercent;
  }
// Allow token to be traded/sent from account to account 
// allow for staking and governance plug-in

  function transfer(address to, uint256 value) public returns (bool) {
    require(value <= _balances[msg.sender]);
    require(to != address(0));

    uint256 tokensToBurn = 0;
    uint256 tokensToTransfer = value.sub(tokensToBurn);

    _balances[msg.sender] = _balances[msg.sender].sub(value);
    _balances[to] = _balances[to].add(tokensToTransfer);

    _totalSupply = _totalSupply.sub(tokensToBurn);

    emit Transfer(msg.sender, to, tokensToTransfer);
    emit Transfer(msg.sender, address(0), tokensToBurn);
    return true;
  }

  function multiTransfer(address[] memory receivers, uint256[] memory amounts) public {
    for (uint256 i = 0; i < receivers.length; i++) {
      transfer(receivers[i], amounts[i]);
    }
  }

  function approve(address spender, uint256 value) public returns (bool) {
    require(spender != address(0));
    _allowed[msg.sender][spender] = value;
    emit Approval(msg.sender, spender, value);
    return true;
  }

  function transferFrom(address from, address to, uint256 value) public returns (bool) {
    require(value <= _balances[from]);
    require(value <= _allowed[from][msg.sender]);
    require(to != address(0));

    _balances[from] = _balances[from].sub(value);

    uint256 tokensToBurn = 0;
    uint256 tokensToTransfer = value.sub(tokensToBurn);

    _balances[to] = _balances[to].add(tokensToTransfer);
    _totalSupply = _totalSupply.sub(tokensToBurn);

    _allowed[from][msg.sender] = _allowed[from][msg.sender].sub(value);

    emit Transfer(from, to, tokensToTransfer);
    emit Transfer(from, address(0), tokensToBurn);

    return true;
  }

  function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
    require(spender != address(0));
    _allowed[msg.sender][spender] = (_allowed[msg.sender][spender].add(addedValue));
    emit Approval(msg.sender, spender, _allowed[msg.sender][spender]);
    return true;
  }

  function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    require(spender != address(0));
    _allowed[msg.sender][spender] = (_allowed[msg.sender][spender].sub(subtractedValue));
    emit Approval(msg.sender, spender, _allowed[msg.sender][spender]);
    return true;
  }

//  1-time token mint/creation function. Tokens are only minted during contract creation, and cannot be done again.
  function _mint(address account, uint256 amount) internal {
    require(amount != 0);
    _balances[account] = _balances[account].add(amount);
    emit Transfer(address(0), account, amount);
  }

  function burn(uint256 amount) external {
    _burn(msg.sender, amount);
  }

  function _burn(address account, uint256 amount) internal {
    require(amount != 0);
    require(amount <= _balances[account]);
    _totalSupply = _totalSupply.sub(amount);
    _balances[account] = _balances[account].sub(amount);
    emit Transfer(account, address(0), amount);
  }

  function burnFrom(address account, uint256 amount) external {
    require(amount <= _allowed[account][msg.sender]);
    _allowed[account][msg.sender] = _allowed[account][msg.sender].sub(amount);
    _burn(account, amount);
  }
}

contract dolopManNFT is ERC721Enumerable, Ownable {
  using Strings for uint256;

  string public baseURI;
  string public baseExtension = ".json";
  uint256 public cost = .025 ether;
  uint256 public maxSupply = 5000;
  uint256 public maxMintAmount = 20;
  bool public paused = false;
  mapping(address => bool) public whitelisted;

  constructor(
    string memory _name,
    string memory _symbol,
    string memory _initBaseURI
  ) ERC721(_name, _symbol) {
    setBaseURI(_initBaseURI);
    mint(msg.sender, 20);
  }

  // internal
  function _baseURI() internal view virtual override returns (string memory) {
    return baseURI;
  }

  // public
  function mint(address _to, uint256 _mintAmount) public payable {
    uint256 supply = totalSupply();
    require(!paused);
    require(_mintAmount > 0);
    require(_mintAmount <= maxMintAmount);
    require(supply + _mintAmount <= maxSupply);

    if (msg.sender != owner()) {
        if(whitelisted[msg.sender] != true) {
          require(msg.value >= cost * _mintAmount);
        }
    }

    for (uint256 i = 1; i <= _mintAmount; i++) {
      _safeMint(_to, supply + i);
    }
  }

  function walletOfOwner(address _owner)
    public
    view
    returns (uint256[] memory)
  {
    uint256 ownerTokenCount = balanceOf(_owner);
    uint256[] memory tokenIds = new uint256[](ownerTokenCount);
    for (uint256 i; i < ownerTokenCount; i++) {
      tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
    }
    return tokenIds;
  }

  function tokenURI(uint256 tokenId)
    public
    view
    virtual
    override
    returns (string memory)
  {
    require(
      _exists(tokenId),
      "ERC721Metadata: URI query for nonexistent token"
    );

    string memory currentBaseURI = _baseURI();
    return bytes(currentBaseURI).length > 0
        ? string(abi.encodePacked(currentBaseURI, tokenId.toString(), baseExtension))
        : "";
  }

  //only owner
  function setCost(uint256 _newCost) public onlyOwner() {
    cost = _newCost;
  }

  function setmaxMintAmount(uint256 _newmaxMintAmount) public onlyOwner() {
    maxMintAmount = _newmaxMintAmount;
  }

  function setBaseURI(string memory _newBaseURI) public onlyOwner {
    baseURI = _newBaseURI;
  }

  function setBaseExtension(string memory _newBaseExtension) public onlyOwner {
    baseExtension = _newBaseExtension;
  }

  function pause(bool _state) public onlyOwner {
    paused = _state;
  }

 function whitelistUser(address _user) public onlyOwner {
    whitelisted[_user] = true;
  }

  function removeWhitelistUser(address _user) public onlyOwner {
    whitelisted[_user] = false;
  }

  function withdraw() public payable onlyOwner {
    require(payable(msg.sender).send(address(this).balance));
  }
}

abstract contract ERC721EnumerableSimple is ERC721, IERC721Enumerable {
    // user => tokenId[]
    mapping(address => mapping(uint => uint)) private _ownedTokens;

    // tokenId => index of _ownedTokens[user] (used when changing token ownership)
    mapping(uint => uint) private _ownedTokensIndex;

    // current total amount of token minted
    uint private _totalSupply;

    function supportsInterface(bytes4 interfaceId) public view virtual override(IERC165, ERC721) returns (bool) {
        return interfaceId == type(IERC721Enumerable).interfaceId || super.supportsInterface(interfaceId);
    }

    function tokenOfOwnerByIndex(address owner, uint256 index) public view virtual override returns (uint256) {
        require(index < ERC721.balanceOf(owner), "ERC721Enumerable: owner index out of bounds");
        return _ownedTokens[owner][index];
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function tokenByIndex(uint index) public view virtual override returns (uint) {
        require(index < ERC721EnumerableSimple.totalSupply(), "ERC721Enumerable: global index out of bounds");
        return index;
    }

    /// Hook that is called before any token transfer. This includes minting
    function _beforeTokenTransfer(
        address from,
        address to,
        uint tokenId
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, tokenId);

        if (from == address(0)) {
            assert(tokenId == _totalSupply); // Ensure token is minted sequentially
            _totalSupply += 1;
        } else if (from != to) {
            _removeTokenFromOwnerEnumeration(from, tokenId);
        }

        if (to == address(0)) {
            // do nothing
        } else if (to != from) {
            _addTokenToOwnerEnumeration(to, tokenId);
        }
    }

    // Private function to add a token to this extension's ownership-tracking data structures.
    // Parameter: to is address representing the new owner of the given token ID
    // Parameter: tokenId is the ID of the token to be added to the tokens list of the given address
    function _addTokenToOwnerEnumeration(address to, uint tokenId) private {
        uint length = ERC721.balanceOf(to);
        _ownedTokens[to][length] = tokenId;
        _ownedTokensIndex[tokenId] = length;
    }

    // Paramter: from is the address representing the previous owner of the given token ID
    // Parameter: tokenId is the ID of the token to be removed from the tokens list of the given address
    function _removeTokenFromOwnerEnumeration(address from, uint tokenId) private {
        // To prevent a gap in from's tokens array, we store the last token in the index of the token to delete, and
        // then delete the last slot (swap and pop).

        uint lastTokenIndex = ERC721.balanceOf(from) - 1;
        uint tokenIndex = _ownedTokensIndex[tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary
        if (tokenIndex != lastTokenIndex) {
            uint lastTokenId = _ownedTokens[from][lastTokenIndex];

            _ownedTokens[from][tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
            _ownedTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index
        }

        // This also deletes the contents at the last position of the array
        delete _ownedTokensIndex[tokenId];
        delete _ownedTokens[from][lastTokenIndex];
    }
}

contract WStakingRewards is ERC1155('WStakingRewards'), ReentrancyGuard, IERC20Wrapper {
  using SafeMath for uint;
  using HomoraMath for uint;
  using SafeERC20 for IERC20;

  address public immutable staking; // Staking reward contract address
  address public immutable underlying; // Underlying token address
  address public immutable reward; // Reward token address

  constructor(
    address _staking,
    address _underlying,
    address _reward
  ) public {
    staking = _staking;
    underlying = _underlying;
    reward = _reward;
    IERC20(_underlying).safeApprove(_staking, uint(-1));
  }

  //  Return the underlying ERC20 for the given ERC1155 token id.
  function getUnderlyingToken(uint) external view override returns (address) {
    return underlying;
  }

  // Return the conversion rate from ERC1155 to ERC20, multiplied 2**112.
  function getUnderlyingRate(uint) external view override returns (uint) {
    return 2**112;
  }

  // Mint ERC1155 token for the specified amount
  // Parameter: amount is the Token amount to wrap
  function mint(uint amount) external nonReentrant returns (uint) {
    IERC20(underlying).safeTransferFrom(msg.sender, address(this), amount);
    IStakingRewards(staking).stake(amount);
    uint rewardPerToken = IStakingRewards(staking).rewardPerToken();
    _mint(msg.sender, rewardPerToken, amount, '');
    return rewardPerToken;
  }

  // Burn ERC1155 token to redeem ERC20 token back.
  // Parameter: id is the Token id to burn
  // Parameter: amount is the Token amount to burn
  function burn(uint id, uint amount) external nonReentrant returns (uint) {
    if (amount == uint(-1)) {
      amount = balanceOf(msg.sender, id);
    }
    _burn(msg.sender, id, amount);
    IStakingRewards(staking).withdraw(amount);
    IStakingRewards(staking).getReward();
    IERC20(underlying).safeTransfer(msg.sender, amount);
    uint stRewardPerToken = id;
    uint enRewardPerToken = IStakingRewards(staking).rewardPerToken();
    uint stReward = stRewardPerToken.mul(amount).divCeil(1e18);
    uint enReward = enRewardPerToken.mul(amount).div(1e18);
    if (enReward > stReward) {
      IERC20(reward).safeTransfer(msg.sender, enReward.sub(stReward));
    }
    return enRewardPerToken;
  }
}

contract Goofballs is ERC721Enumerable, Ownable, ReentrancyGuard {
    uint8 public constant BLOCK_SIZE = 32;
    uint8 public constant MAX_PURCHASE = 20;

    uint256 public MAX_SUPPLY;
    uint256 public dropped;
    bool public saleIsActive = true;
    uint256 private _price = 0.05 ether;
    string private _baseTokenURI;

    uint16[] public _availableBlocks;
    uint8[] public _numAvailableInBlock;

    address private allowedMinter = address(0);
    uint256 public reserved = 0;

    constructor(uint256 maxSupply, uint256 initialDrop, string memory baseTokenURI) ERC721("Goofball", "GOOF") {
        require(maxSupply % BLOCK_SIZE == 0);
        MAX_SUPPLY = maxSupply;
        _baseTokenURI = baseTokenURI;
        _drop(initialDrop);
    }

    function getNFTPrice() public view returns (uint256) {
        return _price;
    }

    function mint(uint256 num) public payable nonReentrant {
        uint256 supply = totalSupply();
        require(saleIsActive, "Sale must be active!");
        require(num <= MAX_PURCHASE, "Exceeds maximum mint amount");
        require(supply + num + reserved <= dropped, "Exceeds maximum supply");
        require(msg.value >= getNFTPrice() * num, "Ether sent is not correct");
        for (uint8 i; i < num; i++) {
            mintToken(msg.sender, i);
        }
    }

    function mintToken(address _newOwner, uint256 num) private {
        uint256 seed = uint256(keccak256(abi.encodePacked(msg.sender, block.timestamp, block.difficulty, num)));
        uint8 indexInBlock = uint8(seed % BLOCK_SIZE);
        uint16 blockIndex = uint16((seed / BLOCK_SIZE) % _availableBlocks.length);
        uint16 blockStart = _availableBlocks[blockIndex];
        while (_exists(blockStart + indexInBlock + 1)) {
            indexInBlock = (indexInBlock + 1) % BLOCK_SIZE;
        }
        _safeMint(_newOwner, blockStart + indexInBlock + 1);
        if (_numAvailableInBlock[blockIndex] <= 1) {
            uint256 lastIndex = _availableBlocks.length - 1;
            if (blockIndex != lastIndex) {
                _availableBlocks[blockIndex] = _availableBlocks[lastIndex];
                _numAvailableInBlock[blockIndex] = _numAvailableInBlock[lastIndex];
            }
            _numAvailableInBlock.pop();
            _availableBlocks.pop();
        } else {
            _numAvailableInBlock[blockIndex]--;
        }
    }

    function _drop(uint256 num) internal {
        require(num % BLOCK_SIZE == 0);
        require(dropped + num <= MAX_SUPPLY);
        uint16 lastBlock = uint16((dropped + num) / BLOCK_SIZE);
        for (uint16 i = uint16(dropped / BLOCK_SIZE); i < lastBlock; i++) {
            _availableBlocks.push(i * BLOCK_SIZE);
            _numAvailableInBlock.push(BLOCK_SIZE);
        }
        dropped += num;
    }

    function drop(uint256 num) external onlyOwner {
        _drop(num);
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _baseTokenURI;
    }

    // https://docs.opensea.io/docs/contract-level-metadata
    function contractURI() public view returns (string memory) {
        return string(abi.encodePacked(_baseURI(), "contract"));
    }

    function walletOfOwner(address _owner) public view returns (uint256[] memory) {
        uint256 tokenCount = balanceOf(_owner);

        uint256[] memory tokensId = new uint256[](tokenCount);
        for (uint256 i; i < tokenCount; i++) {
            tokensId[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokensId;
    }

    function setBaseURI(string memory baseURI) public onlyOwner {
        _baseTokenURI = baseURI;
    }

    function withdrawBalance(uint256 amount) external onlyOwner {
        if (amount == 0) {
            amount = address(this).balance;
        }
        // https://consensys.github.io/smart-contract-best-practices/recommendations/#dont-use-transfer-or-send
        // solhint-disable-next-line avoid-low-level-calls
        (bool success, ) = payable(owner()).call{value: amount}("");
        require(success, "Transfer failed.");
    }

    function giveAway(address to, uint256 num) external onlyOwner nonReentrant {
        uint256 supply = totalSupply();
        require(supply + num + reserved <= dropped, "Exceeds maximum supply");
        for (uint256 i; i < num; i++) {
            mintToken(to, i);
        }
    }

    function setRestrictedMinter(address minter, uint16 reservedCount) external onlyOwner {
        allowedMinter = minter;
        reserved = reservedCount;
    }

    function restrictedMint(address to, uint256 num) external nonReentrant {
        uint256 supply = totalSupply();
        require(allowedMinter == msg.sender, "Contract not allowed to mint");
        require(num <= MAX_PURCHASE, "Exceeds maximum mint amount");
        require(saleIsActive, "Sale must be active!");
        require(num <= reserved, "Exceeds reserved number");
        require(supply + num <= dropped, "Exceeds maximum supply");
        for (uint8 i; i < num; i++) {
            mintToken(to, i);
        }
        reserved -= uint16(num);
    }

    function setPrice(uint256 price) external onlyOwner {
        _price = price;
    }

    function flipSaleState() external onlyOwner {
        saleIsActive = !saleIsActive;
    }
}

contract legendary_tkn is ERC721, ERC721URIStorage, Pausable, Ownable {
    using Address for address;
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIdCounter;
    
    uint256 public maxSupply = 100;
    uint256 public price = 150000000000000000; // 0.15 ETH
    bool public saleOpen = false;
    bool public presaleOpen = false;
    string public baseURI;

    mapping(address => uint256) public Whitelist;

    receive() external payable {}

    constructor() ERC721('Legendary K-Noids', 'LTKN') {
    }

    function mint(uint256 amount) public payable {
        require(!Address.isContract(msg.sender), "Contracts are not allowed to mint");
        require(msg.value >= price * amount, 'Not enough was paid');

        if (!saleOpen) {
            require(presaleOpen == true, 'Presale is not open');
            uint256 who = Whitelist[msg.sender];
            require(who > 0, 'This address is not whitelisted for the presale');
            require(amount <= who, 'This address is not allowed to mint that many during the presale');
            for (uint256 i = 0; i < amount; i++) 
                internalMint(msg.sender);
                Whitelist[msg.sender] = who - amount;
            return;
        }

        require(amount <= 10, 'Only 10 per transaction allowed');
        for (uint256 i = 0; i < amount; i++) internalMint(msg.sender);
    }

    function internalMint(address to) internal {
        require(_tokenIdCounter.current() < maxSupply, 'Supply depleted');
        _tokenIdCounter.increment();
        _safeMint(to, _tokenIdCounter.current());
    }

    function whitelistAddress(address[] memory who, uint256 amount) public onlyOwner {
        for (uint256 i = 0; i < who.length; i++) Whitelist[who[i]] = amount;
    }

    function withdraw() public onlyOwner {
        uint256 balance = address(this).balance;
        payable(owner()).transfer(balance);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function toggleSale() public onlyOwner {
        saleOpen = !saleOpen;
    }

    function togglePresale() public onlyOwner {
        presaleOpen = !presaleOpen;
    }

    function setBaseURI(string memory newBaseURI) public onlyOwner {
        baseURI = newBaseURI;
    }

    function setPrice(uint256 newPrice) public onlyOwner {
        price = newPrice;
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    // The following functions are overrides required by Solidity.

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override(ERC721) whenNotPaused {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

