contract LobsterBeachClub is VRFConsumerBase, ERC721, ERC721Enumerable, ERC721URIStorage, Ownable {

    uint256 public maxSupply; // needed public for LobsterGenome
    uint256 public seedNumber; // needed public for LobsterGenome

    string internal baseURI;

    bytes32 internal keyHash;
    uint256 internal linkFee;

    uint16 internal maxPurchase;
    uint256 internal mintingFee;

    uint256 internal maxPresaleSupply;
    uint256 internal revealDate;
    uint256 internal presaleStartingSupply;

    bool internal saleIsActive;
    bool internal presaleIsActive;
    bool internal seedNumberRequested;
    bool internal seedNumberSet;

    mapping(uint256 => uint256) internal promoLobster;
    mapping(address => bool) internal isWhitelisted;

    event SeedRequested(bool _value);
    event SeedSet(bool _value, uint256 _seedNumber);
    event PresaleState(bool _value, uint256 _startingSupply);
    event SaleState(bool _value);
    event Whitelist(address indexed _address, bool _value);
    event Minted(uint256 _value);
    event RevealTime(uint256 _time);
    event MintFee(uint256 _value);

    // External contract to fetch lobster genes from ILobsterGenome public lobsterGenome;
    constructor(string memory name, string memory symbol, uint256 maxNftSupply, uint256 saleStart) ERC721(name, symbol) ERC721Enumerable() VRFConsumerBase(
            0xf0d54349aDdcf704F77AE15b96510dEA15cb7952, // VRF Coordinator
            0x514910771AF9Ca656af840dff83E8264EcF986CA  // LINK Token
        )
    {
        mintingFee = 0.085 * 10 ** 18;
        keyHash = 0xAA77729D3466CA35AE8D28B3BBAC7CC36A5031EFDC430821C02BC31A238AF445;
        linkFee = 2 * 10 ** 18; // 2 LINK required to call VRF
        baseURI = "https://pegnc1ml7b.execute-api.us-west-1.amazonaws.com/api/lobsters/";
        maxSupply = maxNftSupply;
        maxPresaleSupply = 1500;
        maxPurchase = 20;
        revealDate = saleStart + (86400 * 9); // 9 days from start
        emit RevealTime(revealDate);
        emit MintFee(mintingFee);
    }

    function contractURI() public view returns (string memory) {
        return string(abi.encodePacked(baseURI, "metadata"));
    }

    function setLobsterGenome(address lgAddress) public onlyOwner {
        lobsterGenome = ILobsterGenome(lgAddress);
    }


    // Set the minting fee
    function setMintingFee(uint256 _mintingFee) public onlyOwner {
        mintingFee = _mintingFee;
        emit MintFee(mintingFee);
    }


    // Start state of presale or sale
    function setSaleState(bool _presale, bool _val) public onlyOwner {
        if (_presale) {
            presaleIsActive = _val;
            if (_val) {
                presaleStartingSupply = totalSupply();
            }
            emit PresaleState(_val, presaleStartingSupply);
        } else {
            saleIsActive = _val;
            emit SaleState(_val);
        }
    }


    // Add or remove addresses from presale whitelist
    function whitelist(address[] memory addresses, bool _val) public onlyOwner {
        for (uint i; i < addresses.length; i++) {
            isWhitelisted[addresses[i]] = _val;
            emit Whitelist(addresses[i], _val);
        }
    }


    // Reserve some lobsters for these addresses
    function reserveLobsters(address[] memory addresses) public onlyOwner {
        for(uint i; i < addresses.length; i++) {
            uint256 lobsterId = totalSupply();
            _mint(addresses[i], lobsterId);
            _setTokenURI(lobsterId, uint2str(lobsterId));
        }
        emit Minted(totalSupply());
    }


    // Create promo lobsters for these addresses
    // Promo lobsters are lobsters with predefined geneSequences
    // Promo lobsters are non-transferrable so they're kept track of in a mapping
    function createPromoLobsters(uint256[] memory geneSequences, address[] memory addresses) public onlyOwner {
        require(geneSequences.length == addresses.length, "Argument lengths must be equal");
        for(uint i = 0; i < geneSequences.length; i++) {
            uint256 lobsterId = totalSupply();
            _mint(addresses[i], lobsterId);
            _setTokenURI(lobsterId, uint2str(lobsterId));
            promoLobster[lobsterId] = geneSequences[i];
        }
        emit Minted(totalSupply());
    }


    // Mint lobsters to an address
    // Presale must be active or sale must be active to mint
    // To mint during presale the msg.sender must be whitelisted and can't exceed presaleStartSupply + maxPresaleSupply
    // To mint you must provide minting fee for each token requested and cannot exceed max token supply
    // If minting after reveal date or minting last available supply, submit request from Chainlink VRF
    // If maxSupply reached then sale is no longer active
    // If presaleStartingSupply + maxPresaleSupply is reached then presale is no longer active
    function mint(uint256 numberOfTokens, address _to) public payable {
        require(numberOfTokens <= maxPurchase, "Exceeded the max purchase amount");
        if (presaleIsActive) {
            require(isWhitelisted[msg.sender], "Not whitelisted");
            require(totalSupply() + numberOfTokens <= presaleStartingSupply + maxPresaleSupply, "Exceeded max presale supply");
        } else {
            require(saleIsActive, "Sale must be active");
        }
        require(mintingFee * numberOfTokens <= msg.value, "Mint fee is not correct");
        require(totalSupply() + numberOfTokens <= maxSupply, "Purchase would exceed max supply");

        for(uint i = 0; i < numberOfTokens; i++) {
            uint256 lobsterId = totalSupply();
            _mint(_to, lobsterId);
            _setTokenURI(lobsterId, uint2str(lobsterId));
        }
        emit Minted(totalSupply());

        if (!seedNumberRequested && (totalSupply() >= maxSupply || block.timestamp >= revealDate)) {
            getRandomNumber();
        }

        if (totalSupply() >= maxSupply) {
            saleIsActive = false;
            setSaleState(false, false);
        } else if (presaleIsActive && (totalSupply() >= (presaleStartingSupply + maxPresaleSupply))) {
            presaleIsActive = false;
            setSaleState(true, false);
        }
    }


    // Gives contract owner ability to manually request seed number from Chainlink VRF
    function emergencySetSeedNumber() public onlyOwner {
        getRandomNumber();
    }

    function setRevealTimestamp(uint256 revealTimeStamp) public onlyOwner {
        revealDate = revealTimeStamp;
        emit RevealTime(revealDate);
    } 

    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    function setBaseURI(string memory _baseURI) public onlyOwner {
        baseURI = _baseURI;
    }


    // Get the gene sequence of a lobster if seedNumberSet / lobsters are revealed
    // If lobster is a promo, then return preset gene sequence
    // Gene sequencing logic is outsourced to external LobsterGenome contract
    function getLobster(uint256 tokenId) public view returns (uint256 geneSequence) {
        require(seedNumberSet, "Seed number is not set");
        require(_exists(tokenId), "Token id does not exist");
        if (promoLobster[tokenId] != 0) {
            return promoLobster[tokenId];
        }
        return lobsterGenome.getGeneSequence(tokenId);
    }


    // Callback function for Chainlink VRF
    // Randomness is set as the seedNumber, revealing all Lobsters
    function fulfillRandomness(bytes32 requestId, uint256 randomness) internal override {
        seedNumber = randomness;
        seedNumberSet = true;
        emit SeedSet(true, seedNumber);
    }


    // Request a random number from Chainlink VRF
    function getRandomNumber() internal {
        require(LINK.balanceOf(address(this)) > linkFee && !seedNumberSet, "Not enough LINK or seed set already");
        requestRandomness(keyHash, linkFee);
        seedNumberRequested = true;
        emit SeedRequested(true);
    }

    function uint2str(uint _i) internal pure returns (string memory _uintAsString) {
        if (_i == 0) {
            return "0";
        }
        uint j = _i;
        uint len;
        while (j != 0) {
            len++;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint k = len;
        while (_i != 0) {
            k = k-1;
            uint8 temp = (48 + uint8(_i - _i / 10 * 10));
            bytes1 b1 = bytes1(temp);
            bstr[k] = b1;
            _i /= 10;
        }
        return string(bstr);
    }

    function withdraw() public onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    function withdrawLink() external onlyOwner {
        LINK.transfer(msg.sender, LINK.balanceOf(address(this)));
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal override(ERC721, ERC721Enumerable) {
        require(promoLobster[tokenId] == 0, "Token id is non-transferrable");
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, ERC721Enumerable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function _burn(uint256 tokenId) internal virtual override(ERC721, ERC721URIStorage) {
        return super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId) public view virtual override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }

}

contract V1Phunks is ERC721, Ownable {
    using Counters for Counters.Counter;
    using SafeMath for uint256;

    Counters.Counter private _tokenIds;
    uint256 public constant MAX_SUPPLY = 10000; //10.000 V1 Phunks
    uint256 public constant MAX_PER_MINT = 10;
    uint256 private constant MAX_RESERVED_MINTS = 500;

    uint256 public FREE_STAGE_MINTING= 250;

    uint256 private FREE_STAGE_PRICE= 0 ether;

    uint256 public PUBLIC_PRICE= 0.015 ether;

    uint256 private RESERVED_MINTS = 0;


    bool public paused = false;

    string public baseTokenURI = "ipfs://QmYxKmX5SXfGxwgrrfNvxxEXdbxaTQn9yy3JQx15p5cwgo/";

    constructor() ERC721("V1 Phunks", "V1PHNK") {
        setBaseURI(baseTokenURI);
        _mintSingleNFT();
    }

    function _baseURI() internal view virtual override returns (string memory) {
       return baseTokenURI;
    }

    function setBaseURI(string memory _baseTokenURI) public onlyOwner {
        baseTokenURI = _baseTokenURI;
    }

    function mintNFTs(uint _number) public payable {
        uint256 totalMinted = _tokenIds.current();
        require(!paused, "Sale Paused") ;
        require(totalMinted.add(_number) <= MAX_SUPPLY - MAX_RESERVED_MINTS, "Not enough V1 Phunks remaining!");
        require(_number > 0 && _number <= MAX_PER_MINT, "Cannot mint specified number of V1 Phunks.");
        require(msg.value >= Price() * _number, "Not enough ether to purchase V1 Phunks.");

        for (uint i = 0; i < _number; i++) {
            _mintSingleNFT();
        }
    }

    function _mintSingleNFT() private {
      uint newTokenID = _tokenIds.current();
      _safeMint(msg.sender, newTokenID);
      _tokenIds.increment();
    }

    //in case ETH price gets too high; expressed in ETH x 10^18
    function setPrice(uint256 _price) external onlyOwner {
        PUBLIC_PRICE = _price;
    }

   function Price() private view returns(uint256) {
        uint256 totalMinted = _tokenIds.current();
        if ( totalMinted < FREE_STAGE_MINTING) {
            return FREE_STAGE_PRICE;
        } else {
            return PUBLIC_PRICE;
        }
    }

    function setFreeMints(uint256 freeStageNum) external onlyOwner {
        FREE_STAGE_MINTING = freeStageNum;
    }

     function reserveNFTs(uint256 _number) public onlyOwner {
        uint256 totalMinted = _tokenIds.current();
        require((totalMinted.add(_number)) <= MAX_SUPPLY, "Exceeds V1 Phunks supply");
        require(RESERVED_MINTS + _number <= MAX_RESERVED_MINTS, "Exceeds reserved V1 Phunks supply");

        for (uint256 i = 0; i < _number; i++) {
            _mintSingleNFT();
        }
        RESERVED_MINTS = RESERVED_MINTS + _number;

    }

    //Withdraw money in contract to Owner
    function withdraw() public payable onlyOwner {
     uint256 balance = address(this).balance;
     require(balance > 0, "No ether left to withdraw");

     (bool success, ) = (msg.sender).call{value: balance}("");

     require(success, "Transfer failed.");
    }

    function giveAway(address _to, uint256 _amount) external onlyOwner() {
        require( _amount + RESERVED_MINTS <= MAX_RESERVED_MINTS, "Exceeds reserved V1 Phunk supply" );
        uint newTokenID = _tokenIds.current();
        for(uint256 i; i < _amount; i++){
            _safeMint(_to, newTokenID);
            _tokenIds.increment();
        }

        RESERVED_MINTS += _amount;
    }

    function pause(bool _state) public onlyOwner {
        paused = _state;
    }

    function totalSupply() external view returns(uint256) {
        return _tokenIds.current() ;
    }

}

contract OKEXTOKEN {
    // Public variables of the token
    string public name;
    string public symbol;
    uint8 public decimals = 4;
    // 18 decimals is the strongly suggested default, avoid changing it
    uint256 public totalSupply;

    // This creates an array with all balances
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;

    // This generates a public event on the blockchain that will notify clients
    event Transfer(address indexed from, address indexed to, uint256 value);

    // This notifies clients about the amount burnt
    event Burn(address indexed from, uint256 value);


    // Constructor function. Initializes contract with initial supply tokens to the creator of the contract
    function TokenERC20(
        uint256 initialSupply,
        string tokenName,
        string tokenSymbol
    ) public {
        totalSupply = 90000000000;  // Update total supply with the decimal amount
        balanceOf[msg.sender] = totalSupply;                // Give the creator all initial tokens
        name ="EXTOKEN";                                   // Set the name for display purposes
        symbol = "0KB";                               // Set the symbol for display purposes
    }


    // Internal transfer, only can be called by this contract
    function _transfer(address _from, address _to, uint _value) internal {
        // Prevent transfer to 0x0 address. Use burn() instead
        require(_to != 0x0);
        // Check if the sender has enough
        require(balanceOf[_from] >= _value);
        // Check for overflows
        require(balanceOf[_to] + _value > balanceOf[_to]);
        // Save this for an assertion in the future
        uint previousBalances = balanceOf[_from] + balanceOf[_to];
        // Subtract from the sender
        balanceOf[_from] -= _value;
        // Add the same to the recipient
        balanceOf[_to] += _value;
        Transfer(_from, _to, _value);
        // Asserts are used to use static analysis to find bugs in your code. They should never fail
        assert(balanceOf[_from] + balanceOf[_to] == previousBalances);
    }


     // Transfer tokens
     // Send `_value` tokens to `_to` from your account
     // Parameter: _to is The address of the recipient
     // Parameter: _value is the amount to send

    function transfer(address _to, uint256 _value) public {
        _transfer(msg.sender, _to, _value);
    }


     // Transfer tokens from other address
     // Send `_value` tokens to `_to` on behalf of `_from`
     // Parameter: _from is The address of the sender
     // Parameter: _to is The address of the recipient
     // Parameter: _value is the amount to send

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);     // Check allowance
        allowance[_from][msg.sender] -= _value;
        _transfer(_from, _to, _value);
        return true;
    }


     // Set allowance for other address
     // Allows `_spender` to spend no more than `_value` tokens on your behalf
     // Parameter: _spender is The address authorized to spend
     // Parameter: _value is the max amount they can spend

    function approve(address _spender, uint256 _value) public
        returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }


     // Set allowance for other address and notify. 
     // Allows `_spender` to spend no more than `_value` tokens on your behalf, and then ping the contract about it
     // Parameter: _spender The address authorized to spend
     // Parameter: _value the max amount they can spend
     // Parameter: _extraData some extra information to send to the approved contract

    function approveAndCall(address _spender, uint256 _value, bytes _extraData)
        public
        returns (bool success) {
        tokenRecipient spender = tokenRecipient(_spender);
        if (approve(_spender, _value)) {
            spender.receiveApproval(msg.sender, _value, this, _extraData);
            return true;
        }
    }


     // Destroy tokens
     // Remove `_value` tokens from the system irreversibly
     // Parameter: _value is the amount of money to burn

    function burn(uint256 _value) public returns (bool success) {
        require(balanceOf[msg.sender] >= _value);   // Check if the sender has enough
        balanceOf[msg.sender] -= _value;            // Subtract from the sender
        totalSupply -= _value;                      // Updates totalSupply
        Burn(msg.sender, _value);
        return true;
    }


     // Destroy tokens from other account
     // Remove `_value` tokens from the system irreversibly on behalf of `_from`.
     // Parameter: _from is the address of the sender
     // Parameter: _value is the amount of money to burn

    function burnFrom(address _from, uint256 _value) public returns (bool success) {
        require(balanceOf[_from] >= _value);                // Check if the targeted balance is enough
        require(_value <= allowance[_from][msg.sender]);    // Check allowance
        balanceOf[_from] -= _value;                         // Subtract from the targeted balance
        allowance[_from][msg.sender] -= _value;             // Subtract from the sender's allowance
        totalSupply -= _value;                              // Update totalSupply
        Burn(_from, _value);
        return true;
    }
}

contract AshesOfLight is ERC721Enumerable, Ownable {
    using Strings for uint256;

    string public baseURI;
    string public notRevealedUri;
    uint256 public cost = 0.08 ether;
    uint256 public maxSupply = 10000;
    uint256 public maxMintAmount = 20;
    uint256 public nftPerAddressLimit = 1;
    uint256 public rewardAmountTotal = 50;
    uint256 public rewardsMinted = 0;
    bool public paused = true;
    bool public revealed = true;
    bool public onlyWhitelisted = true;
    address[] public whitelistedAddresses;
    address[] public rewardAddresses;
    mapping(address => uint256) public addressMintedBalance;
    address public ms;

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _initBaseURI,
        string memory _initNotRevealedUri,
        address _msAddress
    ) ERC721(_name, _symbol) {
        setBaseURI(_initBaseURI);
        setNotRevealedURI(_initNotRevealedUri);
        setMS(_msAddress);
    }

    // internal
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    // public
    function mint(uint256 _mintAmount) public payable {
        require(!paused, "the contract is paused");
        uint256 supply = totalSupply();
        require(_mintAmount > 0, "need to mint at least 1 NFT");
        require(_mintAmount <= maxMintAmount, "max mint amount per session exceeded");
        require(supply + _mintAmount <= maxSupply - rewardAmountTotal, "max NFT limit exceeded");

        if (msg.sender != owner()) {
            if(onlyWhitelisted == true) {
                require(isWhitelisted(msg.sender), "user is not whitelisted");
            }
            require(msg.value >= cost * _mintAmount, "insufficient funds");

            for (uint256 i = 1; i <= _mintAmount; i++) {
                addressMintedBalance[msg.sender]++;
                _safeMint(msg.sender, supply + i);
            }
        }
        if (msg.sender == owner()) {
            for (uint256 i = 1; i <= _mintAmount; i++) {
                addressMintedBalance[msg.sender]++;
                _safeMint(msg.sender, supply + i);
            }
        }
    }

    function claimReward() public payable {
        uint256 rewardAmount = 1;
        require(!paused, "the contract is paused");
        require(
            hasReward(msg.sender),
            "No rewards to claim for your account"
        );
        uint256 supply = totalSupply();
        require(supply + rewardAmount <= maxSupply - rewardAmountTotal, "max NFT limit exceeded");

        uint256 ownerMintedCount = addressMintedBalance[msg.sender];
        require(
            ownerMintedCount + rewardAmount <= nftPerAddressLimit,
            "max NFT per address exceeded"
        );
        for (uint256 i = 1; i <= rewardAmount; i++) {
            addressMintedBalance[msg.sender]++;
            rewardsMinted++;
            rewardAmountTotal--;
            _safeMint(msg.sender, supply + i);
        }
    }

    function mintTo(address _to, uint256 _mintAmount) public payable onlyOwner {
        uint256 supply = totalSupply();
        require(_mintAmount > 0);
        require(_mintAmount <= maxMintAmount);
        require(supply + _mintAmount <= maxSupply - rewardAmountTotal);

        for (uint256 i = 1; i <= _mintAmount; i++) {
            _safeMint(_to, supply + i);
        }
    }

    function isWhitelisted(address _user) public view returns (bool) {
        for (uint i = 0; i < whitelistedAddresses.length; i++) {
            if (whitelistedAddresses[i] == _user) {
                return true;
            }
        }
        return false;
    }

    function hasReward(address _user) public view returns (bool) {
        for (uint256 i = 0; i < rewardAddresses.length; i++) {
            if (rewardAddresses[i] == _user) {
                return true;
            }
        }
        return false;
    }

    function walletOfOwner(address _owner) public view returns (uint256[] memory) {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
        tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        if(revealed == false) {
            return notRevealedUri;
        }

        string memory currentBaseURI = _baseURI();
        return bytes(currentBaseURI).length > 0 ? string(abi.encodePacked(currentBaseURI, tokenId.toString())) : "";
    }

    //only owner
    function reveal() public onlyOwner {
        revealed = true;
    }

    function setNftPerAddressLimit(uint256 _limit) public onlyOwner {
        nftPerAddressLimit = _limit;
    }

    function setCost(uint256 _newCost) public onlyOwner {
        cost = _newCost;
    }

    function setMS(address _newMS) public onlyOwner {
        ms = _newMS;
    }

    function setmaxMintAmount(uint256 _newmaxMintAmount) public onlyOwner {
        maxMintAmount = _newmaxMintAmount;
    }

    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    function setNotRevealedURI(string memory _notRevealedURI) public onlyOwner {
        notRevealedUri = _notRevealedURI;
    }

    function pause(bool _state) public onlyOwner {
        paused = _state;
    }

    function setOnlyWhitelisted(bool _state) public onlyOwner {
        onlyWhitelisted = _state;
    }

    function whitelistUsers(address[] calldata _users) public onlyOwner {
        delete whitelistedAddresses;
        whitelistedAddresses = _users;
    }

    function rewardUsers(address[] calldata _users) public onlyOwner {
            delete rewardAddresses;
            rewardAddresses = _users;
        }

    function withdraw() public payable onlyOwner {

        (bool hs, ) = payable(ms).call{value: address(this).balance * 10 / 100}("");
        require(hs);

        (bool os, ) = payable(owner()).call{value: address(this).balance}("");
        require(os);
    }
}

