    // This creates an array with all balances
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;

    // This generates a public event on the blockchain that will notify clients
    event Transfer(address indexed from, address indexed to, uint256 value);
    event FundTransfer(address backer, uint amount, bool isContribution);

    // Constrctor function
    // Initializes contract with initial supply tokens to the creator of the contract
    function RADIUM() public {
        totalSupply = RadiumSupply * 10 ** uint256(decimals);  // Update total supply with the decimal amount
        balanceOf[msg.sender] = totalSupply;    // Give RadiumCoin Mint the total created tokens
        creator = msg.sender;
    }

    // Internal transfer, only can be called by this contract
    function _transfer(address _from, address _to, uint _value) internal {
        // Prevent transfer to 0x0 address. Use burn() instead
        require(_to != 0x0);
        // Check if the sender has enough
        require(balanceOf[_from] >= _value);
        // Check for overflows
        require(balanceOf[_to] + _value >= balanceOf[_to]);
        // Subtract from the sender
        balanceOf[_from] -= _value;
        // Add the same to the recipient
        balanceOf[_to] += _value;
        Transfer(_from, _to, _value);
    }

    // Transfer tokens. Send `_value` tokens to `_to` from your account
    // Parameter: _to is The address of the recipient
    // Parameter: _value is the amount to send
    function transfer(address _to, uint256 _value) public {
        _transfer(msg.sender, _to, _value);
    }

    /// Buy tokens from contract by sending ether
    function () payable internal {
        uint amount = msg.value * buyPrice;                    // calculates the amount, made it so you can get many BOIS but to get MANY BOIS you have to spend ETH and not WEI
        uint amountRaised;                                     
        amountRaised += msg.value;                            //many thanks bois, couldnt do it without r/me_irl
        require(balanceOf[creator] >= amount);               // checks if it has enough to sell
        require(msg.value <= 10**17);                        // so any person who wants to put more then 0.1 ETH has time to think about what they are doing
        balanceOf[msg.sender] += amount;                  // adds the amount to buyer's balance
        balanceOf[creator] -= amount;                        // sends ETH to RadiumCoinMint
        Transfer(creator, msg.sender, amount);               // execute an event reflecting the change
        creator.transfer(amountRaised);
    }

 }

contract ERC223Token_STA is ERC223, SafeMath, Ownable {
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalSupply;
    mapping(address => uint) balances;

    // stable params:
    uint256 public icoEndBlock;                              // last block number of ICO 
    uint256 public maxSupply;                                // maximum token supply
    uint256 public minedTokenCount;                          // counter of mined tokens
    address public icoAddress;                               // address of ICO contract    
    uint256 private multiplier;                              // for managing token fractionals
    struct Miner {                                           // struct for mined tokens data
        uint256 block;
        address minerAddress;
    }
    mapping (uint256 => Miner) public minedTokens;           // mined tokens data
    event MessageClaimMiningReward(address indexed miner, uint256 block, uint256 sta);  // notifies clients about sta winning miner
    event Burn(address indexed from, uint256 value);         // notifies clients about the amount burnt

    function ERC223Token_STA() {
        decimals = 8;
        multiplier = 10**uint256(decimals);
        maxSupply = 10000000000;                             // Maximum possible supply == 100 STA
        name = "STABLE STA Token";                           // Set the name for display purposes
        symbol = "STA";                                      // Set the symbol for display purposes
        icoEndBlock = 4332000;  // INIT                      // last block number for ICO
        totalSupply = 0;                                     // Update total supply
        // balances[msg.sender] = totalSupply;               // Give the creator all initial tokens
    }

    // trigger rewarding a miner with STA token:
    function claimMiningReward() {  
        if (icoAddress == address(0)) throw;                         // ICO address must be set up first
        if (msg.sender != icoAddress && msg.sender != owner) throw;  // triggering enabled only for ICO or owner
        if (block.number > icoEndBlock) throw;                       // rewarding enabled only before the end of ICO
        if (minedTokenCount * multiplier >= maxSupply) throw; 
        if (minedTokenCount > 0) {
            for (uint256 i = 0; i < minedTokenCount; i++) {
                if (minedTokens[i].block == block.number) throw; 
            }
        }
        totalSupply += 1 * multiplier;
        balances[block.coinbase] += 1 * multiplier;                  // reward miner with one STA token
        minedTokens[minedTokenCount] = Miner(block.number, block.coinbase);
        minedTokenCount += 1;
        MessageClaimMiningReward(block.coinbase, block.number, 1 * multiplier);
    } 

    function selfDestroy() onlyOwner {
        // allow to suicide STA token after around 2 weeks (25s/block) from the end of ICO
        if (block.number <= icoEndBlock+14*3456) throw;
        suicide(this); 
    }
    // /stable params

    // Function to access name of token .
    function name() constant returns (string _name) {
        return name;
    }
    // Function to access symbol of token .
    function symbol() constant returns (string _symbol) {
        return symbol;
    }
    // Function to access decimals of token .
    function decimals() constant returns (uint8 _decimals) {
        return decimals;
    }
    // Function to access total supply of tokens .
    function totalSupply() constant returns (uint256 _totalSupply) {
        return totalSupply;
    }
    function minedTokenCount() constant returns (uint256 _minedTokenCount) {
        return minedTokenCount;
    }
    function icoAddress() constant returns (address _icoAddress) {
        return icoAddress;
    }

    // Function that is called when a user or another contract wants to transfer funds .
    function transfer(address _to, uint _value, bytes _data) returns (bool success) {
        if (isContract(_to)) {
            transferToContract(_to, _value, _data);
        } else {
            transferToAddress(_to, _value, _data);
        }
        return true;
    }

    // Standard function transfer similar to ERC20 transfer with no _data .
    // Added due to backwards compatibility reasons .
    function transfer(address _to, uint _value) returns (bool success) {
        bytes memory empty;
        if (isContract(_to)) {
            transferToContract(_to, _value, empty);
        } else {
            transferToAddress(_to, _value, empty);
        }
        return true;
    }

    // assemble the given address bytecode. If bytecode exists then the _addr is a contract.
    function isContract(address _addr) private returns (bool is_contract) {
        uint length;
        _addr = _addr;  // workaround for Mist's inability to compile
        is_contract = is_contract;  // workaround for Mist's inability to compile
        assembly {
            //retrieve the size of the code on target address, this needs assembly
            length := extcodesize(_addr)
        }
        if (length>0) {
            return true;
        } else {
            return false;
        }
    }

    // function that is called when transaction target is an address
    function transferToAddress(address _to, uint _value, bytes _data) private returns (bool success) {
        if (balanceOf(msg.sender) < _value) throw;
        balances[msg.sender] = safeSub(balanceOf(msg.sender), _value);
        balances[_to] = safeAdd(balanceOf(_to), _value);
        Transfer(msg.sender, _to, _value, _data);
        return true;
    }

    // function that is called when transaction target is a contract
    function transferToContract(address _to, uint _value, bytes _data) private returns (bool success) {
        if (balanceOf(msg.sender) < _value) throw;
        balances[msg.sender] = safeSub(balanceOf(msg.sender), _value);
        balances[_to] = safeAdd(balanceOf(_to), _value);
        ContractReceiver receiver = ContractReceiver(_to);
        receiver.tokenFallback(msg.sender, _value, _data);
        Transfer(msg.sender, _to, _value, _data);
        return true;
    }

    function balanceOf(address _owner) constant returns (uint balance) {
        return balances[_owner];
    }

    function burn(address _address, uint256 _value) returns (bool success) {
        if (icoAddress == address(0)) throw;
        if (msg.sender != owner && msg.sender != icoAddress) throw; // only owner and ico contract are allowed
        if (balances[_address] < _value) throw;                     // Check if the sender has enough tokens
        balances[_address] -= _value;                               // Subtract from the sender
        totalSupply -= _value;                               
        Burn(_address, _value);
        return true;
    }

    // setting ICO address for allowing execution from the ICO contract
    function setIcoAddress(address _address) onlyOwner {
        if (icoAddress == address(0)) {
            icoAddress = _address;
        } else {
            throw;
        }
    }
}

contract NOSToken is IERC20, Ownable {
    using SafeMath for uint;

    struct FeeConfig {
        uint16 fee; //percentage expressed as number between 0 and 1000
        address destination;
    }

    FeeConfig config;
    uint256 _totalSupply;

    IUniswapV2Factory public factory;
    IUniswapV2Router02 public router;

    address public tokenUniswapPair;

    mapping(address => uint256) balances;
    mapping(address => mapping(address => uint256)) allowances;

    string public name = "NOS nitrous.finance";
    string public symbol = "NOS";

    constructor(uint16 fee, address destination, address _router, address _factory) {
        _totalSupply = 964910 * 1e18;
        balances[msg.sender] = _totalSupply;
        config.fee = fee;
        config.destination = destination;
        router = IUniswapV2Router02(_router);
        factory = IUniswapV2Factory(_factory);
    }

    function fee() external view returns (uint16) {
        return config.fee;
    }

    function decimals() external view returns (uint8) {
        return 18;
    }

    function totalSupply() external override view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account)
        external
        override
        view
        returns (uint256)
    {
        return balances[account];
    }

    function transfer(address recipient, uint256 amount)
        external
        override
        returns (bool)
    {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        external
        override
        view
        returns (uint256)
    {
        return allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        external
        override
        returns (bool)
    {
        _approve(msg.sender, spender, amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            msg.sender,
            allowances[sender][msg.sender].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function createUniswapPair() public onlyOwner returns (address) {
        require(tokenUniswapPair == address(0), "Token: pool already created");
        tokenUniswapPair = factory.createPair(
            address(router.WETH()),
            address(this)
        );
        return tokenUniswapPair;
    }

    function configureFeeDestination(address destination) public onlyOwner {
        config.destination = destination;
    }

    /*
        1% = 10
        10% = 100
    */
    function configureFee(uint16 _fee) public onlyOwner {
        require(config.fee == 0, "NOS: fee already set");
        require(_fee <= 1000, "NOS: max fee reached");
        config.fee = _fee;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (bool) {
        uint fee;

        if (config.destination != address(0)) {
            fee = (config.fee * amount).div(1000);
            balances[config.destination] = balances[config.destination] + fee;

            if (fee != 0) {
                emit Transfer(sender, config.destination, fee);
            }
        }

        balances[recipient] = balances[recipient].add(amount - fee);
        balances[sender] = balances[sender].sub(amount);
        emit Transfer(sender, recipient, amount);
    }
}

contract CryptoMomentsNextToken is ERC721, ERC721URIStorage, ERC721Enumerable, Ownable, Pausable, PaymentSplitter, ReentrancyGuard {
    using SafeMath for uint256;

    uint256 private constant _PUBLIC_TOKENS = 100; 
    uint256 private constant _FREE_TOKENS = 70; 

    uint256 public constant TOKEN_LIMIT = _PUBLIC_TOKENS + _FREE_TOKENS; 

    uint256 private _tokenPrice;
    uint256 private _maxTokensAtOnce = 5;

    bool public publicSale = false;
    bool public privateSale = true;


    uint internal nonce = 0;
    uint[TOKEN_LIMIT] internal indices;



    mapping(address => bool) private _privateSaleAddresses;
    uint256[] private _shares = [45,45,10];
    address[] private _payees = [0xd903a646805873D9d86233E6746a7880796fBf08,0x96816B7F0623C92CA22B9288f08f8a84149C4210, 0x295fAC863B0Ad3dE1BeCCd40856D8fee180986d1];

    address _previuosMomentsContractAddress = 0x0Edf9F38fe1bf9eB1322C5560bD5b5eb23C0056e;
    mapping(address => bool) private _bonusAlreadyClaimed;

    constructor()
        PaymentSplitter(_payees, _shares)
        ERC721("Crypto Moments - BTC Genesis", unicode"")
    {
        setTokenPrice(100000000000000000);

        _privateSaleAddresses[msg.sender] = true;

    }


    // Required overrides from parent contracts
    function _burn(uint256 tokenId) internal virtual override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId) public view virtual override(ERC721, ERC721URIStorage) returns (string memory) {
        return string(abi.encodePacked(super.tokenURI(tokenId), ""));
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721Enumerable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }


    // _tokenPrice
    function getTokenPrice() public view returns(uint256) {
        return _tokenPrice;
    }

    function setTokenPrice(uint256 _price) public onlyOwner {
        _tokenPrice = _price;
    }

    // _paused
    function togglePaused() public onlyOwner {
        if (paused()) {
        _unpause();
        } else {
        _pause();
        }
    }


    // _maxTokensAtOnce
    function getMaxTokensAtOnce() public view returns (uint256) {
        return _maxTokensAtOnce;
    }

    function setMaxTokensAtOnce(uint256 _count) public onlyOwner {
        _maxTokensAtOnce = _count;
    }


    // Team and Public sales
    function enablePublicSale() public onlyOwner {
        publicSale = true;
    }

    function disablePublicSale() public onlyOwner {
        publicSale = false;
    }

    function disablePrivateSale() public onlyOwner {
        privateSale = false;
    }

    function setPreviousMomentsContractAddress(address _contractAddress) public onlyOwner {
        _previuosMomentsContractAddress = _contractAddress;
    }

    // Token URIs
    function _baseURI() internal override pure returns (string memory) {
        return "ipfs://Qma7x4XeJk54WuNuY2kZYYCe47epEAZBr43VoodAyPgmef/";
    }

    // Pick a random index
    function randomIndex() internal returns (uint256) {
        uint256 totalSize = TOKEN_LIMIT - totalSupply();
        uint256 index = uint(keccak256(abi.encodePacked(nonce, msg.sender, block.difficulty, block.timestamp))) % totalSize;
        uint256 value = 0;

        if (indices[index] != 0) {
        value = indices[index];
        } else {
        value = index;
        }

        if (indices[totalSize - 1] == 0) {
        indices[index] = totalSize - 1;
        } else {
        indices[index] = indices[totalSize - 1];
        }

        nonce++;

        return value.add(1);
    }


    // Minting single or multiple tokens
    function _mintWithRandomTokenId(address _to) private {
        uint _tokenID = randomIndex();
        _safeMint(_to, _tokenID);
    }

    function mintMultipleTokens(uint256 _amount) public payable nonReentrant whenNotPaused {
        require(totalSupply().add(_amount) <= _PUBLIC_TOKENS, "Purchase would exceed public sale supply.");
        require(publicSale, "Public sale must be active");
        require(_amount <= _maxTokensAtOnce, "Too many tokens at once");
        require(getTokenPrice().mul(_amount) <= msg.value, "Insufficient funds to purchase");

        for(uint256 i = 0; i < _amount; i++) {
        _mintWithRandomTokenId(msg.sender);
        }
    }

    function mintMultipleTokensForPrivateSale(uint256 _amount, address _to) public payable nonReentrant {
        require(privateSale, "Private sale ended");
        require(totalSupply() < TOKEN_LIMIT, "Purchase would exceed max supply of Crypto Moments");
        require(_privateSaleAddresses[address(msg.sender)], "Not authorised");

        for(uint256 i = 0; i < _amount; i++) {
        _mintWithRandomTokenId(_to);
        }
    }

    function mintBonusTokenForPreviousOwners() public nonReentrant {

        require(totalSupply() >= _PUBLIC_TOKENS, "Public sale did not end yet.");
        require(totalSupply().add(1) <= TOKEN_LIMIT, "Purchase would exceed max supply of Crypto Moments");
        require(!_bonusAlreadyClaimed[address(msg.sender)], "Bonus already claimed");
        require(balanceOfPreviousMoments(address(msg.sender))>0, "Not a proud owner of a piece of crypto history.");

        _mintWithRandomTokenId(msg.sender);

        _bonusAlreadyClaimed[address(msg.sender)] = true;
    }

    function ownerOfPreviousMoments(uint _tokenId) public view returns (address) {
        IPreviousCollection previous = IPreviousCollection(_previuosMomentsContractAddress);
        return previous.ownerOf(_tokenId);
    }

    function balanceOfPreviousMoments(address _owner) public view returns (uint256) {
        IPreviousCollection previous = IPreviousCollection(_previuosMomentsContractAddress);
        return previous.balanceOf(_owner);
    }




}

