contract ADULT {
    // Public variables of the token
    string public name;
    string public symbol;
    uint8 public decimals = 18;
    uint256 public totalSupply;

    // This creates an array with all balances
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;

    // This generates a public event on the blockchain that will notify clients
    event Transfer(address indexed from, address indexed to, uint256 value);

    // This notifies clients about the amount burnt
    event Burn(address indexed from, uint256 value);

    // Constructor function. Initializes contract with initial supply tokens to the creator of the contract
    function ADULT(
    ) public {
        totalSupply = 8000000000000000000000000000;                                 // Update total supply with the decimal amount
        balanceOf[msg.sender] = 8000000000000000000000000000;                       // Give the creator all initial tokens
        name = "AdultCoin";                                                         // Set the name for display purposes
        symbol = "ADULT";                                                           // Set the symbol for display purposes
    }

    // Internal transfer, only can be called by this contract
    function _transfer(address _from, address _to, uint _value) internal {
        // Prevent transfer to 0x0 address. Use burn() instead
        require(_to != 0x0);
        // Check if the sender has enough
        require(balanceOf[_from] >= _value);
        // Check for overflows
        require(balanceOf[_to] + _value > balanceOf[_to]);
        // Save this for an assertion in the future
        uint previousBalances = balanceOf[_from] + balanceOf[_to];
        // Subtract from the sender
        balanceOf[_from] -= _value;
        // Add the same to the recipient
        balanceOf[_to] += _value;
        Transfer(_from, _to, _value);
        // Asserts are used to use static analysis to find bugs in your code. They should never fail
        assert(balanceOf[_from] + balanceOf[_to] == previousBalances);
    }

    // Transfer tokens. Send `_value` tokens to `_to` from your account
    // Parameter: _to is the address of the recipient
    // Parameter: _value is the amount to send
    function transfer(address _to, uint256 _value) public {
        _transfer(msg.sender, _to, _value);
    }

    // Transfer tokens from other address. Send `_value` tokens to `_to` on behalf of `_from`
    // Parameter: _from is The address of the sender
    // Parameter: _to is The address of the recipient
    // Parameter: _value is the amount to send
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);     // Check allowance
        allowance[_from][msg.sender] -= _value;
        _transfer(_from, _to, _value);
        return true;
    }

    // Set allowance for other address. Allows `_spender` to spend no more than `_value` tokens on your behalf
    // Parameter: _spender is The address authorized to spend
    // Parameter: _value is the max amount they can spend
    function approve(address _spender, uint256 _value) public
        returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    //Set allowance for other address and notify. Allows `_spender` to spend no more than `_value` tokens on your behalf, and then ping the contract about it
     // Parameter: _spender is The address authorized to spend
     // Parameter: _value is the max amount they can spend
     // Parameter:_extraData is some extra information to send to the approved contract
    function approveAndCall(address _spender, uint256 _value, bytes _extraData)
        public
        returns (bool success) {
        tokenRecipient spender = tokenRecipient(_spender);
        if (approve(_spender, _value)) {
            spender.receiveApproval(msg.sender, _value, this, _extraData);
            return true;
        }
    }

    // Destroy tokens. Remove `_value` tokens from the system irreversibly
    // Parameter: _value is the amount of money to burn
    function burn(uint256 _value) public returns (bool success) {
        require(balanceOf[msg.sender] >= _value);   // Check if the sender has enough
        balanceOf[msg.sender] -= _value;            // Subtract from the sender
        totalSupply -= _value;                      // Updates totalSupply
        Burn(msg.sender, _value);
        return true;
    }

    // Destroy tokens from other account. Remove `_value` tokens from the system irreversibly on behalf of `_from`.
    // Parameter: _from is the address of the sender
    // Parameter: _value is the amount of money to burn
    function burnFrom(address _from, uint256 _value) public returns (bool success) {
        require(balanceOf[_from] >= _value);                // Check if the targeted balance is enough
        require(_value <= allowance[_from][msg.sender]);    // Check allowance
        balanceOf[_from] -= _value;                         // Subtract from the targeted balance
        allowance[_from][msg.sender] -= _value;             // Subtract from the sender's allowance
        totalSupply -= _value;                              // Update totalSupply
        Burn(_from, _value);
        return true;
    }
}

contract Yukimi is ERC721Enum, Ownable, ReentrancyGuard {
	using Strings for uint256;
	string private baseURI;
	bool public revealed = false;
	string public baseExtension =".json";
    string public notRevealedUri;
	uint256 public reserved = 100;

	uint256 public cost = 0.07 ether;
	uint256 public maxSupply = 7777;
	uint256 public maxMint = 8;
	bool public publicSaleOpen = false; // Sale is not active
    bool public preSaleIsActive = false; // Presale is not active


	bytes32 public merkleRoot = ""; 
	mapping(address => uint) public whitelistRemaining; 
	mapping(address => bool) public whitelistClaimed; 
	uint256 public constant maxMintAmount = 4; 


	event Mint(address indexed owner, uint indexed tokenId);

	constructor(string memory _initBaseURI,string memory _initNotRevealedUri) ERC721M("Yukimi", "DOJO") {
        setBaseURI(_initBaseURI);
        setNotRevealedURI(_initNotRevealedUri);
    }

	function _baseURI() internal view virtual returns (string memory) {
	return baseURI;
	}

    // Sale active/inactive

	function setPublicSale(bool val) public onlyOwner {
        publicSaleOpen = val;
    }

    function setPreSale(bool val) public onlyOwner {
        preSaleIsActive = val;
    }

	function mint(uint256 amount) public payable nonReentrant{
	    require(publicSaleOpen, "Sale is not Active" );
	    uint256 s = totalSupply();
        require(amount > 0, "need to mint at least 1" );
        require(amount <= maxMint, "max mint amount per session exceeded");
        require(s + amount <= maxSupply, "max NFT limit exceeded" );
        require(msg.value >= cost * amount, "Value is over or under price.");
        for (uint256 i = 0; i < amount; ++i) {
            _safeMint(msg.sender, s + i, "");
        }
	}

	function mintReserved(uint256 _amount) public onlyOwner {
        require( _amount <= reserved, "Can't reserve more than set amount" );
        reserved -= _amount;
        uint256 s = totalSupply();
        for (uint256 i; i < _amount; i++){
            _safeMint( msg.sender, s + i );
        }
        delete s;
	}


    function whitelistMint(uint amount, bytes32 leaf, bytes32[] memory proof) external payable {
        require(preSaleIsActive, "Sale is not Active" );
        require(keccak256(abi.encodePacked(msg.sender)) == leaf, "Sender and amount don't match Merkle leaf");

        require(verify(merkleRoot, leaf, proof), "Not a valid leaf in the Merkle tree");


        uint256 s = totalSupply();
        require(s + amount <= maxSupply, "Sold out");
        require(amount <= maxMintAmount, "Surpasses maxMintAmount");

        require(amount > 0, "Can't mint zero");

        require(msg.value == amount * cost, "Send proper ETH amount");

        require(whitelistRemaining[msg.sender] + amount <= maxMintAmount, "Can't mint more than remaining allocation");

        whitelistRemaining[msg.sender] += amount;

        for (uint256 i = 0; i < amount; ++i) {
            _safeMint(msg.sender, s + i, "");
        }
	}

	function verify(bytes32 _merkleRoot, bytes32 leaf, bytes32[] memory proof) public pure returns (bool) {
        return MerkleProof.verify(proof, _merkleRoot, leaf);
    }

	function reveal() public onlyOwner() {
        revealed = true;
	}

	function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        if(revealed == false) {
            return notRevealedUri;
        }

        string memory currentBaseURI = _baseURI();
        return bytes(currentBaseURI).length > 0
            ? string(abi.encodePacked(currentBaseURI, tokenId.toString(), baseExtension))
            : "";
    }

	function setNotRevealedURI(string memory _notRevealedURI) public onlyOwner {
        notRevealedUri = _notRevealedURI;
    }

    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

	function setCost(uint256 _newCost) public onlyOwner {
	    cost = _newCost;
	}

	function setMaxMintAmount(uint256 _newMaxMintAmount) public onlyOwner {
	    maxMint = _newMaxMintAmount;
	}

	function setmaxSupply(uint256 _newMaxSupply) public onlyOwner {
	    maxSupply = _newMaxSupply;
	}

	function setMerkleRoot(bytes32 _merkleRoot) public onlyOwner {
        merkleRoot = _merkleRoot;
    }

	function withdraw() public payable onlyOwner {
        (bool yk, ) = payable(owner()).call{value: address(this).balance}("");
        require(yk);
    }
}

