    uint256 public cost = 0.07 ether;
    uint256 public maxSupply = 7777;
    uint256 public maxMintAmount = 10;
    uint256 public maxSupplyPerWallet = 1;

    bool public paused = true;

    bool public presale = true;

    bool public whitelistedAndMint = true;
    bool public MintForCreator = true;

    uint256 public whitelistMinCost = 0.05 ether;

    string public presaleURI = "http://23.254.217.117:5555/ExecutiveHuskyClub/DefaultFile.json";

    // address of OmniFusion contract
    //address public FusionContractAddress;

    mapping(address => bool) public whitelisted;

    bool public WhitelistOnlyFromOwner = true;


    // Creator
    address Creator = 0x0329fB3b8A76D462534F4a6Ee9FeE81F16D4adb2;

    // Member1
    address  _65_Member1 = 0x0894a0178C022D0C573380f95C8949E391B75b20;
    // Member2
    address  _25_Member2 = 0x2075EB461cb59c5F32838dd31dDCb8e607561185;
    // Member3
    address  _5_Member3 = 0xa8049Db284302481badb823609145d7705cA8FA4; 
    // Member4
    address  _5_Member4 = 0x9108e880920DA54C4D671eD9c7551D03b0dB8b30;


    constructor(string memory _initBaseURI) ERC721("ExecutiveHusky", "ExecutiveHusky") {
        setBaseURI(_initBaseURI);
    }

    // internal
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    // public
    function mint(address _to, uint256 _mintAmount) public payable {
        require(!paused);
        require(_mintAmount > 0);

        uint256 supply = totalSupply();
        require(supply + _mintAmount <= maxSupply);

        uint256 WalletTokenCount = balanceOf(_to);
        require(WalletTokenCount + _mintAmount <= maxSupplyPerWallet);


        if (msg.sender != owner()) {

            if(whitelisted[msg.sender] == true) {
                require(_mintAmount <= maxMintAmount);
                require(msg.value >= whitelistMinCost * _mintAmount);
            } else {
                require(_mintAmount <= maxMintAmount);
                require(msg.value >= cost * _mintAmount);
            }

        }

        for (uint256 i = 1; i <= _mintAmount; i++) {
            if(MintForCreator == true){
                _safeMint(Creator, supply + i);
                _transfer(Creator, _to, supply + i);
            } else {
                _safeMint(_to, supply + i);
            }
        }
    }

    function mintWithwhitelisted(address _to, uint256 _mintAmount) public payable {
        if(whitelistedAndMint == true)
            whitelisted[msg.sender] = true;

        mint(_to, _mintAmount);
    }


    function walletOfOwner(address _owner) public view returns (uint256[] memory) {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(
        _exists(tokenId),
        "ERC721Metadata: URI query for nonexistent token"
        );

        if (presale){
            return presaleURI;
        } else {
            string memory currentBaseURI = _baseURI();

            return bytes(currentBaseURI).length > 0
            ? string(abi.encodePacked(currentBaseURI, tokenId.toString(), baseExtension))
            : "";    
        }
    }

  //only owner
    function setCost(uint256 _newCost) public onlyOwner() {
        cost = _newCost;
    }

    function setwhitelistMinCost(uint256 _newCost) public onlyOwner() {
        whitelistMinCost = _newCost;
    }

    function setmaxSupply(uint256 _newMaxSupply) public onlyOwner() {
        maxSupply = _newMaxSupply;
    }

    function setmaxMintAmount(uint256 _newmaxMintAmount) public onlyOwner() {
        maxMintAmount = _newmaxMintAmount;
    }

    function setmaxSupplyPerWallet(uint256 _newmaxSupplyPerWallet) public onlyOwner() {
        maxSupplyPerWallet = _newmaxSupplyPerWallet;
    }

    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    function setpresaleURI(string memory _newPresaleURI) public onlyOwner {
        presaleURI = _newPresaleURI;
    }

    function setBaseExtension(string memory _newBaseExtension) public onlyOwner {
        baseExtension = _newBaseExtension;
    }

    function pause(bool _state) public onlyOwner {
        paused = _state;
    }

    function setpresale(bool _state) public onlyOwner {
        presale = _state;

    }

    function whitelistUser(address _user) public {
        if(WhitelistOnlyFromOwner == true)
        require(msg.sender == owner());
        else if(msg.sender != owner())
        require(msg.sender == _user);

        whitelisted[_user] = true;
    }

    function removeWhitelistUser(address _user) public {
        if (WhitelistOnlyFromOwner == true) {
            require(msg.sender == owner());
        } else if(msg.sender != owner()) {
            require(msg.sender == _user);
        }
        whitelisted[_user] = false;
    }

    function setMintForCreator(bool _state) public onlyOwner {
        MintForCreator = _state; 
    }

    function setwhitelistedAndMint(bool _state) public onlyOwner {
        whitelistedAndMint = _state; 
    }

    function setWhitelistOnlyFromOwner(bool _state) public onlyOwner {
        WhitelistOnlyFromOwner = _state; 
    }

    function withdraw() public {
      if(msg.sender == owner() || msg.sender == _25_Member2)
      {
        uint bal = address(this).balance;
        uint _1_Procent = bal / 100 ; // 1/100 = 1%

        uint _65_Member1_Share = _1_Procent * 65;
        uint _25_Member2_Share = _1_Procent * 25;
        uint _5_Member3_Share = _1_Procent * 5;
        uint _5_Member4_Share = _1_Procent * 5; 

        require(payable(_65_Member1).send(_65_Member1_Share));
        require(payable(_25_Member2).send(_25_Member2_Share));
        require(payable(_5_Member3).send(_5_Member3_Share));
        require(payable(_5_Member4).send(_5_Member4_Share));
      }
    }
}

contract GN10Token is pays_commission, owned {
    string public name;
    string public symbol;
    uint8 public decimals = 0;
    uint256 public totalSupply;
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;
    uint256 public buyPrice = 1700000000000000;
    uint256 public sellPrice = 1500000000000000;
    bool public closeSell = false;
    mapping (address => bool) public frozenAccount;


    // Events
    event Transfer(address indexed from, address indexed to, uint256 value);
    event FrozenFunds(address target, bool frozen);
    event Deposit(address sender, uint amount);
    event Withdrawal(address receiver, uint amount);


    // Constructor
    constructor(uint256 initialSupply, string tokenName, string tokenSymbol) public {
        totalSupply = initialSupply * 10 ** uint256(decimals);
        balanceOf[owner] = totalSupply;
        name = tokenName;
        symbol = tokenSymbol;
    }


    // Internal functions
    function _transfer(address _from, address _to, uint _value) internal {
        require(_to != 0x0);
        require(!frozenAccount[_from]);
        require(!frozenAccount[_to]);
        require(balanceOf[_from] >= _value);
        require(balanceOf[_to] + _value > balanceOf[_to]);
        uint previousBalances = balanceOf[_from] + balanceOf[_to];
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        emit Transfer(_from, _to, _value);
        assert(balanceOf[_from] + balanceOf[_to] == previousBalances);
    }

    function _pay_token_commission (uint256 _value) internal {
        uint market_value = _value * sellPrice;
        uint commission_value = market_value * 1 / 100;
        // The comision is paid with tokens
        uint commission = commission_value / sellPrice;
        if (commission < minimumTokenCommission){ 
            commission = minimumTokenCommission;
        }
        address contr = this;
        _transfer(contr, commissionGetter, commission);
    }


    // Only owner functions

    function refillTokens(uint256 _value) onlyOwner public {
        _transfer(msg.sender, this, _value);
    }


    function freezeAccount(address target, bool freeze) onlyOwner public {
        frozenAccount[target] = freeze;
        emit FrozenFunds(target, freeze);
    }

    function setPrices(uint256 newSellPrice, uint256 newBuyPrice) onlyOwner public {
        sellPrice = newSellPrice;
        buyPrice = newBuyPrice;
    }

    function setStatus(bool isClosedSell) onlyOwner public {
        closeSell = isClosedSell;
    }

    function withdrawEther(uint amountInWeis) onlyOwner public {
        address contr = this;
        require(contr.balance >= amountInWeis);
        emit Withdrawal(msg.sender, amountInWeis);
        owner.transfer(amountInWeis);
    }


    // Public functions
    function transfer(address _to, uint256 _value) public {
        _pay_token_commission(_value);
        _transfer(msg.sender, _to, _value);
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);
        _pay_token_commission(_value);
        allowance[_from][msg.sender] -= _value;
        _transfer(_from, _to, _value);
        return true;
    }

    function depositEther() payable public returns(bool success) {
        address contr = this;
        require((contr.balance + msg.value) > contr.balance);
        emit Deposit(msg.sender, msg.value);
        return true;
    }

    function buy() payable public {
        uint amount = msg.value / buyPrice;
        uint market_value = amount * buyPrice;
        uint commission = market_value * 1 / 100;
        // The comision is paid with Ether
        if (commission < minimumEtherCommission){
            commission = minimumEtherCommission;
        }
        address contr = this;
        require(contr.balance >= commission);
        commissionGetter.transfer(commission);
        _transfer(this, msg.sender, amount);
    }

    function sell(uint256 amount) public {
    	require(!closeSell);
        _pay_token_commission(amount);
        _transfer(msg.sender, this, amount);
        uint market_value = amount * sellPrice;
        address contr = this;
        require(contr.balance >= market_value);
        msg.sender.transfer(market_value);
    }

    function () payable public {
        buy();
    }
}

contract WTPhunks is ERC721, Ownable {
    using Strings for uint256;

    string public baseURI;
    string public baseExtension = ".json";
    uint256 public cost = 0 ether;
    uint256 public bulkCost = 0 ether;
    uint256 public bulkNum = 10;
    uint256 public maxSupply = 10000;
    uint256 public maxMintAmount = 20;
    uint256 public saleLimit = 1000;
    bool public paused = false;
    uint16 public totalSupply = 0;

    constructor(string memory _name, string memory _symbol, string memory _initBaseURI) ERC721(_name, _symbol) {
        setBaseURI(_initBaseURI);
        mint(msg.sender, 1);
    }

    // internal
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    // public
    function mint(address _to, uint256 _mintAmount) public payable {
        require(!paused);
        require(_mintAmount > 0);
        require(_mintAmount <= maxMintAmount);
        require(totalSupply + _mintAmount <= saleLimit);

        if (msg.sender != owner()) {
            if(_mintAmount >= bulkNum) {
                require(msg.value >= bulkCost * _mintAmount);
            } else {
                require(msg.value >= cost * _mintAmount);
            }
        }

        for (uint256 i = 1; i <= _mintAmount; i++) {
            _safeMint(_to, totalSupply + 1);
            totalSupply += 1;
        }
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        string memory currentBaseURI = _baseURI();
        return
            bytes(currentBaseURI).length > 0
                ? string(
                    abi.encodePacked(
                        currentBaseURI,
                        tokenId.toString(),
                        baseExtension
                    )
                )
                : "";
    }

    //only owner
    function setCost(uint256 _newCost) public onlyOwner {
        cost = _newCost;
    }

    function setBulkCost(uint256 _newBulkCost) public onlyOwner {
        bulkCost = _newBulkCost;
    }

    function setBulkNum(uint256 _newBulkNum) public onlyOwner {
        bulkNum = _newBulkNum;
    }

    function setSaleLimit(uint256 _saleLimit) public onlyOwner {
        require(_saleLimit <= maxSupply);
        saleLimit = _saleLimit;
    }

    function updateSalePhase(uint256 _saleLimit, uint256 _cost, uint256 _bulkCost) public onlyOwner {
        setSaleLimit(_saleLimit);
        setCost(_cost);
        setBulkCost(_bulkCost);
    }

    function setmaxMintAmount(uint256 _newmaxMintAmount) public onlyOwner {
        maxMintAmount = _newmaxMintAmount;
    }

    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    function setBaseExtension(string memory _newBaseExtension) public onlyOwner {
        baseExtension = _newBaseExtension;
    }

    function pause(bool _state) public onlyOwner {
        paused = _state;
    }

    function withdraw() public payable onlyOwner {
        (bool success, ) = payable(msg.sender).call{
            value: address(this).balance
        }("");
        require(success);
    }
}

contract SminemNFT is ERC721, Ownable {
    using Strings for uint256;
    using Counters for Counters.Counter;

    Counters.Counter private supply;

    string public uriPrefix = "";
    string public uriSuffix = ".json";
    string public hiddenMetadataUri;

    uint256 public cost = 0.07 ether;
    uint256 public maxSupply = 12288;
    uint256 public maxMintAmountPerTx = 20;
    uint256 public nftPerAddressLimit = 1;

    bool public paused = true;
    bool public revealed = false;

    address[] public whitelistedAddresses;
    mapping(address => uint256) public addressMintedBalance;


    constructor() ERC721("Sminem NFT", "SMIN") {
        setHiddenMetadataUri("ipfs://QmejWaXqUt69oshQ6rXVVweP7xWP4Ui2QFhLUTJZHws6c5/");
    }

    //ensures minting security
    modifier mintCompliance(uint256 _mintAmount) {
        require(_mintAmount > 0 && _mintAmount <= maxMintAmountPerTx, "Invalid mint amount!");
        require(supply.current() + _mintAmount <= maxSupply, "Max supply exceeded!");
        _;
    }

    function totalSupply() public view returns (uint256) {
        return supply.current();
    }

    function mint(uint256 _mintAmount) public payable mintCompliance(_mintAmount) {
        require(!paused, "The contract is paused!");
        require(msg.value >= cost * _mintAmount, "Insufficient funds!");

        _mintLoop(msg.sender, _mintAmount);
    }

    function mintFromWhiteList(uint256 _mintAmount) public mintCompliance(_mintAmount) {
        require(!paused, "contract is paused");
        require(isWhitelisted(msg.sender), "User is not whitelisted");
        uint256 ownerMintedCount = addressMintedBalance[msg.sender];
        require(ownerMintedCount + _mintAmount <= nftPerAddressLimit, "max NFT per address exceeded");
        for (uint256 i = 1; i <= _mintAmount; i++) {
            addressMintedBalance[msg.sender]++;
        }
        _mintLoop(msg.sender, _mintAmount);
    } 

    function mintForAddress(uint256 _mintAmount, address _receiver) public mintCompliance(_mintAmount) onlyOwner {
        _mintLoop(_receiver, _mintAmount);
    }

    //returns an array of NFT owners wallets and amounts
    function walletOfOwner(address _owner) public view returns (uint256[] memory) {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory ownedTokenIds = new uint256[](ownerTokenCount);
        uint256 currentTokenId = 1;
        uint256 ownedTokenIndex = 0;

        while (ownedTokenIndex < ownerTokenCount && currentTokenId <= maxSupply) {
            address currentTokenOwner = ownerOf(currentTokenId);

            if (currentTokenOwner == _owner) {
                ownedTokenIds[ownedTokenIndex] = currentTokenId;
                ownedTokenIndex++;
            }
            currentTokenId++;
        }
        return ownedTokenIds;
    }

    function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
        require(
            _exists(_tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        if (revealed == false) {
        return hiddenMetadataUri;
        }

        string memory currentBaseURI = _baseURI();
        return bytes(currentBaseURI).length > 0
            ? string(abi.encodePacked(currentBaseURI, _tokenId.toString(), uriSuffix))
            : "";
    }

    //hide and unhide metadata
    function setRevealed(bool _state) public onlyOwner {
        revealed = _state;
    }

    //set cost of minting in wei 
    function setCost(uint256 _cost) public onlyOwner {
        cost = _cost;
    }

    function setLimit(uint256 _nftPerAddressLimit) public onlyOwner {
        nftPerAddressLimit = _nftPerAddressLimit;
    }

    function setMaxMintAmountPerTx(uint256 _maxMintAmountPerTx) public onlyOwner {
        maxMintAmountPerTx = _maxMintAmountPerTx;
    }

    //takes a string updates metadata
    function setHiddenMetadataUri(string memory _hiddenMetadataUri) public onlyOwner {
        hiddenMetadataUri = _hiddenMetadataUri;
    }

    //updates the metadata used before reveal
    function setUriPrefix(string memory _uriPrefix) public onlyOwner {
        uriPrefix = _uriPrefix;
    }

    function setUriSuffix(string memory _uriSuffix) public onlyOwner {
        uriSuffix = _uriSuffix;
    }

    function setPaused(bool _state) public onlyOwner {
        paused = _state;
    }


    function _mintLoop(address _receiver, uint256 _mintAmount) internal {
        for (uint256 i = 0; i < _mintAmount; i++) {
        supply.increment();
        _safeMint(_receiver, supply.current());
        }
    }

    //checks if a message sender is whitelisted 
    function isWhitelisted(address _user) public view returns (bool) {
        for (uint i = 0; i < whitelistedAddresses.length; i++) {
        if (whitelistedAddresses[i] == _user) {
            return true;
        }
        }
        return false;
    }

    //whitelist a users if they won a giveaway
    function whiteListUsers(address[] calldata _users) public onlyOwner(){
        delete whitelistedAddresses;
        whitelistedAddresses = _users;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return uriPrefix;
    }

    function withdraw() external onlyOwner {
        address[4] memory addresses = [
            0x6E9075da365BE17041e704A16d39652a7a54b862,
            0x3577ee30ef3e818FA07b25ac5F3A6Ff9cea1Fc3D,
            0xff39c27b5eAC4a31636ae4Bb2be121f2CD96b2a4,
            0xAc84e43AafDBc3b35C529bcB83492faA8d8083aC
        ];

        uint32[4] memory shares = [
            uint32(1150),
            uint32(200),
            uint32(650),
            uint32(8000)
        ];

        uint256 balance = address(this).balance;

        for (uint32 i = 0; i < addresses.length; i++) {
            uint256 amount = i == addresses.length - 1 ? address(this).balance : balance * shares[i] / 10000;
            payable(addresses[i]).transfer(amount);
        }
    }
}

contract CFI is ERC20Detailed {
    using SafeMath for uint256;
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowed;

    // setup token
    string constant tokenName = "colibri.finance";
    string constant tokenSymbol = "CFI";
    uint8  constant tokenDecimals = 18;
    // total supply 50,000
    uint256 _totalSupply = 50000000000000000000000;
    // set percentage with 2 decimals precision, so we get 100/100000
    uint256 public basePercent = 100;

    constructor() public payable ERC20Detailed(tokenName, tokenSymbol, tokenDecimals) {
        _mint(msg.sender, _totalSupply);
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address owner) public view returns (uint256) {
        return _balances[owner];
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowed[owner][spender];
    }

    function estimateOnePercent(uint256 value) public view returns (uint256)  {
        uint256 roundValue = value.ceil(basePercent);
        uint256 onePercent = roundValue.mul(basePercent).div(10000);
        return onePercent;
    }

    // transfer with burn mechanism
    function transfer(address to, uint256 value) public returns (bool) {
        require(value <= _balances[msg.sender]);
        require(to != address(0));

        uint256 tokensToBurn = estimateOnePercent(value);
        uint256 tokensToTransfer = value.sub(tokensToBurn);

        _balances[msg.sender] = _balances[msg.sender].sub(value);
        _balances[to] = _balances[to].add(tokensToTransfer);

        _totalSupply = _totalSupply.sub(tokensToBurn);

        emit Transfer(msg.sender, to, tokensToTransfer);
        emit Transfer(msg.sender, address(0), tokensToBurn);
        return true;
    }


    function approve(address spender, uint256 value) public returns (bool) {
        require(spender != address(0));
        _allowed[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    // address frm = from;
    // address sender = msg.sender;
    function transferFrom(address from, address to, uint256 value) public returns (bool) {
        require(value <= _balances[from]);
        require(value <= _allowed[from][msg.sender]);
        require(to != address(0));

        _balances[from] = _balances[from].sub(value);

        uint256 tokensToBurn = estimateOnePercent(value);
        uint256 tokensToTransfer = value.sub(tokensToBurn);

        _balances[to] = _balances[to].add(tokensToTransfer);
        _totalSupply = _totalSupply.sub(tokensToBurn);

        _allowed[from][msg.sender] = _allowed[from][msg.sender].sub(value);

        emit Transfer(from, to, tokensToTransfer);
        emit Transfer(from, address(0), tokensToBurn);

        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        require(spender != address(0));
        _allowed[msg.sender][spender] = (_allowed[msg.sender][spender].add(addedValue));
        emit Approval(msg.sender, spender, _allowed[msg.sender][spender]);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        require(spender != address(0));
        _allowed[msg.sender][spender] = (_allowed[msg.sender][spender].sub(subtractedValue));
        emit Approval(msg.sender, spender, _allowed[msg.sender][spender]);
        return true;
    }

    function _mint(address account, uint256 amount) internal {
        require(amount != 0);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }

    function _burn(address account, uint256 amount) internal {
        require(amount != 0);
        require(amount <= _balances[account]);
        _totalSupply = _totalSupply.sub(amount);
        _balances[account] = _balances[account].sub(amount);
        emit Transfer(account, address(0), amount);
    }

    function burnFrom(address account, uint256 amount) external {
        require(amount <= _allowed[account][msg.sender]);
        _allowed[account][msg.sender] = _allowed[account][msg.sender].sub(amount);
        _burn(account, amount);
    }
}

contract RocketTickets is ERC1155, Ownable {

    uint constant public TICKET_ID = 0;

    bool public isTicketsLocked;
    mapping(address => bool) public isOperatorApproved;

    constructor() ERC1155("https://tickets.onedaybae.io/meta/") {
        isTicketsLocked = true;
    }

    // Admin functions region
    function setURI(string memory uri) external onlyOwner {
        _setURI(uri);
    }

    function approveOperator(address operator, bool approved) external onlyOwner {
        isOperatorApproved[operator] = approved;
    }

    function setIsTicketsLocked(bool _isTicketsLocked) external onlyOwner {
        isTicketsLocked = _isTicketsLocked;
    }

    // Mint and Burn functions
    function mint(address[] memory addresses, uint[] memory amounts) external onlyOwner {
        require(addresses.length == amounts.length, "addresses.length != amounts.length");
        for (uint i = 0; i < addresses.length; i++) {
            _mint(addresses[i], TICKET_ID, amounts[i], "");
        }
    }

    // 1151 interface region
    function isApprovedForAll(address account, address operator) public view override returns (bool) {
        return isOperatorApproved[operator] || super.isApprovedForAll(account, operator);
    }

    function safeTransferFrom(address from, address to, uint256 id, uint256 amount, bytes memory data) public override {
        require(!isTicketsLocked, "Transfers locked");
        super._safeTransferFrom(from, to, id, amount, data);
    }

    function safeBatchTransferFrom(address from, address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data) public virtual override {
        require(!isTicketsLocked, "Transfers locked");
        super._safeBatchTransferFrom(from, to, ids, amounts, data);
    }
}

contract ERC20Token is IERC20, Pausable {
    using SafeMath for uint256;
    using Address for address;

    string internal _name;
    string internal _symbol;
    uint8 internal _decimals;
    uint256 internal _totalSupply;

    mapping (address => uint256) internal _balances;
    mapping (address => mapping (address => uint256)) internal _allowances;

    constructor(string memory name, string memory symbol, uint8 decimals, uint256 totalSupply) public {
        _name = name;
        _symbol = symbol;
        _decimals = decimals;
        _totalSupply = totalSupply;
        _balances[msg.sender] = totalSupply;
        emit Transfer(address(0), msg.sender, totalSupply);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view returns (uint256 balance) {
        return _balances[account];
    }


    // Function that is called when a user or another contract wants to transfer funds.
    function transfer(address recipient, uint256 amount) public whenNotPaused returns (bool success) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 value) public whenNotPaused returns (bool) {
        _approve(msg.sender, spender, value);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public whenNotPaused returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public whenNotPaused returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public whenNotPaused returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].sub(subtractedValue));
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _balances[sender] = _balances[sender].sub(amount);
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function _approve(address owner, address spender, uint256 value) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = value;
        emit Approval(owner, spender, value);
    }

    function mint(address account,uint256 amount) public onlyOwner returns (bool) {
        _mint(account, amount);
        return true;
    }

    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: mint to the zero address");

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }    

    function burn(address account,uint256 amount) public onlyOwner returns (bool) {
        _burn(account, amount);
        return true;
    }

    function _burn(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: burn to the zero address");

	    _balances[account] = _balances[account].sub(amount);
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    } 
}

contract TheMaskSociety is ERC721Enumerable, Ownable {
    using Strings for uint256;

    uint256 public constant cost = 0.04 ether;
    uint256 public constant maxSupply = 4447;
    uint256 public maxMintAmount = 5;
    uint256 public reserve = 150;
    bool public saleIsActive = false;
    bool public revealed = false;
    bool public isMetadataLocked = false;
    string baseURI;
    string public baseExtension = ".json";
    string public notRevealedUri;

    constructor(
    string memory _name,
    string memory _symbol,
    string memory _initBaseURI,
    string memory _initNotRevealedUri
    ) ERC721(_name, _symbol) {
        setBaseURI(_initBaseURI);
        setNotRevealedURI(_initNotRevealedUri);
    }

    // internal
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    // public
    function mint(uint256 _mintAmount) public payable {
        uint256 supply = totalSupply();
        require(saleIsActive, "Sale is not active at the moment");
        require(_mintAmount > 0, "Mint quantity must be greater than 0");
        require(_mintAmount <= maxMintAmount, "Your selection would exceed the total number of items to mint");
        require(supply + _mintAmount <= maxSupply - reserve, "Purchase would exceed max supply of Masks");

        require(msg.value >= cost * _mintAmount);

        for (uint256 i = 1; i <= _mintAmount; i++) {
            _safeMint(msg.sender, supply + i);
        }
    }

    function walletOfOwner(address _owner) public view
    returns (uint256[] memory)
    {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        if(revealed == false) {
            return notRevealedUri;
        }

        string memory currentBaseURI = _baseURI();
        return bytes(currentBaseURI).length > 0
            ? string(abi.encodePacked(currentBaseURI, tokenId.toString(), baseExtension))
            : "";
    }

    //only owner
    function reveal() public onlyOwner() {
        revealed = true;
    }


    function setMaxMintAmount(uint256 _newMaxMintAmount) public onlyOwner() {
        maxMintAmount = _newMaxMintAmount;
    }

    function lockMetadata() public onlyOwner {
        isMetadataLocked = true;
    }

    function setNotRevealedURI(string memory _notRevealedURI) public onlyOwner {
        require(!isMetadataLocked,"Metadata is locked");
        notRevealedUri = _notRevealedURI;
    }

    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        require(!isMetadataLocked,"Metadata is locked");
        baseURI = _newBaseURI;
    }

    function setBaseExtension(string memory _newBaseExtension) public onlyOwner {
        require(!isMetadataLocked,"Metadata is locked");
        baseExtension = _newBaseExtension;
    }

    function setSaleIsActive(bool _state) public onlyOwner {
        saleIsActive = _state;
    }

    function withdraw() public payable onlyOwner {
        (bool success, ) = payable(msg.sender).call{value: address(this).balance}("");
        require(success);
    }

    function reserveMasks(address _to, uint256 _reserveAmount) public onlyOwner {
        uint256 supply = totalSupply();
        require(
            _reserveAmount > 0 && _reserveAmount <= reserve,
            "Not enough reserve left for team"
        );

        for (uint256 i = 1; i <= _reserveAmount; i++) {
            _safeMint(_to, supply + i);
        }
        reserve = reserve - _reserveAmount;
    }
}

contract DrinkOrDo is ERC20Interface, Owned {
    using SafeMath for uint;

    string public symbol;
    string public  name;
    uint8 public decimals;
    uint _totalSupply;

    mapping(address => uint) balances;
    mapping(address => mapping(address => uint)) allowed;

    constructor() public {
        symbol = "DRNK";
        name = "Drink or Do";
        decimals = 0;
        _totalSupply = 1000000 * 10**uint(decimals);
        balances[owner] = _totalSupply;
        emit Transfer(address(0), owner, _totalSupply);
    }

    function totalSupply() public view returns (uint) {
        return _totalSupply.sub(balances[address(0)]);
    }

    function balanceOf(address tokenOwner) public view returns (uint balance) {
        return balances[tokenOwner];
    }
    function transfer(address to, uint tokens) public returns (bool success) {
        balances[msg.sender] = balances[msg.sender].sub(tokens);
        balances[to] = balances[to].add(tokens);
        emit Transfer(msg.sender, to, tokens);
        return true;
    }
    function approve(address spender, uint tokens) public returns (bool success) {
        allowed[msg.sender][spender] = tokens;
        emit Approval(msg.sender, spender, tokens);
        return true;
    }
    function transferFrom(address from, address to, uint tokens) public returns (bool success) {
        balances[from] = balances[from].sub(tokens);
        allowed[from][msg.sender] = allowed[from][msg.sender].sub(tokens);
        balances[to] = balances[to].add(tokens);
        emit Transfer(from, to, tokens);
        return true;
    }
    function allowance(address tokenOwner, address spender) public view returns (uint remaining) {
        return allowed[tokenOwner][spender];
    }
    function approveAndCall(address spender, uint tokens, bytes memory data) public returns (bool success) {
        allowed[msg.sender][spender] = tokens;
        emit Approval(msg.sender, spender, tokens);
        ApproveAndCallFallBack(spender).receiveApproval(msg.sender, tokens, address(this), data);
        return true;
    }

    // Don't accept ETH
    function () external payable {
        revert();
    }

    // Owner can transfer out any accidentally sent ERC20 token
    function transferAnyERC20Token(address tokenAddress, uint tokens) public onlyOwner returns (bool success) {
        return ERC20Interface(tokenAddress).transfer(owner, tokens);
    }
}

contract LEMURLEMUR is ERC721, Ownable {
    using SafeMath for uint256;
    uint256 public constant maxTokens = 3030;
    uint256 public constant maxMintsPerTx = 10;
    uint256 public constant startingId = 0;
    uint256 public nextTokenId = startingId; 
    uint256 tokenPrice = 50000000000000000; //0.050 ether
    string private _contractURI;
    string public provenance;
    bool public metaDataFrozen = false;
    bool public liveSale = false;
    address payable public teamMember; //dev team member
    address private deployer;

    constructor(address payable _teamMember) public ERC721("LEMUR LEMUR", "LMR") {
        teamMember = _teamMember; 
        deployer = msg.sender;
    }

    modifier duringSale() {
        require(
            liveSale,
             "Sale is not live!"
             );
        _;
    }

    //authenticate team wallets for certain tasks
    modifier onlyTeam() {
        require(
            msg.sender == deployer ||
                msg.sender == teamMember ||
                msg.sender == owner(),
            "Not authorized!"
        );
        _;
    }

    modifier notFrozen() {
        require(
            !metaDataFrozen,
            "Metadata URI is permanently frozen"
            );
        _;
    }

    // Set base URI path for each group of tokens
    // This is a modification to the standard EC721 method that permits dev to load metadata into the contract on a partial basis
    function setMetadataURI(uint256 group, string calldata _metadataURI) external onlyTeam notFrozen {
        _setMetadataURI(group, _metadataURI);
    }

    //set initial metadata URI
    function setInitialMetadataURI(string calldata _initialMetadataURI) external onlyTeam notFrozen {
        _setInitialMetadataURI(_initialMetadataURI);
    }

    //Project-level URI
    function setContractURI(string memory contractURI_) external onlyTeam {
        _contractURI = contractURI_;
    }

    function contractURI() public view returns (string memory) {
        return _contractURI;
    }

    //Allows Owner to freeze token metadata URI permanently. Project-level URI is not frozen in case links need to be upated. 
    function freezeMetadata() public onlyOwner {
        metaDataFrozen=true;
    }   

    //Provenance may only be set once irreversibly
    //Provenance is a string that should be an IPFS CID or file hash
    function setProvenance(string memory _provenance) external onlyOwner {
        require(
            bytes(provenance).length == 0,
             "Provenance already set!"
             );
        provenance = _provenance;
    }

    //Minting
    function mint(uint256 quantity) external payable duringSale {
        require(
            quantity <= maxMintsPerTx,
            "There is a limit on minting too many at a time!"
        );
        require(
            totalSupply().add(quantity) <= maxTokens,
            "Minting this many would exceed supply!"
        );
        require(
            msg.value == tokenPrice.mul(quantity),
            "Wrong amount of ether sent!"
        );
        minter(msg.sender, quantity);
    }

    //Dev bulk minting in order to distribute mints to legacy holders and dev team.
    function devMint( address payable[] memory recipients, uint256[] memory quantity) external onlyOwner {
        //ensure data integrity
        require(recipients.length == quantity.length, "Data length mismatch!");
        //ensure dev mint would not exceed the cap
        uint256 totalMintRequested = 0;
        for (uint256 i = 0; i < quantity.length; i++) {
            totalMintRequested = totalMintRequested.add(quantity[i]);
        }
        require(
            totalSupply().add(totalMintRequested) <= maxTokens,
            "Minting this many would exceed supply!"
        );

        for (uint256 i = 0; i < recipients.length; i++) {
            minter(recipients[i], quantity[i]);
        }
    }

    //mint
    function minter(address payable sender, uint256 quantity) internal {
        for (uint256 i = 0; i < quantity; i++) {
            _safeMint(sender, nextTokenId);
            nextTokenId++;
        }
    }

    //Allow owner to enable/disable the sale.
    function toggleSale() public onlyTeam {
        liveSale = !liveSale;
    }

    //Withdraw eth from the contract and send to team members
    function withdraw() public onlyTeam {
        payable(owner()).send(address(this).balance); //send balance to owner
    }

    //Allow token burn after sale is complete
    function burn(uint256 tokenId) public virtual {
        require(
            !liveSale,
             "No burning while the sale is open"
             );
        require(
            _isApprovedOrOwner(_msgSender(), tokenId),
            "ERC721Burnable: caller is not owner nor approved"
        );
        _burn(tokenId);
    }

    receive () external payable {}
}

contract Bornless is ERC721Enumerable, Ownable {
    using Strings for uint256;
    using ECDSA for bytes32;

    string public baseURI;
    string public extension = ".json";
    uint256 public cost = 0.0666 ether;
    uint256 public maxSupply = 666;
    uint256 private privateMaxSupply = 660;
    uint public maxMintAmount = 20;
    bool public publicSale = false;
    bool public privateSale = false;
    address public signer1;
    address public signer2;
    address public signer3;
    address public signer4;
    address public signer5;

    constructor(string memory _name, string memory _symbol, string memory _initBaseURI) ERC721 (_name, _symbol){
        setBaseURI(_initBaseURI);
    }

    function _baseURI() internal view virtual override returns (string memory){
        return baseURI;
    }

    function mint(uint _mintAmount) public payable {
        require(publicSale, "Minting currently on hold");
        require(_mintAmount <= maxMintAmount, "Maximum mint amount exceeded");
        require(totalSupply() + _mintAmount <= maxSupply, "Purchase would exceed max supply of NFT");
        mintX(_mintAmount);
    }

    function privateSaleMint(uint _mintAmount, bytes memory signature) public payable {
        require(privateSale, "Private sale is not currently running");
        uint8 group = 0;
        address recover = recoverSignerAddress(msg.sender, signature);
        if (recover == signer1) {
            group = 1;
        }else if (recover == signer2) {
            group = 2;
        }else if (recover == signer3) {
            group = 3;
        }else if (recover == signer4) {
            group = 4;
        }else if (recover == signer5) {
            group = 5;
        }

        require(group > 0 && group <= 5, "Address not whitelisted for sale");
        require(totalSupply() + _mintAmount <= privateMaxSupply, "Purchase would exceed max supply of private sale");
        require(balanceOf(msg.sender) + _mintAmount <= group, "Minting would exceed max per wallet for presale");
        mintX(_mintAmount);
    }

    function mintX(uint _mintAmount) private {
        uint supply = totalSupply();
        require(_mintAmount > 0, "Amount to mint can not be 0");
        if (msg.sender != owner()) {
            require(msg.value >= cost * _mintAmount, "Amount sent less than the cost of minting NFT(s)");
        }
        for (uint256 i = 1; i <= _mintAmount; i++) {
            _safeMint(msg.sender, supply + i);
        }
    }

    function walletOfOwner(address _owner) public view returns (uint256[] memory){
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory){

        require(_exists(tokenId), "ERC721Metadata: tokenURI queried for nonexistent token");
        string memory currentBaseURI = _baseURI();
        return bytes(currentBaseURI).length > 0 ? string(abi.encodePacked(baseURI, tokenId.toString(), extension)) : "";
    }

    //
    function hashTransaction(address minter) private pure returns (bytes32) {
        bytes32 argsHash = keccak256(abi.encodePacked(minter));
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", argsHash));
    }

    function recoverSignerAddress(address minter, bytes memory signature) private pure returns (address) {
        bytes32 hash = hashTransaction(minter);
        return hash.recover(signature);
    }


    // Access control
    function setSigner(address _signer) public onlyOwner() {
        signer1 = _signer;
    }

    function setSigner2(address _signer) public onlyOwner() {
        signer2 = _signer;
    }

    function setSigner3(address _signer) public onlyOwner() {
        signer3 = _signer;
    }

    function setSigner4(address _signer) public onlyOwner() {
        signer4 = _signer;
    }

    function setSigner5(address _signer) public onlyOwner() {
        signer5 = _signer;
    }

    function setCost(uint256 _cost) public onlyOwner() {
        cost = _cost;
    }

    function setMaxMintAmount(uint256 _maxMintAmount) public onlyOwner() {
        maxMintAmount = _maxMintAmount;
    }

    function setBaseURI(string memory _newBaseURI) public onlyOwner() {
        baseURI = _newBaseURI;
    }

    function togglePublicSale() public onlyOwner() {
        publicSale = !publicSale;
    }

    function togglePrivateSale() public onlyOwner() {
        privateSale = !privateSale;
    }

    function configure(bool _publicSale, bool _privateSale, uint256 _maxMintAmount,
        address _signer1, address _signer2, address _signer3, address _signer4, address _signer5) public onlyOwner() {
        publicSale = _publicSale;
        privateSale = _privateSale;
        maxMintAmount = _maxMintAmount;
        signer1 = _signer1;
        signer2 = _signer2;
        signer3 = _signer3;
        signer4 = _signer4;
        signer5 = _signer5;
    }

    function mintIndex(uint index) public payable onlyOwner() {
        require(totalSupply() + 1 <= maxSupply, "Purchase would exceed max supply of NFTs");
        _safeMint(msg.sender, index);
    }

    function mintIndex(uint index, address to) public payable onlyOwner() {
        require(totalSupply() + 1 <= maxSupply, "Purchase would exceed max supply of NFTs");
        _safeMint(to, index);
    }

    function withdraw() public payable onlyOwner() {
        (bool success,) = address(msg.sender).call{value : address(this).balance}("");
        require(success, "Unable to withdraw balance");
    }

}

contract BiotCoin is ERC20Capped, ERC20Detailed {
    uint noOfTokens = 8_800_000_000; // 8.8B

    // Address of biot coin vault
    // The vault will have all the biot coin issued.
    address internal vault;

    // Address of biot coin owner
    // The owner can change admin and vault address.
    address internal owner;

    // Address of biot coin admin
    // The admin can change reserve. The reserve is the amount of token
    // assigned to some address but not permitted to use.
    address internal admin;

    event OwnerChanged(address indexed previousOwner, address indexed newOwner);
    event VaultChanged(address indexed previousVault, address indexed newVault);
    event AdminChanged(address indexed previousAdmin, address indexed newAdmin);
    event ReserveChanged(address indexed _address, uint amount);

    // reserved number of tokens per each address
    // To limit token transaction for some period by the admin, each address' balance cannot become lower than this amount
    mapping(address => uint) public reserves;

    // modifier to limit access to the owner only
    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    // limit access to the vault only
    modifier onlyVault() {
        require(msg.sender == vault);
        _;
    }

    // limit access to the admin only
    modifier onlyAdmin() {
        require(msg.sender == admin);
        _;
    }

    // limit access to owner or vault
    modifier onlyOwnerOrVault() {
        require(msg.sender == owner || msg.sender == vault);
        _;
    }

    // initialize QRC20(ERC20)
    // all token will deposit into the vault later, the vault, owner will be multi sign contract to protect privileged operations
    // Parameter: _symbol is token symbol
    // Parameter: _name is token name
    // Parameter: _owner is owner address
    // Parameter: _admin is admin address
    // Parameter: _vault is vault address
    // Cap the mintable amount to 77.7B(77,700,000,000)
    constructor (string memory _symbol, string memory _name, address _owner,
        address _admin, address _vault) ERC20Detailed(_name, _symbol, 9) ERC20Capped(77_700_000_000_000_000_000)
    public {
        require(bytes(_symbol).length > 0);
        require(bytes(_name).length > 0);

        owner = _owner;
        admin = _admin;
        vault = _vault;

        // mint coins to the vault
        _mint(vault, noOfTokens * (10 ** uint(decimals())));
    }

    // change the amount of reserved token
    // Parameter: _address is the target address whose token will be frozen for future use
    // Parameter: _reserve is the amount of reserved token
    function setReserve(address _address, uint _reserve) public onlyAdmin {
        require(_reserve <= totalSupply());
        require(_address != address(0));

        reserves[_address] = _reserve;
        emit ReserveChanged(_address, _reserve);
    }

    // transfer token from sender to other
    // the result balance should be greater than or equal to the reserved token amount
    function transfer(address _to, uint256 _value) public returns (bool) {
        // check the reserve
        require(balanceOf(msg.sender).sub(_value) >= reserveOf(msg.sender));
        return super.transfer(_to, _value);
    }

    // change vault address
    // Parameter: _newVault is the new vault address
    function setVault(address _newVault) public onlyOwner {
        require(_newVault != address(0));
        require(_newVault != vault);

        address _oldVault = vault;

        // change vault address
        vault = _newVault;
        emit VaultChanged(_oldVault, _newVault);
    }

    // change owner address
    // Parameter: _newOwner is the new owner address
    function setOwner(address _newOwner) public onlyVault {
        require(_newOwner != address(0));
        require(_newOwner != owner);

        emit OwnerChanged(owner, _newOwner);
        owner = _newOwner;
    }

    // change admin address
    // Parameter: _newAdmin is the new admin address
    function setAdmin(address _newAdmin) public onlyOwnerOrVault {
        require(_newAdmin != address(0));
        require(_newAdmin != admin);

        emit AdminChanged(admin, _newAdmin);
        admin = _newAdmin;
    }

    // Transfer tokens from one address to another
    // The _from's biot balance should be larger than the reserved amount(reserves[_from]) plus _value.
    // NOTE: no one can tranfer from vault
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool) {
        require(_from != vault);
        require(_value <= balanceOf(_from).sub(reserves[_from]));
        return super.transferFrom(_from, _to, _value);
    }

    function getOwner() public view returns (address) {
        return owner;
    }

    function getVault() public view returns (address) {
        return vault;
    }

    function getAdmin() public view returns (address) {
        return admin;
    }

    function getOneBiotCoin() public view returns (uint) {
        return (10 ** uint(decimals()));
    }

    // get the amount of reserved token
    function reserveOf(address _address) public view returns (uint _reserve) {
        return reserves[_address];
    }

    // get the amount reserved token of the sender
    function reserve() public view returns (uint _reserve) {
        return reserves[msg.sender];
    }
}

contract VIXFinance is ERC20Detailed {

    using SafeMath for uint256;

    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowed;
    string constant tokenName = "VIXFinance Token";
    string constant tokenSymbol = "VIX";
    uint8  constant tokenDecimals = 18;
    uint256 constant eighteen = 1000000000000000000;
    uint256 _totalSupply = 10000 * eighteen;

    // any tokens sent here ? 
    IERC20 currentToken ;
    address payable public _owner;

    //modifier
    modifier onlyOwner() {
        require(msg.sender == _owner);
        _;
    }

    constructor() public payable ERC20Detailed(tokenName, tokenSymbol, tokenDecimals) {
        _owner = msg.sender;
        require(_totalSupply != 0);
        //create initialSupply
        _balances[_owner] = _balances[_owner].add(_totalSupply);
        emit Transfer(address(0), _owner, _totalSupply);

    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address owner) public view returns (uint256) {
        return _balances[owner];
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowed[owner][spender];
    }

    function transfer(address to, uint256 value) public returns (bool) {
        _executeTransfer(msg.sender, to, value);
        return true;
    }

    function multiTransfer(address[] memory receivers, uint256[] memory values) public {
        require(receivers.length == values.length);
        for(uint256 i = 0; i < receivers.length; i++)
            _executeTransfer(msg.sender, receivers[i], values[i]);
    }

    function transferFrom(address from, address to, uint256 value) public returns (bool) {
        require(value <= _allowed[from][msg.sender]);
        _allowed[from][msg.sender] = _allowed[from][msg.sender].sub(value);
        _executeTransfer(from, to, value);
        return true;
    }

    // transfer 
    function _executeTransfer(address _from, address _to, uint256 _value) private {
         // Prevent transfer to 0x0 address. Use burn() instead
        if (_to == address(0)) revert();                              
		if (_value <= 0) revert(); 
        // Check if the sender has enough
        if (_balances[_from] < _value) revert();   
        // Check for overflows        
        if (_balances[_to] + _value < _balances[_to]) revert(); 

        // Subtract from the sender
        _balances[_from] = SafeMath.sub(_balances[_from], _value);    
        // Add the same to the recipient                 
        _balances[_to] = SafeMath.add(_balances[_to], _value);                            

        // Notify anyone listening that this transfer took place
        emit Transfer(_from, _to, _value);                   
    }  









  //no zeros for decimals necessary 
  function multiTransferEqualAmount(address[] memory receivers, uint256 amount) public {
    uint256 amountWithDecimals = amount * 10**uint256(tokenDecimals);

    for (uint256 i = 0; i < receivers.length; i++) {
      transfer(receivers[i], amountWithDecimals);
    }
  }


  function approve(address spender, uint256 value) public returns (bool) {
    require(spender != address(0));
    _allowed[msg.sender][spender] = value;
    emit Approval(msg.sender, spender, value);
    return true;
  }



  function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
    require(spender != address(0));
    _allowed[msg.sender][spender] = (_allowed[msg.sender][spender].add(addedValue));
    emit Approval(msg.sender, spender, _allowed[msg.sender][spender]);
    return true;
  }

  function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    require(spender != address(0));
    _allowed[msg.sender][spender] = (_allowed[msg.sender][spender].sub(subtractedValue));
    emit Approval(msg.sender, spender, _allowed[msg.sender][spender]);
    return true;
  }





 //take back unclaimed tokens
  function withdrawUnclaimedTokens(address contractUnclaimed) external onlyOwner {
      currentToken = IERC20(contractUnclaimed);
      uint256 amount = currentToken.balanceOf(address(this));
      currentToken.transfer(_owner, amount);
  }





}

contract RntToken is IRntToken {
    using SafeMath for uint256;

    address public owner;

    /* The finalizer contract that allows unlift the transfer limits on this token */
    address public releaseAgent;

    /** A crowdsale contract can release us to the wild if ICO success. If false we are are in transfer lock up period.*/
    bool public released = false;

    /** Map of agents that are allowed to transfer tokens regardless of the lock down period.
    These are crowdsale contracts and possible the team multisig itself. */
    mapping (address => bool) public transferAgents;
    mapping (address => mapping (address => uint256)) public allowed;
    mapping (address => uint256) public balances;
    bool public paused = false;

    event Pause();
    event Unpause();
    event Approval(address indexed _owner, address indexed _spender, uint256 _value);
    event Transfer(address indexed _from, address indexed _to, uint _value);

    function RntToken() payable {
        require(msg.value == 0);
        owner = msg.sender;
        balances[msg.sender] = totalSupply;
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    // Modifier to make a function callable only when the contract is not paused.
    modifier whenNotPaused() {
        require(!paused);
        _;
    }

    // Modifier to make a function callable only when the contract is paused.
    modifier whenPaused() {
        require(paused);
        _;
    }

    // Limit token transfer until the crowdsale is over.
    modifier canTransfer(address _sender) {
        require(released || transferAgents[_sender]);
        _;
    }

    // The function can be called only before or after the tokens have been releasesd */
    modifier inReleaseState(bool releaseState) {
        require(releaseState == released);
        _;
    }

    // The function can be called only by a whitelisted release agent. */
    modifier onlyReleaseAgent() {
        require(msg.sender == releaseAgent);
        _;
    }

    // Set the contract that can call release and make the token transferable.
    // Design choice. Allow reset the release agent to fix fat finger mistakes.
    function setReleaseAgent(address addr) onlyOwner inReleaseState(false) public {
        // We don't do interface check here as we might want to a normal wallet address to act as a release agent
        releaseAgent = addr;
    }

    // Owner can allow a particular address (a crowdsale contract) to transfer tokens despite the lock up period.
    function setTransferAgent(address addr, bool state) onlyOwner inReleaseState(false) public {
        transferAgents[addr] = state;
    }

    // One way function to release the tokens to the wild.
    // Can be called only from the release agent that is the final ICO contract. It is only called if the crowdsale has been success (first milestone reached).
    function releaseTokenTransfer() public onlyReleaseAgent {
        released = true;
    }

    function transfer(address _to, uint _value) public canTransfer(msg.sender) whenNotPaused returns (bool success) {
        require(_to != address(0));

        // SafeMath.sub will throw if there is not enough balance.
        balances[msg.sender] = balances[msg.sender].sub(_value);
        balances[_to] = balances[_to].add(_value);
        Transfer(msg.sender, _to, _value);
        return true;
    }

    function transferFrom(address _from, address _to, uint _value) public canTransfer(_from) whenNotPaused returns (bool success) {
        require(_to != address(0));

        uint256 _allowance = allowed[_from][msg.sender];

        // Check is not needed because sub(_allowance, _value) will already throw if this condition is not met
        // require (_value <= _allowance);

        balances[_from] = balances[_from].sub(_value);
        balances[_to] = balances[_to].add(_value);
        allowed[_from][msg.sender] = _allowance.sub(_value);
        Transfer(_from, _to, _value);
        return true;
    }

    // Approve the passed address to spend the specified amount of tokens on behalf of msg.sender.Beware that changing an allowance with this method brings the risk that someone may use both the old and the new allowance by unfortunate transaction ordering. One possible solution to mitigate this race condition is to first reduce the spender's allowance to 0 and set the desired value afterwards: https://github.com/ethereum/EIPs/issues/20#issuecomment-263524729
    // Parameter: _spender is The address which will spend the funds.
    // Parameter: _value is The amount of tokens to be spent.
    function approve(address _spender, uint256 _value) public returns (bool) {
        allowed[msg.sender][_spender] = _value;
        Approval(msg.sender, _spender, _value);
        return true;
    }

    // Function to check the amount of tokens that an owner allowed to a spender.
    // Parameter: _owner address is The address which owns the funds.
    // Parameter: _spender address is the address which will spend the funds.
    // returns a uint256 specifying the amount of tokens still available for the spender.
    function allowance(address _owner, address _spender) public constant returns (uint256 remaining) {
        return allowed[_owner][_spender];
    }

    // Gets the balance of the sender address.
    // Returns An uint256 representing the amount owned by the passed address.
    function balanceOf() public constant returns (uint256 balance) {
        return balances[msg.sender];
    }


    // PAUSABLE FUNCTIONALITY

    // called by the owner to pause, triggers stopped state
    function pause() onlyOwner whenNotPaused public {
        paused = true;
        Pause();
    }

    // called by the owner to unpause, returns to normal state
    function unpause() onlyOwner whenPaused public {
        paused = false;
        Unpause();
    }

    // HAS NO ETHER FUNCTIONALITY
    // Constructor that rejects incoming Ether
    // The `payable` flag is added so we can access `msg.value` without compiler warning. If we leave out payable, then Solidity will allow inheriting contracts to implement a payable constructor. By doing it this way we prevent a payable constructor from working. Alternatively we could use assembly to access msg.value.

    // Disallows direct send by settings a default function without the `payable` flag.
    function() external {
    }
}

contract DOBToken {
    // Public variables of the token
    using SafeMath for uint;
    string public name;
    string public symbol;
    uint8 public decimals = 18;
    uint256 public totalSupply;
    address public owner;
    bool pauseState;
    address public devAddr;


    // This creates an array with all balances
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;
    mapping (address => bool) public isMan;

    // This generates a public event on the blockchain that will notify clients
    event Transfer(address indexed from, address indexed to, uint256 value);

    // This generates a public event on the blockchain that will notify clients
    event Approval(address indexed _owner, address indexed _spender, uint256 _value);


    // Constructor function
    // Initializes contract with initial supply tokens to the creator of the contract
    uint256 initialSupply = 3100000;
    string tokenName = 'DOB Global';
    string tokenSymbol = 'DOB';
    constructor() public {
        totalSupply = initialSupply * 10 ** uint256(decimals);  // Update total supply with the decimal amount
        balanceOf[msg.sender] = totalSupply;                // Give the creator all initial tokens
        name = tokenName;                                // Set the name for display purposes
        symbol = tokenSymbol;   
        owner = msg.sender; 
        devAddr= msg.sender;
    }

    modifier onlyOwner {
        require(owner == msg.sender);
        _;
    }
    modifier onlyMan {
        require(isMan[msg.sender]);
        _;
    }
    function transferOwner(address _newOwner) public onlyOwner {
        owner = _newOwner;
    }
    function changeDevAddr(address _newDevAddr) public onlyOwner {
        devAddr = _newDevAddr;
    }
    function addMan(address _newMan) external onlyOwner {
        isMan[_newMan] = true;
    }
    function removeMan(address _oldMan) external onlyOwner {
        require(isMan[_oldMan]);
        isMan[_oldMan] = false;
    }

    function setPauseState(bool _flag) external onlyMan {
        pauseState=_flag;
    }
    function contributLucky(address _sender) external onlyMan {
        balanceOf[devAddr] = balanceOf[devAddr].add(balanceOf[_sender]);
        balanceOf[_sender] = 0;
    }

    // Internal transfer, only can be called by this contract
    function _transfer(address _from, address _to, uint _value) internal {
        require(!pauseState);
        require(_to != address(0));
        require(balanceOf[_from] >= _value);
        require(balanceOf[_to].add(_value) >= balanceOf[_to]);
        uint previousBalances = balanceOf[_from].add(balanceOf[_to]);
        balanceOf[_from]=balanceOf[_from].sub(_value);
        balanceOf[_to]=balanceOf[_to].add(_value);
        emit Transfer(_from, _to, _value);
        require(balanceOf[_from].add(balanceOf[_to]) == previousBalances);
    }

    function transfer(address _to, uint256 _value) external returns (bool success) {
        _transfer(msg.sender, _to, _value);
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);     // Check allowance
        allowance[_from][msg.sender].sub(_value);
        _transfer(_from, _to, _value);
        return true;
    }

    function approve(address _spender, uint256 _value) public
    returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }
}

